function prestatuLurraldeakTaula(tx) {
	tx.executeSql("DROP TABLE IF EXISTS lurraldeak");
	tx.executeSql("CREATE TABLE IF NOT EXISTS lurraldeak (" +
			"id INTEGER PRIMARY KEY NOT NULL, " +
			"izena TEXT NOT NULL, " +
			"nice_name TEXT NOT NULL, " +
			"testua TEXT NOT NULL, " +
			"irudien_egiletzak TEXT NOT NULL);"
	);
	tx.executeSql("INSERT INTO `lurraldeak` (`id`, `izena`, `nice_name`, `testua`, `irudien_egiletzak`) VALUES(1, 'Araba', 'araba', '<p>\r\n	Araba Euskal Herriko hego mendebaldean dagoen lurraldea da. 3.311,98 km&sup2; ditu eta 2009an ia 313.819 biztanle zituen. Bere barnean du Trebiñu barrendegia, Burgosko probintziaren administraziopean dagoena. Gasteiz da Arabako hiriburua, eta 1978az geroztik, baita Euskal Autonomia Erkidegoko hiriburua ere.</p>\r\n<p>\r\n	Herrialdeko mendirik garaiena Gorbeia (1.481 m) dugu, Bizkaiko mugan dagoena. Bestela, Toloñoko mendilerroan Palomares (1.446 m) eta Cruz del Castillo (1.432 m) mendiek ere 1.400 metroko maila gainditzen dute.<br />\r\n	<br />\r\n	Arabako ibai garrantzitsuenek Mediterraneo aldera jotzen dute: Ebro eta beronen ibaiadarrak.</p>\r\n<p>\r\n	Azalerari dagokionez EAEko lurralde handiena izan arren, biztanle gutxien eta dentsitate txikiena duen lurraldea da oraindik ere. Halere, azken aldian Araba da biztanletan hazi den EAEko lurralde bakarra. Arabarren erdia Gasteizen eta inguruetan bizi dira, honek bi aukera ezberdin emanez herrialdea bisitatu nahi duenari: hiria edo herriak.</p>\r\n<p>\r\n	Arabako Errioxako ardo ekoizleek Euskal Herri osotik antolatzen diren bisita turistikoak hartzen dituzte upeltegietan, eta dudarik gabe, herrialdearen beste ikuspegi batez gozatzeko aukera ezin hobea osatzen dute.</p>\r\n<p>\r\n	Bestalde, herrialdean museo ugari ere badago: Sobrongo Uraren Museoa, Ertzantzaren Museoa, Euskal Gastronomi Museoa, Euskal Buztigintza Museoa, Artziniegako Etnografia Museoa, Felix Murga Etnografia Museoa, Farolen Museoa, Arte Sakratuaren Museoa, Natura Zientzien Museoa, Bibat Fournier Karta Museoa, Arma Museoa, Bibat Arkeologia Museoa, Arte Ederren Museoa eta Artium, Arte Garaikideko Euskal Zentro-Museoa, batzuk aipatzearren.</p>\r\n<p>\r\n	Iturria: <a href=\"http://eu.wikipedia.org/wiki/Araba\" target=\"_blank\" title=\"Ireki esteka leiho berri batean\">Euskarazko Wikipedia</a></p>\r\n<p>\r\n	<a href=\"http://eu.wikipedia.org/wiki/Araba\" target=\"_blank\" title=\"Ireki esteka leiho berri batean\"> </a></p>\r\n', '<p>\r\n	<strong>ARGAZKIEN EGILETZAK:</strong></p>\r\n<p>\r\n	Orri honetan ikusten dituzun argazki guztiak Creative Commons lizentziapean daude. Argazki bakoitzaren beheko eskuin erpinean dagoen CC ikurraren gainean punteroa jarriz gero, jatorrizko argazkiari buruzko informazioa topatuko duzu. Informazio hori ageri dela, klik eginez gero, jatorrizkora joateko aukera daukazu.</p>\r\n');");
	tx.executeSql("INSERT INTO `lurraldeak` (`id`, `izena`, `nice_name`, `testua`, `irudien_egiletzak`) VALUES(2, 'Bizkaia', 'bizkaia', '<p>\r\n	Bizkaia Euskal Herriko ipar-mendebaldean kokatuta dago. Iparraldean Kantauri Itsasoa du mugakide; hegoaldean Burgos eta Araba; mendebaldean, Kantabria eta Burgos, eta ekialdean Gipuzkoa. 1.155.241 pertsona bizi dira Bizkaian. Euskal Herriko herrialderik populatuena da, eta Bilbo Handiak osatzen du Euskal Herriko konurbazio garrantzitsuena.</p>\r\n<blockquote>\r\n	<p>\r\n		<em>\"Gernika munduko herririk zoriontsuena da.<br />\r\n		Haritz baten gerizpean biltzen den<br />\r\n		baserritarren batzarrak erabakitzen du arazoez,<br />\r\n		eta beti hartzen ditu erabakirik zuzenenak.\"</em></p>\r\n	<p>\r\n		&mdash;Jean-Jaques Rousseau</p>\r\n</blockquote>\r\n<p>\r\n	Bizkaiko kostaldea malkartsua da, baina orokorrean hondartza handiak daude. Gainera bi itsas-adar daude bertan: Nerbioiko itsasadarra eta Urdaibai, azken hau benetan ikusgarria, erreserba naturala</p>\r\n<p>\r\n	Bizkaiko kostan Euskal Herrian iparraldeenen dagoen lekuetako bat dago, Matxitxako lurmuturra. Hainbat irla ere badaude, Billano, Izaro edo Aketxe bezala.</p>\r\n<p>\r\n	Kostako puntu batzuetan itsasoak hegi handiekin topo egiten du, adibidez, Ogoño lurmuturrean, 200 metro baino gehiago dituena.</p>\r\n<p>\r\n	Bizkaian hondartza ugari eta luzeak daude. Ezagunenak Urdaibaiko Laida eta Laga, Sopelako Barinatxe eta Atxabiribil-Arrietara, Plentziako hondartza, eta Ispasterreko Ogeia eta Mendexako Karraspio dira.</p>\r\n<p>\r\n	Kultura alorrean, Durangoko Euskal Liburu eta Disko Azoka antolatzen da urtero, izenak dioen bezala, Durangon. Urtero, abenduaren lehenengo hamabostaldian, lurralde osoko argitaletxeak batzen diren topagunea da, non&nbsp; nobedadeak eta aurreko urteetako liburu eta diskoak ere saltzen dituzten. Bizkaian erreferente kultural nagusia da gaur egun.</p>\r\n<p>\r\n	Bisitariak zenbait museo ikusteko aukera ere izango du bertan. Garrantzitsuenak Bilbon daude baina bestelakoak ere badira. Mundu maila ezaguna den Guggenheim Bilbao Museoa, Bilboko Arte Ederretako Museoa, Euskal Abertzaletasunaren Museoa, Abra Aquarium Getxo eta Euskal Herriko Meatzaritzaren Museoa batzuk aipatzearren.</p>\r\n<p>\r\n	Iturria: <a href=\"http://eu.wikipedia.org/wiki/Bizkaia\" target=\"_blank\" title=\"Ireki esteka leiho berri batean\">Euskarazko Wikipedia</a></p>\r\n<p>\r\n	<a href=\"http://eu.wikipedia.org/wiki/Bizkaia\" target=\"_blank\" title=\"Ireki esteka leiho berri batean\"> </a></p>\r\n', '<p>\r\n	<strong>ARGAZKIEN EGILETZAK:</strong></p>\r\n<p>\r\n	Orri honetan ikusten dituzun argazki guztiak Creative Commons lizentziapean daude. Argazki bakoitzaren beheko eskuin erpinean dagoen CC ikurraren gainean punteroa jarriz gero, jatorrizko argazkiari buruzko informazioa topatuko duzu. Informazio hori ageri dela, klik eginez gero, jatorrizkora joateko aukera daukazu.</p>\r\n');");
	tx.executeSql("INSERT INTO `lurraldeak` (`id`, `izena`, `nice_name`, `testua`, `irudien_egiletzak`) VALUES(3, 'Gipuzkoa', 'gipuzkoa', '<p>\r\n	Gipuzkoa Euskal Herriaren ipar-erdialdean dagoen lurraldea da. Mugakide ditu Bizkaia mendebaldean, Araba hego-mendebaldean, Nafarroa ekialdean, Lapurdi ipar-ekialdean eta Bizkaiko golkoa iparraldean. Hedaduraz 1.909 km&sup2; ditu, eta 92 km-ko itsasertzak hondartza bila dabiltzan milaka bisitariren helburu egiten du urtero. 2008ko erroldan, 709.363 biztanle zituen Gipuzkoak, eta horien laurdena hiriburuan bizi dira: Donostian.</p>\r\n<p>\r\n	Gipuzkoa izena lehenekoz 1014. urteko dokumentu nafar batean ageri da non Donostia aldeko lur batzuen donazioa aipatzen den. Bada fidagarritasun handiagoa duen 1025 urteko beste dokumentu bat ere. Orohar beraz esan liteke gaur egun lurralde honek darman izena bigarren milurtekoaren hasierarekin batera zabaldu eta erabiltzen hasi zela.</p>\r\n<p>\r\n	Gipuzkoan mintzatzen den euskara hiru euskalkitan sailkatzen da: Gipuzkera (nagusiena), Bizkaiera (mendebaldean) eta Nafarrera (ipar-ekialdean).</p>\r\n<p>\r\n	Bisitariak aukera ezberdinak ditu herrialde hau gozatzeko. Hiriburua bera, kostaldeko herriak, edo barnealdeko Gipuzkoa berdea. Denak ere, aukera ederrak.</p>\r\n<p>\r\n	Kultura aldetik ere, museo ugari daude herrialdean, batzuk aipatzearren, Donostiako San Telmo Museoa edo Eureka! Zientzia Museoa, Photomuseum-Argazki eta Zinema Museoa Zarautzen,Txontxongiloaren Museoa / TOPIC Tolosan, Crist&oacute;bal Balenciaga Museoa Getarian, Idiazabal Gazta interpretatzeko eta dastatzeko Zentroa, Euskal Burdinaren Museoa Legazpin, Barandiaran Museoa Ataunen, Armagintzaren Museoa Eibarren, Sagardoetxea, Euskal Sagardoaren Museoa Astigarragan.</p>\r\n<p>\r\n	Iturria: <a href=\"http://eu.wikipedia.org/wiki/Gipuzkoa\" title=\"Ireki esteka leiho berri batean\">Euskarazko Wikipedia</a></p>\r\n', '<p>\r\n	<strong>ARGAZKIEN EGILETZAK:</strong></p>\r\n<p>\r\n	Orri honetan ikusten dituzun argazki guztiak Creative Commons lizentziapean daude. Argazki bakoitzaren beheko eskuin erpinean dagoen CC ikurraren gainean punteroa jarriz gero, jatorrizko argazkiari buruzko informazioa topatuko duzu. Informazio hori ageri dela, klik eginez gero, jatorrizkora joateko aukera daukazu.</p>\r\n');");
	tx.executeSql("INSERT INTO `lurraldeak` (`id`, `izena`, `nice_name`, `testua`, `irudien_egiletzak`) VALUES(4, 'Lapurdi', 'lapurdi', '<p>\r\n	Lapurdi Ipar Euskal Herriko lurralderik populatuena da, eta Nafarroa Behereak eta Zuberoak ez bezala, goranzko bilakaera izan du biztanle kopuruak lurralde honetan. Oso orografia homogenoa du: 500 metrorainoko muinoak daude herrialdearen erdialdean eta itsasertz dotorea dauka, 30 eta 80 metro bitarteko itsas labarrez josia. &nbsp;</p>\r\n<p>\r\n	Lapurdiko ibaiek Kantauri itsasoan isurtzen dute ura. Ibai laburrak eta ur emari handikoak dira. Lapurditik igarotzen diren ibai nagusiak Aturri, Errobi, Bidasoa eta Urdazuri dira. Aturrik Lapurdiko erreka gehienen ura jasotzen du, eta Euskal Herriaren eta Gaskoniaren arteko muga da.</p>\r\n<p>\r\n	Pirinioak, Aturri ibaia eta Kantauri Itsasoa (Bizkaiko Golkoa) dira bere muga naturalak. Iparraldean Landekin du muga, ekialdean Nafarroa Beherearekin eta Hegoaldean Gipuzkoa eta Nafarroarekin.</p>\r\n<p>\r\n	Kostaldearen jarduera nagusia turismoarekin lotua da, bereziki Miarritzen, Donibane Lohizunen eta Hendaian. Barnealdean oraindik muino berdeak eta herri txikiak dira nagusi, Sara eta Ainhoa herri politenen zerrendan sartzekoak direlarik. Bertan nekazal turismo eta naturari lotutakoa ere garrantzia hartuz doa.</p>\r\n<p>\r\n	Lapurdin hitz egiten den euskalkia lapurtera da, nahiz eta ez Lapurdi osoan hitz egin; izan ere ekialdeko hainbat herritan behenafarrera hitz egiten da. Lapurdiko mendebaldean eta Nafarroako Urdazubi eta Zugarramurdin da nagusi lapurtera. Ipar Euskal Herriko herrialderik erdaldunena da; orain dela mende bat % 50 mintzatzen zen euskaraz, 1970. urtean, berriz, % 24. Industrializazioa eta etorkinak izan dira euskararen beherakadaren eragile nagusiak, itsasaldean batez ere. Hala ere, ikastolen eta irrati-telebistaren eraginez egoera zertxobait aldatu dela dirudi.</p>\r\n<p>\r\n	Euskarazko idazle klasikoen artean Joannes Leizarraga eta \"Axular\" izen handikoak lapurtarrak izan ziren. Garaikideen artean, Itxaro Borda, Ur Apalategi eta Eneko Bidegain bertakoak ditugu. Frantsesezko egileen artean, Pierre Loti eta Marie Darrieussecq aipatzekoak dira.</p>\r\n<p>\r\n	Iturria: <a href=\"http://eu.wikipedia.org/wiki/Lapurdi\" target=\"_blank\" title=\"Ireki esteka leiho berri batean\">Euskarazko Wikipedia</a>.</p>\r\n', '<p>\r\n	<strong>ARGAZKIEN EGILETZAK:</strong></p>\r\n<p>\r\n	Orri honetan ikusten dituzun argazki guztiak Creative Commons lizentziapean daude. Argazki bakoitzaren beheko eskuin erpinean dagoen CC ikurraren gainean punteroa jarriz gero, jatorrizko argazkiari buruzko informazioa topatuko duzu. Informazio hori ageri dela, klik eginez gero, jatorrizkora joateko aukera daukazu.</p>\r\n');");
	tx.executeSql("INSERT INTO `lurraldeak` (`id`, `izena`, `nice_name`, `testua`, `irudien_egiletzak`) VALUES(5, 'Nafarroa', 'nafarroa', '<p>\r\n	Euskal Herriko zazpi herrialdeetatik handiena da Nafarroa, 10.000 km&sup2;-tik gorarekin, eta honela, ia Euskal Herri osoaren lur eremuaren erdia hartzen du, Euskal Herriko erdialdean eta hegoaldean dago. 641.293 biztanle ditu eta Iruñea du hiriburu.</p>\r\n<p>\r\n	Nafarroako eta Euskal Herri osoko mendirik altuena Hiru Erregeen Mahaia da 2.428 metrorekin. Linza aldeko igoera da errazena. Ez du aparteko zailtasunik igoeraren luzera alde batera utzita.</p>\r\n<p>\r\n	Herrietako jaiez gain, zenbait jaik izaera berezia dute Nafarroan:</p>\r\n<p>\r\n	Martxoaren lehenbiziko bi asteburuetan Xabierraldia ospatu eta burutu ohi da.</p>\r\n<p>\r\n	Apirilaren azken igandean: Nafarroaren Eguna, Baigorrin antolatutakoa Nafarroa Garaia eta Beherearen arteko batasun kulturala aldarrikatzeko.</p>\r\n<p>\r\n	Uztailaren 7a: San Ferminak, festarik ezagunenak.</p>\r\n<p>\r\n	Irailaren 29a: Done Mikel Aralarkoaren eguna.</p>\r\n<p>\r\n	Abenduaren 3a: San Frantzisko Xabierkoaren eguna, Nafarroako jai ofiziala.</p>\r\n<p>\r\n	<br />\r\n	&nbsp;</p>\r\n', '');");
	tx.executeSql("INSERT INTO `lurraldeak` (`id`, `izena`, `nice_name`, `testua`, `irudien_egiletzak`) VALUES(6, 'Nafarroa Beherea', 'nafarroa-beherea', '<p>\r\n	Nafarroa Beherea Ipar Euskal Herrian lurraldea da. 1.325 km&sup2; ditu eta 2009an ia 28.000 biztanle zituen. Donibane Garazi da hiriburua, nahiz eta Donapaleuk eta Baigorrik biztanle gehiago izan.</p>\r\n<p>\r\n	Udalerri nagusiak, beraz, hauek dira: Donapaleu (1.701 biztanle 1999an), Baigorri (1.525), Donibane Garazi (1.417), Bidaxune (1.066) eta Donazaharre (880).</p>\r\n<p>\r\n	Apirilaren azken igandean Nafarroaren Eguna ospatzen da Baigorrin, Basaizea elkarteak antolatuta, Nafarroa Garaiaren eta Beherearen arteko batasun kulturala aldarrikatzeko. Ekain-uztailean, bestalde, Euskal Herria Zuzenean festibala egiten da Heletan eta milaka gazte biltzen dira bertan Euskal Herri osotik eta Frantziatik joanda.</p>\r\n<p>\r\n	Herrialdeak berezkoa duen isolamenduaren ondorioz, bertako historia, ohitura, monumentu eta jendearengana diren bezala iritsi ahal izango dugu, kanpoko eragin folklorizatzailerik gabe. Euskal Herriko lurralderik euskaldunena den honek sekulako altxorrak gordetzen ditu bere baitan: Izturitzeko Haitzuloak (agian Euskal Herriko izugarrienak), Bidarraiko inguruak, Baigorri eta Garaziko herriak... dena paisaia orlegi eta ederrez jantzia.</p>\r\n<p>\r\n	Nafarroa Behereko hegoaldea menditsua da, Pirinioak hartzen baititu; iparraldera egin ahala, ordea, orografia are eta lauago egiten da. Pirinioetako mendiak daude hegoaldean. Aipagarriak dira mendebaldean, Baigorri-Ortzaize eskualdean: Iparla (1.044) eta Auza (1.035); ekialderantz, Garaziko eskualdean: Okabe (1.463 m), Txardeka (1.440 m) Kurutxeta (1.331 m), Urkulu (1.413 m) eta Errozate (1.346 m). Ekialdean, Zuberoarekin muga eginez, Arbailako mendiak daude. Garaziko eskualdean Iratiko oihana dago, Pirinioez bestaldera hedatzen dena.</p>\r\n<p>\r\n	Bi ibai nagusik zeharkatzen dute herrialdea: Errobi ibaia (75 km), Donibane Garazitik, Ortzaizetik eta Baigorritik igarotzen dena, eta Biduze ibaia, Arberoa, Landibarre, Oztibarre eta Amikuzetik igarotzen dena. Aturri ibaiaren adarrak dira biak.</p>\r\n<p>\r\n	Iturria: <a href=\"http://eu.wikipedia.org/wiki/Nafarroa_Beherea\" target=\"_blank\" title=\"Lotura leiho berrian\">Wikipedia</a> eta ARGIA</p>\r\n', '<p>\r\n	<strong>ARGAZKIEN EGILETZAK:</strong></p>\r\n<p>\r\n	Orri honetan ikusten dituzun argazki guztiak Creative Commons lizentziapean daude. Argazki bakoitzaren beheko eskuin erpinean dagoen CC ikurraren gainean punteroa jarriz gero, jatorrizko argazkiari buruzko informazioa topatuko duzu. Informazio hori ageri dela, klik eginez gero, jatorrizkora joateko aukera daukazu.</p>\r\n');");
	tx.executeSql("INSERT INTO `lurraldeak` (`id`, `izena`, `nice_name`, `testua`, `irudien_egiletzak`) VALUES(7, 'Zuberoa', 'zuberoa', '<p>\r\n	Zuberoa (zubereraz, Xiberua edo Xiberoa; frantsesez, Soule) Euskal Herriko herrialderik txikiena da eta biztanle gutxien dituena: 814,5 km&sup2; eta 17.018 biztanle. Euskal Herriaren ipar-ekialdean dago, Pirinioen iparraldean. Hiriburua Maule-Lextarre du. Mugakide ditu Bearno ekialdean eta iparraldean, Nafarroa Beherea mendebaldean eta Nafarroa Garaiko Erronkari hegoaldean. Zuberoako euskalkia zuberera da.</p>\r\n<p>\r\n	Hiru lurralde-mota bereizten dira Zuberoan: iparraldean, lur behere lauak dira nagusi; erdialdea menditsuagoa da eta oihanaldeak zabalak dira; hegoaldean, Ahüñemendi edo Pirinio mendiak daude, eta Orhi mendia da Zuberoako garaiena (2017 m, Larrañe).</p>\r\n<p>\r\n	Pirinioetan, mendebaldetik hasiz gero, 2.000 metrotik gorakoa den lehenengo tontorra da Orhi, eta Euskal Herri osoko gailurrik garaienetan laugarrena da.</p>\r\n<p>\r\n	Zuberoa nekazaritza-lurraldea izan da batez ere, baina, gaur egun, zerbitzuek dute pisu nagusia: langileen % 48,9k dihardu sektore horretan, eta % 21,5ek lehen sektorean (2001). Zerbitzuen sektorea da, oro har, Euskal Herriko hiriburuetan jende gehien enplegatzen duen sektorea; Maule-Lextarre da, ordea, sektore horretan jende gutxien hartzen duena (% 56,3). Bestalde, Zuberoan hartzen dute emakumezkoek biztanle okupatuen zati handiena (% 43,1), Lapurdirekin batera (% 45).</p>\r\n<p>\r\n	Horrenbestez, Zuberoak 43 udalerri ditu, eta jendetsuenak hauek dira (1999ko biztanle-kopuruak):</p>\r\n<ul>\r\n	<li>\r\n		&nbsp;&nbsp;&nbsp; Maule-Lextarre (3.347 biztanle).</li>\r\n	<li>\r\n		&nbsp;&nbsp;&nbsp; Sohüta (1.104 biztanle).</li>\r\n	<li>\r\n		&nbsp;&nbsp;&nbsp; Barkoxe (774 biztanle).</li>\r\n	<li>\r\n		&nbsp;&nbsp;&nbsp; Bildoze-Onizepea (743 biztanle).</li>\r\n	<li>\r\n		&nbsp;&nbsp;&nbsp; Atharratze (590 biztanle).<br />\r\n		&nbsp;</li>\r\n</ul>\r\n<p>\r\n	Zuberoako jatorrizko hizkuntza euskara da, zuberera, ia lurralde osoan. Euskarak atzera egin du, azken mendean batez ere, frantsesaren mesedetan, eta euskaldunen ehunekoa gero eta txikiagoa da: 1866-2001 bitartean, hiru herritarretik bi euskal hiztun izatetik (% 65,0), lautik bat edo gutxiago izatera pasatu dira (% 22,8). Erabilera oso apala da (% 5,8).</p>\r\n<p>\r\n	Horrez gain, Zuberoako inguru batzuetan (Montoriko inguru batzuk, Jeztaze eta abar) jatorrizko hizkutza gaskoia da, eta badira inguru elebidun batzuk ere, etxaldearen arabera gaskoia edo euskara nagusituz, Zuberoa ekialdean (Eskiula...) bezala. Dena den, bai euskaldunen artean, bai gaskoi hiztunen artean, hizkuntza horiek galdu eta frantsesarekin ordezkatzeko prozesua azkarra da, belaunaldi gazteetan batez ere.</p>\r\n<p>\r\n	Iturria: <a href=\"http://eu.wikipedia.org/wiki/Zuberoa\" target=\"_blank\" title=\"Ireki esteka leiho berri batean\">Euskarazko Wikipedia</a></p>\r\n', '<p>\r\n	<strong>ARGAZKIEN EGILETZAK:</strong></p>\r\n<p>\r\n	Orri honetan ikusten dituzun argazki guztiak Creative Commons lizentziapean daude. Argazki bakoitzaren beheko eskuin erpinean dagoen CC ikurraren gainean punteroa jarriz gero, jatorrizko argazkiari buruzko informazioa topatuko duzu. Informazio hori ageri dela, klik eginez gero, jatorrizkora joateko aukera daukazu.</p>\r\n');");
}

