function prestatuHerriakTaula(tx) {
	tx.executeSql("DROP TABLE IF EXISTS herriak");
	tx.executeSql('CREATE TABLE IF NOT EXISTS herriak (\
		"id" INTEGER PRIMARY KEY NOT NULL,\
		"alta_data" TEXT NOT NULL,\
		"izena" TEXT NOT NULL,\
		"fk_lurraldea" INTEGER NOT NULL,\
		"testua" TEXT NOT NULL,\
		"sorrera" TEXT NOT NULL,\
		"biztanleak" TEXT NOT NULL,\
		"gps" TEXT NOT NULL,\
		"web" TEXT NOT NULL,\
		"festak" TEXT NOT NULL,\
		"argazkia" TEXT NOT NULL,\
		"gmaps_lat" REAL NOT NULL,\
		"gmaps_lng" REAL NOT NULL,\
		"gmaps_zoom" INTEGER NOT NULL,\
		"gmaps_bista" TEXT NOT NULL,\
		"gmaps_eskubia_lat" REAL NOT NULL,\
		"gmaps_eskubia_lng" REAL NOT NULL,\
		"gmaps_eskubia_zoom" INTEGER NOT NULL);'
	);
	
	tx.executeSql("INSERT INTO `herriak` VALUES(30, '2012-01-30 15:48:16', 'Donostia', 3, '<p>\r\n	Kantauri Itsasoaren ertzean, Bizkaiko golkoaren muturrean, eta Urumea ibaiaren bokalean kokatuta dago hiria. Donostiak hiru hondartza ditu: Ondarreta, Kontxa eta Zurriola. Horiek Ulia, Urgul eta Igeldo mendien artean daude. Azken bi mendi horien artean, eta Kontxa eta Ondarreta hondartzen artean Santa Klara uhartea dago.</p>\r\n<p>\r\n	Ondare edo monumento garrantzitsuei dagokienez, Kursaal Jauregia, Victoria Eugenia Antzokia, Miramar Jauregia eta Eduardo Txillidak egindako burdinezko Haizearen orrazia har daitezke kontutan esaterako.</p>\r\n<p>\r\n	Donostia bertako gastronomiagatik ere da ezaguna. Bertako sukaldari eta jatetxe entzutetsuak (Arzak, Subijana, Berasategi eta Castillo) mundu osoan dira ospetsuak. Halaber, Parte Zaharreko taberna eta jatetxeetan tradizioz zerbitzatzen diren pintxoek ere goi-mailako errekonozimendua lortu dute. Horrez gain, hainbat jai eta jaialdi antolatzen dira urtero: Donostiako Nazioarteko Zinemaldia, Jazzaldia, Musika Hamabostaldia, Kontxako bandera eta San Tomas eguneko feria, besteak beste. Hiriaren zaindariak San Sebastian eta Koruko Andre Maria dira, eta haien jaiegunetan ospatzen dira hiriko jai nagusiak: San Sebastian eguna (Donostiako Danborrada), urtarrilaren 20an, eta Aste Nagusia, abuztuaren 15eko astean.</p>\r\n<p>\r\n	Donostiako festa ezagunenak Danborrada (urtarrilaren 20an) Aste Nagusia (abuztuaren erdialdean), eta Santo Tomas eguna (abenduaren 21ean) dira. Gipuzkoako hiriburua 2016ko Europako Kultur Hiriburu izango da, Poloniako Wroc&#322;aw hiriarekin batera.</p>\r\n', '1180', '186.185', '43°19&#8242;25&#8243;N 1°59&#8242;05&#8243;W ', 'http://www.donostia.org/', 'San Sebastian Eguna. Urtarrilaren 20a\r\nAste Nagusia. Abuztuaren 15eko astea\r\nSanto Tomas. Abenduaren 21a\r\n', 'donostia.jpg', 43.31805801391601562500, -1.98723793029785156250, 14, 'hybrid', 43.31468582153320312500, -1.97822570800781250000, 8);");
	tx.executeSql("INSERT INTO `herriak` VALUES(31, '2012-01-31 11:01:46', 'Irun', 3, '<p>\r\n	Gipuzkoako herri honek Lapurdirekin eta Nafarroarekin egiten du muga. Historian garrantzi handia izan duen herria da, Espainiako eta Frantziako estatuen arteko mugagatik eta bertatik pasa direlako azken mendeetan, mugaren alde batera eta bestera milaka iheslari.</p>\r\n<p>\r\n	Ondasun nabarmenen artean daude XVI. mende inguruan eraikitako Junkaleko Andre Mariren eliza, Udaletxea, San Martzial baseliza, FICOBA azoka eta Oiasso erromatar museoa, besteak beste.</p>\r\n<p>\r\n	Festa nagusiena ekainaren 30ean ospatzen da, San Martzialetan. Egun horretan &ldquo;San Martzialetako alardea&rdquo; egiten dute herritarrek. 1522an Enrike II. Nafarroakoaren gudarosteak &ndash;nafarrez, lapurtarrez, frantsesez eta lanskenet alemaniarrez osaturik&ndash; Bidasoa gurutzatu zuen bertoko gaztelua erasotzeko asmoz. Irungo eta Gipuzkoako miliziek lagunduta, Gaztelako gudarosteak nafarrak garaitu zituen Aldabe mendian ekainaren 30ean, \"San Martzialgo guda\" deiturikoan; hori gogoratzeko ospatzen dute urtero egun hau irundarrek.</p>\r\n', '1776', '61.006', '', 'http://www.irun.org/', 'San Martzialak. Ekainaren 30a', 'irun.jpg', 43.33722686767578125000, -1.79935455322265625000, 14, 'hybrid', 43.33716583251953125000, -1.79077148437500000000, 8);");
	tx.executeSql("INSERT INTO `herriak` VALUES(32, '2012-01-31 11:12:37', 'Oiartzun', 3, '<p>\r\n	Oiartzun Gipuzkoako ipar-ekialdean kokatuta dago. Errenteria, Pasaia et Lezorekin batera Oarsoaldea eskualdea osatzen du.</p>\r\n<p>\r\n	<br />\r\n	Mendiz inguratua dago herria, eta Aiako Harria irudi eta oinarri du. Oiartzun ibaiak bailara zeharkatzen du eta herria honen inguruan dago eraikita.</p>\r\n<p>\r\n	<br />\r\n	Ondasun nabarmenen artean daude XVI. mendean eraikitako San Esteban eliza gotikoa, San Joan Batailatzailarean basilika, 1678an eraikitako Udaletxea, Goi Paleolitoko aztarnategi arkeologikoa den Torre leizea, eta erromatar galeriak ikus daiztezken Arditurriko meategia esaterako. San Estebanen omenez, herriko jaiak abuztuaren 3an ospatzen dira.</p>\r\n', '1453', '10.006', '43°17&#8242;N 1°49&#8242;W', 'http://www.oiartzun.org/', 'San Estebanak. Abuztuaren 3an', 'oiartzun2.jpg', 43.29804229736328125000, -1.86095952987670898438, 15, 'hybrid', 43.29794692993164062500, -1.86012268066406250000, 9);");
	tx.executeSql("INSERT INTO `herriak` VALUES(33, '2012-01-31 11:17:48', 'Hernani', 3, '<p>\r\n	Sagardoagatik ezaguna den Gipuzkoako herriak Astigarraga, Arano, Elduain, Errenteria, Lasarte-Oria eta Urnieta ditu mugakide, eta Urumea ibaiak zeharkatzen du. Santa Barbara mendiaren magalean dago hirigunea. Inguruko beste zenbait menditan, Akola, Igorin, Urdaburu eta Oindi-n historiaurreko aztarna daude.</p>\r\n<p>\r\n	<br />\r\n	Monumentu nagusien artean daude hirigune historikoa, San Joan batailatzailearen eliza, Udaletxea, San Agustin Komentua eta Kale Nagusian dagoen Dorretxea esaterako.</p>\r\n<p>\r\n	<br />\r\n	Herriko festarik ezagunenak San Joan jaiak dira, ekainaren 23tik 26ra bitartean ospatzen direnak.</p>\r\n<p>\r\n	<br />\r\n	Hernaniar ezagunak dira besteak beste Agustin Kardaberaz eta Gabriel Zelaia idazleak, Txirrita eta Maialen Lujanbio bertsolariak, eta Martin Zabaleta (Everestera heldu zen lehen euskalduna).</p>\r\n', '1380', '19.296 ', '43°16&#8242;N 1°58&#8242;W', 'http://www.hernani.net/', 'San Joanak. Ekainaren 23tik 27ra.', 'hernani.jpg', 43.26858139038085937500, -1.97299003601074218750, 15, 'hybrid', 43.26207351684570312500, -1.97259521484375000000, 8);");
	tx.executeSql("INSERT INTO `herriak` VALUES(34, '2012-01-31 11:20:33', 'Pasaia', 3, '<p>\r\n	Jaizkibel eta Ulia mendien artean dagoen udalerria lau barrutik osatzen dute herria; Pasai Antxo, Pasai Donibane, Pasai San Pedro eta Trintxerpek. Bere mugakide dira Hondarribia, Lezo, Errenteria eta Donostia.&nbsp; Bertan dago Euskal Herriko hirugarren portu nagusia, Bilbokoaren eta Baionakoaren atzetik.</p>\r\n<p>\r\n	<br />\r\n	Donostia eta Hondarribiarekin izan du lotura estua beti Pasaiak, hauek hiribilduak zirenean San Pedro Donostiari oso lotuta zegoen eta Donibane Hondarribiari. Azkenean hiribildua izatea lortu zuten biek eta XIX. mende amaieran Gipuzkoako portu nagusi bilakatu zen.</p>\r\n<p>\r\n	<br />\r\n	Donibaneko Santa Isabel gaztelua oso ongi dago mantenduta, eta aztarnak ez ditu guztiz galdu.</p>\r\n', '1805', '15.945', '43°19&#8242;30&#8243;N 1°55&#8242;16&#8243;W ', 'http://www.pasaia.net', 'Donibane. Ekainaren 24a, Pasai Donibane\r\nKepa Deuna. Ekainaren 29a , Pasai San Pedro\r\nSan Fermin. Uztailaren 7a, Pasai Antxo\r\nKarmenak. Uztailaren 16a, Trintxerpe\r\n', 'pasaia.jpg', 43.32208633422851562500, -1.92127704620361328125, 15, 'hybrid', 43.32524108886718750000, -1.92217826843261718750, 9);");
	tx.executeSql("INSERT INTO `herriak` VALUES(35, '2012-01-31 11:53:17', 'Hondarribia', 3, '<p>\r\n	Gipuzkoa ipar-ekialdean dago kokatuta, Txingudiko ezkerraldean, bertan itsaoratzen da Bidasoa ibaia. Hendaia eta Irunekin batera Txingudiko metropoli-eremua osatzen du. Arrantzale herri dotorea da Hondarribia.</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	Ondasun nabarmenen artean daude, besteak beste: 1474 eta 1549 urteen artean eraikitako Jasokundeko eta Sagarrondoko Andre Mariaren eliza, herrihunetik kanpo dagoen Guadalupeko baseliza (XVIII. mendean sortua), Karlos V.aren gaztelua edo Hondarribiko gaztelua deritzona, eta Zuloaga jauregia.</p>\r\n<p>\r\n	<br />\r\n	Gipuzkoako herri honetan ondorengo egunetan ospakizunak egiten dituzte: Inauteriak; apiliaren 25ean, San Markon, Opilaren eguna; ekainaren 29an, San Petrin, arrantzaleen eguna; uztailaren 25ean kutxa entrega; eta irailaren 8an Guadalupeko festak.</p>\r\n', '1776', '61.006 ', '43°20&#8242;16.13&#8243;N 1°47&#8242;19.72&#8243;W', 'http://www.hondarribia.org/', 'San Martzialak. Ekainaren 30a.', 'hondarribia.jpg', 43.36787033081054687500, -1.79508447647094726562, 15, 'hybrid', 43.36243820190429687500, -1.79156112670898437500, 9);");
	tx.executeSql("INSERT INTO `herriak` VALUES(36, '2012-01-31 11:56:21', 'Andoain', 3, '<p>\r\n	Donostialdean dago kokatuta, eta mugakide ditu Urnieta, Aduna, Lasarte-Oria, Zubieta, Billabona, Elduain eta Zizurkil. Oria eta Leitzaran ibaiek udalerria zeharkatzen dute, eta biak gainera, herrigunean elkartu egiten dra.</p>\r\n<p>\r\n	<br />\r\n	Leitzaran ibaiak Leitzarango bailarara zeharkatzen du. Gipuzkoan dagoen bailara bakarra da, monumento megalitikoak daude bertan. Basoa oso zaindua dago eta Biotopo Babestu izendatu da, bertan dagoen ondare kulturala babestu eta berez duen dizdira berreskuratu asmoz.</p>\r\n<p>\r\n	<br />\r\n	Herritar ezagunenen artean daude ondorengoak: Aita Manuel Larramendi filologo eta jesuita, Martin Ugalde kazetari eta idazlea, Joseba Arregi eta Joseba Egibar politikariak, eta Anjel Alkain aktorea.</p>\r\n', '1615', '14.689 ', '43°13&#8242;7&#8243;N 2°1&#8242;8&#8243;W', 'http://www.andoain.org', 'Santa Krutz. Maiatzaren 3an \r\nSan Joan. Ekainaren 24an\r\n', 'andoain.jpg', 43.21962356567382812500, -2.02084064483642578125, 15, 'hybrid', 43.22087478637695312500, -2.02052736282348632812, 8);");
	tx.executeSql("INSERT INTO `herriak` VALUES(37, '2012-01-31 12:23:12', 'Lasarte Oria', 3, '<p>\r\n	Gipuzkoan, Donostiatik zortzi kilometro ingurura dago kokatuta Lasarte-Oria. Donostia, Hernani, Urnieta, Usurbil eta Andoain ditu mugakide.</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	Oria ibaiaren eskuinaldeko ingurunea da udalerria. Herriko ondasun nagusien artean dadue Brigiden Komentua eta San Pedro eliza, eta Manuel Lekuona Kultur Etxea. San Pedrotan ospatzen dira herriko jaiak ekainaren 29an, egun horretan danborrada egiten dute eta herriko zenbait kale igarotzen dituzte partaidean danborrak astinduta.</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	Lasarte herria oso lotua egon izan da Michelin gurpilak egiteko fabrikarekin, eta historikoki bizilagun askok bertan egin izan dute lan, eta nolabait enpresa hau herriarekin lotu izan da, bai auto lasterketak antolatuz, baita herriarentzat izen bereko futbol zelaia eraikiz.</p>\r\n', '1986', '17.889', '43°13&#8242;N 2°1&#8242;W', 'http://www.lasarte-oria.org', 'San Pedroak. Ekain amaieran', 'lasarte.jpg', 43.26576995849609375000, -2.01747179031372070312, 14, 'hybrid', 43.26679611206054687500, -2.02021408081054687500, 8);");
	tx.executeSql("INSERT INTO `herriak` VALUES(38, '2012-01-31 12:38:55', 'Usurbil', 3, '<p>\r\n	Herria ezaguna da bertakosagardotegiengatik eta garai batean Oria ibaian harrapatzen ziren angulengatik. Ekonomian, XX. mendeko erdialdetik industria eta, geroago, zerbitzuak dira bertako sektore nagusiak. Donostia, Lasarte-Oria eta inguruko beste herrien aldean, bere euskal izaerari eutsi dion herria da, euskaldunen portzentaje handia atxikiz bertako bizilagunen artean.</p>\r\n', '1371', '6.062 ', '43°16&#8242;N 2°2&#8242;W', 'http://www.usurbil.net', 'Santixabelak. Uztailaren 2an', 'usurbil.jpg', 43.27098846435546875000, -2.05004453659057617188, 16, 'hybrid', 43.27093887329101562500, -2.04943084716796875000, 9);");
	tx.executeSql("INSERT INTO `herriak` VALUES(39, '2012-01-31 13:39:17', 'Zarautz', 3, '<p>\r\n	Gipuzkoako kostaldeko herri hau bere hondartzagatik da ezaguna, bi kilometro eta erdiko luzera du. Surflari asko biltzen dituen hondartza da, bertan egoten diren olatuengatik, besteak beste Aritz Aranburu eta Hodei Collazo zarauztarrek bertan ikasi hasi zuten euren bidea.</p>\r\n<p>\r\n	<br />\r\n	Surfaz gain golf zelaia ere badu herriak, Talaimendiren magalean dago Real Club de Golf-a hondartzaren alboan, paisaia ikusgarri baten aurrean.</p>\r\n<p>\r\n	<br />\r\n	Getaria eta Aia ditu mugakide eta turismoa du jarduera nagusi. Udan brtako ohiko biztanle kopurua bikoiztu egiten da.<br />\r\n	San Pelaioak (ekainaren 26a), Aste Nagusia (abuztuaren 14tik 16ra), eta Euskal Jaiak (irailaren 9an) dira jaiegun ezagunenak.</p>\r\n', '1237', '22.697 ', '43°17&#8242;00&#8243;N 2°10&#8242;00&#8243;W', 'http://www.zarautz.com', 'San Pelaioak. Ekainaren 26an\r\nEuskal Jaiak. Irailaren 9an\r\n', 'zarautz.jpg', 43.28542327880859375000, -2.16743946075439453125, 15, 'hybrid', 43.28070068359375000000, -2.17144775390625000000, 9);");
	tx.executeSql("INSERT INTO `herriak` VALUES(40, '2012-01-31 13:51:16', 'Zumaia', 3, '<p>\r\n	Urola ibaia Zumaian itsasoratzen da, hala, bi hondartza hartuz: Santiago eta Itzurun. Azken hori izaten da surfistentzako gune preziatua. Halere, herriaren ikur nagusia flyscha da, mundu osoan bakarrenetakoa. Besteak beste, Nazioarteko Estratigrafia Batzordeak Paleozeno garaiaren zabalera hoberen ikusten den munduko leku izendatu zuen.</p>\r\n<p>\r\n	Zumaiako turismoa erabat dago lotuta itsoarekin, eta itsasontziz egiten diren ibilaldiak oso ezagun bihurtu dira azken urteetan.</p>\r\n<p>\r\n	<br />\r\n	Jaiegunei dagokienez, San Telmo jaiak ospatzen dituzte martxoa eta apirila artean, egun horretan marinelen jantzi urdinak jakintza ateratzen dira herritarrak, eta, horrez gain, San Pedroak ekainaren 29an.</p>\r\n', '1347', '9.461', '43°17&#8242;N 2°15&#8242;W', 'http://zumaia.net/', '', 'zumaia.jpg', 43.29557418823242187500, -2.25540518760681152344, 15, 'hybrid', 43.29713439941406250000, -2.25596094131469726562, 9);");
	tx.executeSql("INSERT INTO `herriak` VALUES(41, '2012-01-31 13:53:54', 'Mutriku', 3, '<p>\r\n	Gipuzkoako ipar-mendebaldean dago kokatuta Mutriku, Bizkaiarekin muga-mugan. Sei hondartza ditu herriak: Ondarbeltz, Alkolea, Portuko hondartza, Burumendiko hondartza, Zazpi Hondartzak eta Saturraran.</p>\r\n<p>\r\n	<br />\r\n	1995ean Eusko Jaurlaritzako monumentu-multzo izendatu zuen Mutrikuko Hirigune Historikoa. Horrren lur zatiketa gotikoa da neurri batean, eta XVItik&nbsp; XVIII mendera bitartean sortutako jauregi eta etxeak aurki daitezke bertan. Beste zenbaiten artean neoklasiko estiloko Jasokundeko Ama Birjinaren eliza; Txurruka plaza, Kosme Damian Txurruka almirantearen omenez egindako eskultura duena; eta XVIII. mendean eraikitako&nbsp; Montalibet jauregia.</p>\r\n', '1209', '5.029', '43°18&#8242;N 2°23&#8242;W', 'http://www.mutrikukoudala.net', 'Berdel eguna: apirileko lehen larunbatean\r\nSan Isidro: maiatzaren 15ean\r\nMadalenak: uztailaren 21-25ean\r\nKalbario: irailaren 14-16an\r\nArrantzaleen eguna: irailaren 16an', 'mutriku1.jpg', 43.30651855468750000000, -2.38512754440307617188, 16, 'hybrid', 43.30692672729492187500, -2.38459968566894531250, 9);");
	tx.executeSql("INSERT INTO `herriak` VALUES(42, '2012-01-31 13:57:03', 'Azpeitia', 3, '<p>\r\n	Gipuzkoako udalerri honek Deba, Zestoa, Errezil, Beizama, Beasain, Ezkio-Itsaso, Zumarraga eta Akoitia ditu mugakide. Sorrerak Iraurgi ibaiarenkin du lotura estua. 1310ean Gaztelako Fernando IVak emandako hiri-gutunaz hiribildu bihurtu zen Garmendia Iraurgiko izenarekin, Azpeitia izena XIV. mendean izan zuen, eta Azpeitia eta Azkoitiaren arteko mugak 1491an zehaztu ziren.</p>\r\n<p>\r\n	<br />\r\n	San Ignazio Loiolakoa bertan jaio zenez, bere omenez XVII. mendean sortutako barroko estiloko Loiolako Santutegia bertan dago. Euskal burdinbidearen museoa, Ingurugiro etxea, Soreasuko San Sebastian parrokia, Loiola eta Olatzko ermita ikugarri daude herrian.</p>\r\n<p>\r\n	<br />\r\n	Herriko festa nagusiak, Danborrada (urtarrilak 19-20), Inauteriak, Saninazioak (uztailak 30tik abuztuak 2ra) eta Santo Tomas eguna (abenduak 21) dira.</p>\r\n', '1310', '14.351', '43°10&#8242;55&#8243;N 002°15&#8242;55&#8243;O', 'http://www.azpeitia.net', 'San Inazioak. Uztailaren 31an\r\nSanto Tomas. Abenduaren 21ean\r\nSan Sebastian. Urtarrilak 20an\r\n', 'azpeitia.jpg', 43.18080520629882812500, -2.26472854614257812500, 15, 'hybrid', 43.18395996093750000000, -2.26495170593261718750, 9);");
	tx.executeSql("INSERT INTO `herriak` VALUES(43, '2012-01-31 13:58:52', 'Azkoitia', 3, '<p>\r\n	Gipuzkoako erdialdean, Izarraitzen magalean eta Urola ibaiaren ertzean dagoen udalerria da. Deba, Azpeitia, Antzuola, Zumarraga, Elgoibar, Mundaro eta Bergara ditu mugakide.</p>\r\n<p>\r\n	<br />\r\n	Lehen aztarnak herriaren inguruko mendietan aurkitu dituzte, Izarraitzeko dolmenak eta Munoaundiko kastroaren inguruetan. Aztarna horiek 5.000 urte atzera egiteko parada ematen dute.</p>\r\n<p>\r\n	<br />\r\n	Santa Maria La Real XVI. mendeko eliza da, hiru nabe eta zortzi koloma ditu.1324an hiribildu titulua eman zion Alfontso XIak eta Azkoitia eta azpeitiaren arteko muga 1491an zehaztu zuten.</p>\r\n<p>\r\n	<br />\r\n	Azkoitiko hirigune historikoa Eusko Jaurlaritzak kultura ondasun izendatu zuen 2005eko urtarrilaren 25an, monumentu multzo sailkapenarekin.</p>\r\n', '1331', '11.417 ', '43°11&#8242;24&#8243;N 2°18&#8242;36&#8243;W', 'http://www.azkoitikoudala.info', 'Abuztuaren 15ean', 'azkoitia.jpg', 43.17698287963867187500, -2.31124877929687500000, 15, 'hybrid', 43.17814254760742187500, -2.31031322479248046875, 9);");
	tx.executeSql("INSERT INTO `herriak` VALUES(44, '2012-01-31 14:00:59', 'Arrasate', 3, '<p>\r\n	Debagoiena inguruko herri populatuena da, eta Aramaio, Aretxabaleta, Bergara, Elorrio, eta Oñati ditu mugakide.</p>\r\n<p>\r\n	<br />\r\n	Gipuzkoan ezagutzen diren aztarna zaharrenak bertan daude; Lezetxiki haitzuloan esaterako historiaurreko aztarnak aurkitu dira; besteak beste, Neanderthal gizakien hezurrak eta Paleolito Ertaineko zenbait aztarna. Herriak Erdi Aroko hirigunearen egitura eta ezaugarriak ditu, kale-egituraketa pradignatikoa da eta hiriguneak egitura eliptikoa du. 1997ko otsailean Eusko Jaurlaritzak Arrasateko Hirigune Hsitorikoa monument-multzo izendatu zuen.</p>\r\n<p>\r\n	<br />\r\n	San Joan jaiak dira herriko festa ezagunenak, ekainaren 23tik 26ra bitartean ospatzen dira, baita Maritxu Kajoi ere urriko lehen ostiralean ospatzen da urtero eta jendea elegante jantzita ateratzen da kalera.</p>\r\n', '1260', '21.972', '43°3&#8242;55&#8243;N 2°29&#8242;35&#8243;W', 'http://www.arrasate-mondragon.org', 'San Joanak. Ekainaren 24an\r\nMaritxu kajoi. Urriko lehen ostiralean\r\n', 'arrasate.jpg', 43.06556320190429687500, -2.48861789703369140625, 15, 'hybrid', 43.06545257568359375000, -2.48886251449584960938, 9);");
	tx.executeSql("INSERT INTO `herriak` VALUES(45, '2012-01-31 14:06:29', 'Getaria', 3, '<p>\r\n	Getaria Urola Kosta eskualdeko udalerria da, itsas bazterrean kokatua. Ekialdean Zarautz du eta mendebaldean Zumaia. Herri honen ingurune naturala oso berezia da bere ikur garrantzitsu bategatik batez ere, baina baita bere bi hondartzengatik eta ondare historikoagatik ere.</p>\r\n<p>\r\n	Juan Sebastian Elkano marinelaren jaioterria da, munduri bira osoa eman zion lehena izandakoa. Euskal kostaldeko turismogune ezaguna da, Getariako Txakolinaren sorleku nagusia. Bertan dago San Anton mendia, lehorrera lotua dagoen mendixka ezaguna, kostaldeko itxura aipagarria.</p>\r\n<p>\r\n	Getariko arratoia izenez ezaguna den San anton mendira begira dago Malkorbe hondartza, eta mendiaren mendebaldean surfa gustuko dutenentzako hondartza dago: Gaztetape. Horietaz gainera, ingurune naturalari dagokionez, aipatzekoa da Getariko arratoian landaredia eta zuhaitza mota ugari aurki daitezkela, bertakoak zein exotikoak.</p>\r\n<p>\r\n	Ondare historikoari dagokionez, azpimarragarriak dira gotiko estiloko San Salbador eliza, Etxe barrokoa eta gotiko estiloko Zarautz dorretxea. Kristobal Balentziaga Museoa Gipuzkoako herri honetan dago kokatuta, bertan Balentziaga jostun eta diseinatzailearen lanak ikus daitezke.</p>\r\n', '1209', '2.666', '43°18&#8242;12&#8243;N 2°12&#8242;16&#8243;W', 'http://www.getaria.net', 'Salbatoreak. Abuztuaren 6an', 'getari-2.jpg', 43.30300903320312500000, -2.20353126525878906250, 16, 'hybrid', 43.30348968505859375000, -2.20369005203247070312, 8);");
	tx.executeSql("INSERT INTO `herriak` VALUES(46, '2012-01-31 18:53:43', 'Tolosa', 3, '<p>\r\n	Tolosaldeko udalerri nagusia da eta Oria ibaiak zeharkatzen du herria, bitan banatuz. Horrez gain, gune menditsuan dago, ondorengo mendiak baititu inguruan: Uzturre, Belabieta, Loatzo, Erroizpe, Otsabio, Mendikute, Ubeltz eta Herniozabal besteak beste.</p>\r\n<p>\r\n	<br />\r\n	Herria ezaguna egin da bertako Inauteriengatik. Ostegun gizenean hasten dira jaiak, hau da tolosarrentzat urteko egun garrantzitsuenetako bat, ondaoren ostiral mehea ospatzen dute, larunbat edo &ldquo;zaldunita&rdquo; bezperan ume eta helduek danborra jotzen dute, igandea edo&rdquo; zaldunita&rdquo; eguna, astelehena edo &ldquo;astelehenitan&rdquo; ume gazte eta helduak konpartsan ateratzen dira, eta, astearte edo &ldquo;asteartita&rdquo; izanik azken eguna, inauteriei agur esaten hasi eta amaitzen dute eguna. Inauteriak herriko jai ezagunenak bihurtu diren arren San Joan jaiak ere ospatzen dituzten ekaineko azkeneko astean.</p>\r\n', '1256', '18.232', '43°08&#8242;16&#8243;N 2°04&#8242;16&#8243;W', 'http://www.tolosakoudala.net/', 'Inauteriak \r\nSan Joanak. Ekainaren 24an', 'tolosa2.jpg', 43.13547134399414062500, -2.07384109497070312500, 15, 'hybrid', 43.13381195068359375000, -2.07435607910156250000, 10);");
	tx.executeSql("INSERT INTO `herriak` VALUES(47, '2012-01-31 18:56:00', 'Beasain', 3, '<p>\r\n	Gipuzkoan, Goierri eskualdean, udalerria da, Oria ibaiaren ertzean dago kokatuta. Azken ibai honek herria zeharkatzen du. Udalerritik gertu daude Aizkorriko eta Aralarko mendilerroak. Udalerrian Arriarango urtegia dago, herrigunetik joanda ibilbide ederra egin daiteke. Beste ibilbide bat egiteko Usurbe mendia gune paregabea da.</p>\r\n<p>\r\n	<br />\r\n	Ondasun nabarmenen artean daude XVII. mendean eraikitako Letamendi baserria, XVI. mendean sortutako Loinazko Andre Mariaren baseliza eta Erdi Aroko Jasokundeko Andre Mariaren eliza.</p>\r\n', '1615', '13.717 ', ' 43°02&#8242;50&#8243;N 2°11&#8242;46&#8243;W', 'http://www.beasain.org', '', 'beasain.jpg', 43.04392623901367187500, -2.20739364624023437500, 14, 'hybrid', 43.04727935791015625000, -2.20440673828125000000, 10);");
	tx.executeSql("INSERT INTO `herriak` VALUES(48, '2012-01-31 19:01:11', 'Orio', 3, '<p>\r\n	Gipuzkoako kostaldean, Urola Kosta eskualdekoan, dago kokatuta. Bi hondartza ditu Oriok, Kukuarri mendiaren magaldean dagoen Antilla hondartza, ezagunena, eta Oribarzar izeneko hondartza txikia.</p>\r\n<p>\r\n	<br />\r\n	Azken urteetan arrantzale kopurua nabarmen jaitsi den arren, arrantzari lotutako herria da Orio, eta ibai ertzean ikus daitezke baporeak asteko edzoein egunetan. Mendi ibilaldiak egiteko gune zoragarria da eta Oria ibaian piragoa eta kanua jarduerak egitea ere ohikoa da.</p>\r\n<p>\r\n	<br />\r\n	Bi festa nagusi ospatzen dria herrian:&nbsp; ekainaren&nbsp; 29an Sanpedroak, eta abenduaren 5tik 8ra Sanikolasak.</p>\r\n', '1329', '5.270 ', '43°16&#8242;N 2°13&#8242;W', 'http://www.oriora.com/', 'Sanikolasak. Abenduaren hasieran.\r\nSan Pedroak. Ekainaren 29an', 'orio.jpg', 43.27517318725585937500, -2.12551116943359375000, 15, 'hybrid', 43.27815628051757812500, -2.12629222869873046875, 9);");
	tx.executeSql("INSERT INTO `herriak` VALUES(49, '2012-02-01 17:30:19', 'Orereta', 3, '<p>\r\n	Orereta Oiartzualdean dago kokatuta, Donostiatik zazpi kilometro ingurura.&nbsp;</p>\r\n<p>\r\n	Gaztelako Alfontso XI.ak herri eskutitza eman zion 1320an, Villanueva de Oiarso izenpean. Arkitekturari dagokionez, Torrekua eta Morrontxo dorretxeak, San Marko eta Txoritokietako gotorlekuak, Iturrizako eta Urantzuko oinetxeak eta Zubiarreko jauretxea daude bertan.</p>\r\n<p>\r\n	Herriko jaiak madalenetan, uztailaren 22an, ospatzen dira.</p>\r\n', '1320', '39.217', '43°18&#8242;45&#8243;N 1°53&#8242;59&#8243;W', ' http://www.errenteria.net/', '', 'orereta.jpg', 43.31171798706054687500, -1.90256595611572265625, 15, 'hybrid', 43.31121826171875000000, -1.90002107620239257812, 9);");
	tx.executeSql("INSERT INTO `herriak` VALUES(50, '2012-02-01 17:32:58', 'Zestoa', 3, '<p>\r\n	Gipuzkoako iparraldean, Urola Kosta eskualdean, dagoen udalerria da. Mugakide ditu Deba, Zumaia, Aizarnazabal, Aia, Azpeitia eta Errezil. Onasun historikoen artean daude XV-XVI. mendeetan eraikitako gotiko estiloko Lili Jauregia, Euskal Herriko Bainuetxe zaharrena (1804koa), Arotzena hotela&nbsp; eta XVI. mendean eraikitako Zubi zaharra.</p>\r\n<p>\r\n	<br />\r\n	Historiaurreko ondare izugarria dago udalerrian.&nbsp; 2008an Ekainberri zentroa ireki zuten, bertan&nbsp; labar-pinturen kopiak daude eta kobazuloaren egitura bera duen zentroa da. Gainera, Aizarna auzoan, garrantzia handiko aztarnategi arkeologikoak daude Erraila eta Amalda kobatan.</p>\r\n', '1383', '3.655 ', '43°14&#8242;24&#8243;N 2°15&#8242;28&#8243;W', 'http://www.zestoa.net', '', 'zestoa.jpg', 43.23966979980468750000, -2.25826978683471679688, 16, 'hybrid', 43.23960494995117187500, -2.25722694396972656250, 10);");
	tx.executeSql("INSERT INTO `herriak` VALUES(51, '2012-02-01 17:43:02', 'Eibar', 3, '<p>\r\n	Eibar Gipuzkoan, Debabarrena eskualdean, dagoen udalerri handiena da. Ermua, Mallabia, Zaldibar, Soraluze,Elgoibar, Bergara eta Elgeta ditu mugakide. Eibarrek beti izan du metalarekin zerikusia duen industriarekin lotura estua.</p>\r\n<p>\r\n	<br />\r\n	Herriko ondare garrantzitsuenen artean XVI eta XVII. mendeen artean eraikitako gotiko estiloko San Andres eliza dago, baita XVII. mende hasieran sortutako Arrateko Amaren santutegia, XVII. mendean eraikitako Aldatze jauregia eta XVII. mendeko Untzueta jauregia ere.</p>\r\n<p>\r\n	<br />\r\n	Herriko festan San Joan jaiak, ekainaren 24an, eta San Andres egunean azaroaren 30ean ospatzen dituzte.</p>\r\n', '1346', '27.396', '43°11&#8242;2&#8243;N 2°28&#8242;24&#8243;W	', ' http://www.eibar.es', 'San Joanak. Ekainaren 24an\r\nSan Andres. Azaroaren 30ean\r\n', 'eibar.jpg', 43.18374252319335937500, -2.47334003448486328125, 15, 'hybrid', 43.18408584594726562500, -2.47253322601318359375, 8);");
	tx.executeSql("INSERT INTO `herriak` VALUES(52, '2012-02-01 17:45:14', 'Elgoibar', 3, '<p>\r\n	Gipuzkoan, Debabarrena eskualdean, dago kokatuta udalerria. Bizkaia du mugakide, hain justu Mendaro, Eibar, Soraluze, Azkoitia eta Markina dira honen herri mugakideak. Irukurutzeta (899 metro) eta Kalamua (768 metro) mendiak ditu inguruan. Deba ibaiak zeharkatzen du udalerriak hegoaldetik iparralderantz.</p>\r\n<p>\r\n	<br />\r\n	Industriarekin lotutako herria da, makina erremintarekin lotutako empresa asko dago bertan, eta horregatik makina erremintaren hiriburua deitze diote.</p>\r\n<p>\r\n	<br />\r\n	Herriko ondare garrantzitsuenetakoak dira Altzolako Lonja etxea, XV. mendean eraikitako Zabala Torre baserria, eta mende bereko Altzola dorretexea.</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	San Bartolomeren omenezko eguna ospatzen dute, abuztuaren 23an.</p>\r\n', '1346', '11.360 ', '43°12&#8242;52&#8243;N 2°24&#8242;56&#8243;W', 'http://www.elgoibar.org', 'San Bartolomeak. Abuztuaren 23an', 'elgoibar.jpg', 43.21480941772460937500, -2.41102695465087890625, 15, 'hybrid', 43.21437072753906250000, -2.41472625732421875000, 8);");
	tx.executeSql("INSERT INTO `herriak` VALUES(53, '2012-02-01 17:47:30', 'Bergara', 3, '<p>\r\n	Gipuzkoan, Debagoiena eskualdean, dago udalerria. Deba ibaiak udalerria zeharkatzen du eta Gipuzkoako herri handienetakoa da. Historikoki Debagoienako hiriburutzat hartu izan da, duen kokapenari esker herri garrantzitsua izan da. Udalerriaren mugakideak dira Eibar, Soraluze, Azkoitia, Antzuola, Oñati, Arrasate eta Elgeta.</p>\r\n<p>\r\n	<br />\r\n	Herriko ondasun nagusien artean daude Agirre baserria, Artzamendi dorretxea, Bergarako Errege Mintegia eta Egino-Mallea Jauregia.</p>\r\n<p>\r\n	<br />\r\n	Jai nagusien artean dadue Aratusteak, ekainaren 30ean ospatzen den San Martzialgo Erromeria, irailaren 16an San Martin Agirre jaiak eta urriaren 12an Elosuko Ganadu Feriak.</p>\r\n', '1268', '14.577 ', '43°7&#8242;N 2°25&#8242;W', 'http://www.bergara.net/', 'Pentekoste Jaiak.', 'bergara.jpg', 43.11714935302734375000, -2.41343021392822265625, 15, 'hybrid', 43.11796188354492187500, -2.41313838958740234375, 9);");
	tx.executeSql("INSERT INTO `herriak` VALUES(54, '2012-02-01 17:50:36', 'Deba', 3, '<p>\r\n	Gipuzkoako kostaldean, Debabarrenean dagoen udalerria da eta bertan itsasoratzen da Deba ibaia. Udalerriaren mugakide dira Mutriku, Zestoa, Zumaia eta Azkoitia. Hirigunearen ondoan 420 metro dituen hondartza du.</p>\r\n<p>\r\n	Herriko ondasun garrantzitsuena leizeak eta historiaurreko aztarnategiak dira, horietatik garrantzitsuena Ekaingo haitzuloa da, Unescok Gizartearen Ondare izendatua.</p>\r\n<p>\r\n	Erlijio arkitekturako lanetan Santa Maria Eliza gailendu behar dugu, hala ere aipagarriak dira Udal Azoka, XV. Mendeko Agirre Jauregia, Bañez Etxea, Itziarko Santutegia eta Sasiolako eliza&nbsp; ere.</p>\r\n<p>\r\n	Deban ibilibide ezberdinak egiten dituzte ingurune naturala hobeto ezagutzeko. Debatik Zumaiararteko kostaldea Biotopo Babestua da eta bi udalerri hauek Mutrikurekin batera Euskal Kostaldeko Geoparkea osatzen dute. Bisita gidatuen aukera zabala dago: Flyscharen Ibilbidea, Marearteko zabalguneko biodibertsitatea, Karsta, Espeleologia, artzain txikiak&hellip;</p>\r\n', '1294', '5.420 ', ' 43°17&#8242;43&#8243;N 1°20&#8242;0&#8243;W', 'http://www.deba.net', '', 'deba-2.jpg', 43.29573059082031250000, -2.35330581665039062500, 15, 'hybrid', 43.29519653320312500000, -2.35299253463745117188, 10);");
	tx.executeSql("INSERT INTO `herriak` VALUES(55, '2012-02-01 17:54:11', 'Oñati', 3, '<p>\r\n	Herri hau Gipuzkoako hego-mendebaldean dago kokatuta, Debagoiena eskuadean. Bergara, Antzuola, Barrundia, Donemiliaga, Legazpi,&nbsp; Arrasate eta Aretxabaleta ditu mugakide.</p>\r\n<p>\r\n	<br />\r\n	Gipuzkoa barrutik ezagutzeko aukera dago herri honetan, Oñati-Arrikutz haitzuloaren bitartez, honi &laquo;Seigarren Kontinentea&raquo; deitu izan diote. Hori bisitarientzako irekita egoten da, 60 minutuko bisitaldiak egiten dituzte bertan.</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	Ondare historikoari dagokionez, garrantzitsuak dira \"Sancti Spiritus\" Unibertsitatea,&nbsp; San Migel Parrokia, Arantzazu Santutegia, Arkitektura zibil eta erlijiosoa eta Oñatiko San Migel Errota.</p>\r\n', '1467', '11.033 ', ' 43°2&#8242;N 2°25&#8242;W', 'http://www.oinati.eu', '', 'onati-2.jpg', 43.03282165527343750000, -2.41171360015869140625, 15, 'hybrid', 43.03293228149414062500, -2.41116428375244140625, 9);");
	tx.executeSql("INSERT INTO `herriak` VALUES(56, '2012-02-01 18:09:35', 'Miarritze', 4, '<p>\r\n	Lapurdiko kostaldean, Biarrizko Kantomaneduan, dago kokatuta. Herri honen mugakideak Angelu, Arbona, Arrangoitze eta Bidarte dira. Miarritzera bitartean kostalde zuzena aurki daiteke, baina udalerri honetatik aurrera lurzoru malkartsua da nagusi kostaldean.</p>\r\n<p>\r\n	<br />\r\n	Udalerri honetan Art D&eacute;co estiloko eraikin batean 1933 urtean eraikitako Itsas-museoa dago, bertan itsas-animalia eta hegaltien bilduma handi bat aurki daiteke.</p>\r\n<p>\r\n	<br />\r\n	Kulturari dagokionean zinemari garrantzia handia ematen zaiola ikus daiteke, urtean bi jaialdi ospatzen baitira: Miarritzeko Zinema Latinoamerikarraren Jaialdia eta Miarritzeko Nazioarteko Ikus-entzunezko Sailen Jaialdia.</p>\r\n<p>\r\n	<br />\r\n	Udalerrian sei hondartza daude; Miramar, Handia, Portu Zaharra, Euskal kostaldea, Marbella eta Milady.</p>\r\n', '', '26.828', '43°29&#8242;N 1°33&#8242;W', 'http://www.biarritz.fr/', '', 'biarritzx.jpg', 43.47628021240234375000, -1.55053138732910156250, 14, 'hybrid', 43.48347091674804687500, -1.55850076675415039062, 8);");
	tx.executeSql("INSERT INTO `herriak` VALUES(57, '2012-02-01 18:16:29', 'Donibane Lohizune', 4, '<p>\r\n	Lapurdiko udalerri hau Bizkaiko golkoan dago kokatuta, Donibane Lohizuneko kantomanenduan hain justu. Udalerria Urdazuri ibaia amaitzen den lekuan dago, eta mugakide ditu Getaria, Bidarte, Azkaine, Ziburu, Ahetze eta Senpere.</p>\r\n<p>\r\n	<br />\r\n	Heririan 2,5 hektareako lorategi botanikoa dago, itsasotik 50 metro ingurura. Hori irekitzeko proposamena Miarritzeko Historia Natualaren Museoko langile Paul Jovetek 1980ko hamarkadan egin zuen arren, 2008an ireki zuten.</p>\r\n<p>\r\n	<br />\r\n	Ondasun nagusien artean daude San Joan Batailatzailearen eliza eta Luis XIV.aren etxea.</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	Jose Antonio Agirre lehen lehendakari izandakoaren oroigarria dago, Telesforo Monzon izandakoaren etxearen aurrean.</p>\r\n', '', '13.579 ', '43°23&#8242;23&#8243;N 1°39&#8242;40&#8243;W', 'http://www.ville-saintjeandeluz.fr/', '', 'donibane-lohizune.jpg', 43.39154434204101562500, -1.64833545684814453125, 14, 'hybrid', 43.38813018798828125000, -1.66203403472900390625, 9);");
	tx.executeSql("INSERT INTO `herriak` VALUES(58, '2012-02-01 18:17:59', 'Urruña', 4, '<p>\r\n	Urruña Lapurdiko kostaldean dago kokatuta. Untxin, Bidasoa, Ibardin eta Udazuri errekak pasatzen dira bertatik, eta Ibardingo lepoa (317 metro) mendia udalerriarenb alboan dago. Udalerri honen mugakideak dira ZIburu, Hendaia, Azkaine, Biriatu eta Bera.</p>\r\n<p>\r\n	<br />\r\n	Ondare historikoari dagokionez, XI. mendean eraikitako San Bixintxo eliza, XIV, XVI eta XVII. mendeetan eraikitako Urtubiako gaztelua, eta XVIII. mendeko Ama Sokorrikoaren Kapera.</p>\r\n', '1654', '7.759 ', '43°21&#8242;N 1°41&#8242;W', 'http://www.urrugne-tourisme.com', '', 'urrunax.jpg', 43.36410903930664062500, -1.69990897178649902344, 15, 'hybrid', 43.36230087280273437500, -1.69911289215087890625, 9);");
	tx.executeSql("INSERT INTO `herriak` VALUES(59, '2012-02-01 18:20:14', 'Getari', 4, '<p>\r\n	Lapurdiko kostaldeko udalerria da, Donibane Lohikutzeko kantomaneduan, Lapurdi Itsasegian dago kokatuta hain justu. Ondorengo udalerriak ditu mugakide: Bidarte, Donibane Lohizune eta Ahetze.</p>\r\n<p>\r\n	<br />\r\n	Ondasun historiko nagusien artean daude XVI. mendeko San Nikolas eliza eta Getariko talaia. Azken hau XVIII. mendeko dorrea da eta arrantzaleei baleak zeudela ohartaratzeko erabiltzen zuten. 1993an monumento historiko izendatu zuten Talaia.</p>\r\n', '1633', '1.365', '43°25&#8242;N 1°26&#8242;W', 'http://www.guethary-tourisme.com', '', 'getarix.jpg', 43.42184829711914062500, -1.60949707031250000000, 15, 'hybrid', 43.42121124267578125000, -1.60843276977539062500, 9);");
	tx.executeSql("INSERT INTO `herriak` VALUES(60, '2012-02-01 18:24:52', 'Bidarte', 4, '<p>\r\n	Lapurdiko kostaldean dagoen udalerria da, bertan itsatoratzen da Uhabia ibaia. Udalerri mugakideak ditu Biarritz, Donibane Lohizune, Getaria, Arbona eta Ahetze.</p>\r\n<p>\r\n	<br />\r\n	Ilbarritz gaztelua dago bertan, 1895 eta 1897 urte bitartean eraikia. Hau Lehenengo Mundu gerran ospitale izan zen eta Espainiako gerra zibilagatik ihesean zeuden pertsonentzako ostatua izan zen gerora. Gaur egun berritze prozesuan dago gaztelua.</p>\r\n', '', '5.614 ', '43°26&#8242;N 1°35&#8242;W', 'http://www.mairie-bidart.com/', '', 'bidarte.jpg', 43.43818283081054687500, -1.58825397491455078125, 15, 'hybrid', 43.43911361694335937500, -1.58922815322875976562, 9);");
	tx.executeSql("INSERT INTO `herriak` VALUES(61, '2012-02-01 18:28:51', 'Ezpeleta', 4, '<p>\r\n	Lapurdin, Lapurdi Garaian, dagoen udalerria da, eta mugakide ditu Uztaritze, Larresoro, Ainhoa, Zuraide eta Itsasu herriak. Latsa, Urotze, Larreko eta Antzara errekek udalerria gurutzatzen dute.</p>\r\n<p>\r\n	<br />\r\n	Ezpeleta ezaguna da bertako piper gorriengatik, Ezpeletako piperrak. Horiek oso ezagunak dira eta sukaldaritzan oso erabiliak. Ezpeletan horrela diote: &ldquo;Euskaldun, fededun, biperdun&rdquo;. Urtero besteak beste piper eguna ospatzen dute.</p>\r\n', '', '1.879 ', '', 'http://espelette.mairie.com', '', 'ezpeleta.jpg', 43.34000778198242187500, -1.44749164581298828125, 16, 'hybrid', 43.34037780761718750000, -1.44870185852050781250, 9);");
	tx.executeSql("INSERT INTO `herriak` VALUES(62, '2012-02-01 18:31:45', 'Hazparne', 4, '<p>\r\n	Lapurdiko ekialdean dagoen kokatuta udalerria, Hazparneko Kantonamenduaren hain justu, eta mugakide ditu Beskoitze, Ahuti, Kanbo; haltsu, Jatsu, Mugerre, Makea, Lekorne, Lekuine, Aiherra eta Bastida. Bertatik pasatzen da Hazketa erreka.</p>\r\n', '', '5 839 ', '43°23&#8242;8&#8243;N 1°18&#8242;13&#8243;W', ': http://www.ville-hasparren.fr/', '', 'hazparne.jpg', 43.38356018066406250000, -1.30481958389282226562, 15, 'hybrid', 43.38348007202148437500, -1.30472087860107421875, 9);");
	tx.executeSql("INSERT INTO `herriak` VALUES(63, '2012-02-01 18:35:07', 'Uztaritze', 4, '<p>\r\n	Lapurdiko erdialdean dago kokatuta udalerria. 1177tik 1789 urtera bitartean, Frantziako Iraultza gertatu bitartean, Lapurdiko hiriburua izan zen.</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	Mugakide ditu Milafranga, Basusarri, Arrangoitze, Senpere, Zuraide, Ezpeleta, Larresoro eta Jatsu.</p>\r\n<p>\r\n	<br />\r\n	Errobi ibaia pasatzen da bertatik eta Uhabi ibaiak udalerria alderik alde gurutzatzen du.</p>\r\n', '5 517 ', '', '43°24&#8242;N 1°27&#8242;W', 'http://www.ustaritz.fr/eu.html', '', 'uztaritze.jpg', 43.39550781250000000000, -1.45575284957885742188, 16, 'hybrid', 43.39573287963867187500, -1.45475292205810546875, 9);");
	tx.executeSql("INSERT INTO `herriak` VALUES(64, '2012-02-01 18:38:51', 'Heleta', 6, '<p>\r\n	Nafarroa Beherean dago kokatuta udalerria, Arberoa eskualdean. Udalerri honek mugakide ditu Aiherra, Lekorne, Armendaritze, Iholdi eta Irisarri. Bertan jaiotzen da Harana ibaia, eta Arberoa, Errobi, Lakako eta Baigura errekak bertatik pasatzen dira.</p>\r\n<p>\r\n	<br />\r\n	2009 urteaz geroztik, Euskal Herria Zuzenean jaialdia bertan ospatzen da. Uztaileko lehenengo asteburuan, milaka pertsona bilduz asteburu batez.</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	Abuztuaren 15ean herriko jaiak ospatzen dituzte.</p>\r\n', '', '680', '43°18&#8242;33&#8243;N 1°14&#8242;35&#8243;W', '', 'Abuztuaren 15ean', 'heleta.jpg', 43.30817794799804687500, -1.24386906623840332031, 17, 'satellite', 43.30740737915039062500, -1.24391841888427734375, 10);");
	tx.executeSql("INSERT INTO `herriak` VALUES(65, '2012-02-01 18:43:58', 'Senpere', 4, '<p>\r\n	Lapurdin dago kokatuta udalerria, Uztaritzeko Kantonamenduan, Lapurdi Erdialdea izeneko eskualdean hain justu. Udalerri honen mugakideak dira Ahetze, Arrangoitze, Donibane Lohizune, Azkaine, Sara, Urdazubi, Zugarramurdi, Uztaritze, Zuraide eta Ainhoa. Hiru ibaik gurutzatzen dute udalerria: Urdazubik, Basarunek eta Uhabiak.</p>\r\n<p>\r\n	<br />\r\n	Gainera, bertan dago, herrigunetik ekialdera joanda, bost kilometro ingurura, 12 hekatarea inguru dituen Senpereko aintzira. Oso ezaguna da Ipar Euskal Herriko ikastolen Herri Urrats jaialdia bertan ospatzen delako urtero.</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	Horretaz gain, Senpereko festak ekainaren 29an ospatzen dituzte.</p>\r\n', '', '5 251 ', '43°21&#8242;N 1°32&#8242;W', 'http://www.saint-pee-sur-nivelle.com/eusk/index_eusk.php', 'Ekainaren 29an', 'senpere.jpg', 43.35589218139648437500, -1.54937267303466796875, 15, 'hybrid', 43.35464096069335937500, -1.55044555664062500000, 9);");
	tx.executeSql("INSERT INTO `herriak` VALUES(66, '2012-02-01 18:46:07', 'Kanbo', 4, '<p>\r\n	Lapurdi barnealdean, Ezpeletako Kantomaneduan, dagoen udalerria da, eta mugakide ditu Haltsu, Larresoro, Itsasu, Luhuso, Makea eta Hazparne. Errobi ibaiak udalerria zehartkatzen du alderik alde.</p>\r\n<p>\r\n	<br />\r\n	Kanbon iturburuak daude eta hauek ezagunak egin dira bertan sortu dituzten hamabi bainuetxe eta termengatik.<br />\r\n	Ondasun historiko garrantzitsuenen artean daude Arnaga etxea egun Edmond Rostand museoa dagoen eraikina eta lehen aipatutako neoklasiko estiloko bainuetxeak.</p>\r\n', '1790', '6 177 ', '43°21&#8242;N 1°24&#8242;W', 'http://www.cambo-les-bains.net/', '', 'kanbo.jpg', 43.35904312133789062500, -1.40011310577392578125, 15, 'hybrid', 43.35963439941406250000, -1.40058517456054687500, 8);");
	tx.executeSql("INSERT INTO `herriak` VALUES(67, '2012-02-01 18:48:57', 'Hendaia', 4, '<p>\r\n	Lapurdiko kostaldean dagoen udalerria da, Hendaiako Kantomaneduan hain justu. Txingudiren, Bidasoa ibaia itsasoratzen den lekuaren, eskuinaldean dago kokatuta. Mugakide ditu Biriatu, Urruña, Irun eta Hondarribia.</p>\r\n<p>\r\n	<br />\r\n	Hendaian 3,5 kilometro neurtzen dituen hondartza dago, Euskal Herriko hondartzarik seguruenaren fama duena gainera.</p>\r\n<p>\r\n	<br />\r\n	Udalerriko ondasun nagusienen artean daude 1598. Urtean eraikitako San Bixintxo eliza, XIX. mendean Viollet-le-Duc arkitektoak eraikitako negokotiko estiloko Abbadia jauregia eta estilo arabiarreko Ondarraitz kasinoa.&nbsp;</p>\r\n', '1654', '14.081', '43°21&#8242;34&#8243;N 1°46&#8242;24&#8243;W', 'http://www.hendaye.com/', '', 'hendaia.jpg', 43.36899185180664062500, -1.76476478576660156250, 14, 'hybrid', 43.35886764526367187500, -1.76561021804809570312, 11);");
	tx.executeSql("INSERT INTO `herriak` VALUES(68, '2012-02-01 18:56:58', 'Angelu', 4, '<p>\r\n	Lapurdiko kostaldean, Baiona eta Miarritze artean, dago kokatuta udalerria, Aturri ibaia du albo batean. Tarnose, Bokale, Basusarri, Miarritze, Baiona eta Arrangoitze ditu mugakide.</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	4,5 kilometroko kostaldea du eta hamaika hondartza daude bertan, ondorengoak dira, iparraldetik hegoaldera: Barra hondartza, Zaldunen hondartza, Hareatzeena, Ozeanoarena, Madraga, Madragatxo, Kortsarioena, Marinela, Urrezko hareena, Klubaren eta VVF hondartza.</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	Ondaren historikoaren artean daude Notre-Dame du Refuge etxaldea, 1928an monumento historiko izendatutako Dominikoen antzinako komentua, eta Beatrix Enea izena duen eraikina.</p>\r\n', '', '37.646 ', '43°29&#8242;6&#8243;N 1°31&#8242;6&#8243;W', 'http://www.anglet.fr/', '', 'angelu.jpg', 43.49320602416992187500, -1.52656745910644531250, 13, 'hybrid', 43.48104095458984375000, -1.51399755477905273438, 8);");
	tx.executeSql("INSERT INTO `herriak` VALUES(69, '2012-02-01 19:00:46', 'Baiona', 4, '<p>\r\n	Lapurdiko hiriburua da, kostaldetik gertu dago eta bertan bat egiten dute Aturri eta Errobi ibaiek. 1998an apaingarri moduan erabilitako loreengatik saritu egin zuten; besteak beste, Caradoc parkea, lorategi-publikoa, lorategi botanikoa eta harresien pasealekua daude bertan, eta baita Ansotegiko zabalgunea eta Ilbaritzeko Padura gune naturalak ere.</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	Bi museo daude Baionan, Euskal Museoa eta Bonnat museoa, ondare historiko nabarmena dago bertan; besteak beste, Erromatar gotorlekuaren hondakinak, XV. mendean eraikitako Ch&acirc;teau-Neuf edo Jauregi Berria, eta 1213. Urte inguruan eriakitako Santa Maria katedrala.</p>\r\n', '', '44.498', '43°13&#8242;9&#8243;N 2°44&#8242;3&#8243;W ', 'http://www.bayonne.fr/', 'Uztaileko azken astean.', 'baiona.jpg', 43.49153900146484375000, -1.47070884704589843750, 14, 'hybrid', 43.49222183227539062500, -1.47642517089843750000, 9);");
	tx.executeSql("INSERT INTO `herriak` VALUES(70, '2012-02-01 19:02:15', 'Bokale', 4, '<p>\r\n	Lapurdin, Aturri ibaia itsasoratzen den lekuan, dago kokatuta udalerria, eta Baiona, Landak eta Bizkaiko Golkoa ditu mugakide.</p>\r\n', '', '', '', '', '', 'bokale.jpg', 43.52281951904296875000, -1.48572921752929687500, 15, 'hybrid', 43.52288055419921875000, -1.48595237731933593750, 9);");
	tx.executeSql("INSERT INTO `herriak` VALUES(71, '2012-02-01 19:03:10', 'Ainhoa', 4, '<p>\r\n	Lapurdiko barnealdean, Nafarroako mugan, dago kokatuta udalerria mendebaldeko Pirinio inguruan. Sara, Urdazubi eta Zugarramurdi udalerriekin batera Xareta elkartea osatzen du. Udalerri mugakideak ditu Zuraide, Ezpeleta, Itsasu, Urdazubi, eta Senpere. Urdazuri ibaia pasatzen da bertatik.</p>\r\n<p>\r\n	<br />\r\n	Ainhoa udalerriaren inguruan lapurdiko eta nafarroako arkitektura ikus daiteke, &ldquo;Ortillopitz&rdquo; euskal etxea, besteak beste Euskal bixkotxaren eta Aranaga museoak, eta Sara, Urdazubi eta Zugarramurdiko lezeak.</p>\r\n<p>\r\n	<br />\r\n	Jean Pierre Duvoisin euskal idazlea bertan jaio zen.</p>\r\n', 'XIII. mendea', '658', '43°18&#8242;26&#8243;N 1°29&#8242;51&#8243;W', 'http://www.ainhoa.fr/ainhoaeu/index.html', '', 'ainhoa.jpg', 43.30643081665039062500, -1.49787425994873046875, 16, 'hybrid', 43.30631637573242187500, -1.49814033508300781250, 9);");
	tx.executeSql("INSERT INTO `herriak` VALUES(72, '2012-02-01 19:06:04', 'Ziburu', 4, '<p>\r\n	Lapurdiko kostaldean dago kokatuta eta Ugarana ibaia eta Donibane-Lohizuneko badia ditu inguruan. Urruña, Azkaine eta Donibane-Lohizune dira udalerriaren mugakideak. Kostaldeko udalerri honetan hainbat hondartza txiki daude, besteen artean Zokoa eta Ziburu.</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	Ziburuko gune interesgarrienen artean monumento historiko izendatutako Portuko itsasargia, Bordagaingo Andra Mariaren eliza eta XVII. mendeko iturri bat; baita XVI. mendeko San Bizente eliza eta 1612an eraikitako Errekoletoen komentua ere.</p>\r\n', '1574', '6.719', '43°23&#8242;11&#8243;N 1°40&#8242;0&#8243;W', 'http://www.ciboure.fr/', '', 'ziburu.jpg', 43.38636779785156250000, -1.67462110519409179688, 16, 'hybrid', 43.38741302490234375000, -1.67501592636108398438, 9);");
	tx.executeSql("INSERT INTO `herriak` VALUES(73, '2012-02-02 11:40:49', 'Donapaleu', 6, '<p>\r\n	Nafarroa Behereko Amikuze eskualdeko udalerria da. Mugakide ditu Aiziritze-Gamue-Zohazti, Amendüze-Unaso, Behaskane-Laphizketa, Bithriña, Larribare-Sorhapürü, Ostankoa eta Uhartehiri.</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	Martxoaren amaieran hamabostaldi merkatari-azoka egiten dute, uztailaren amaieran herriko festak ospatzen dituzte eta abuztuan herriko kirolen erakustaldia izaten da protagonista.</p>\r\n<p>\r\n	<br />\r\n	Herriko ondasun nagusien artean dituzte Arnaud Oihenart historialaria XVI. mendean bizi zen Buruen Etxea, Nafarroa Behereko Museoa, Biduzeko zubia, eta 1522an eraikitako San Pauloren eliza eta 1866ko Santa Maria Magdalenarena.</p>\r\n', 'XIII. mendean', '1.875 ', '43°19&#8242;45&#8243;N 1°1&#8242;57&#8243;W', 'http://www.saint-palais-sur-mer.com/', '', 'donapaleu.jpg', 43.32767486572265625000, -1.03207111358642578125, 15, 'hybrid', 43.32692337036132812500, -1.03353881835937500000, 8);");
	tx.executeSql("INSERT INTO `herriak` VALUES(74, '2012-02-02 11:42:26', 'Baigorri', 6, '<p>\r\n	Nafarroa Behereko herria da, Baigorriko kantonamenduko hiriburua. Izpegi erreka jaiotzen da udalerri honetan eta Baigorri zehartkatzen du. Horrez gain, Aldude ibaiak ere zeharkatzen du udalerria. Mugakide ditu Bidarrai, Arrosa, Irulegi, Anhauze, Banka eta Erratzu</p>\r\n<p>\r\n	<br />\r\n	Mendiz inguratuta dagoen herria da. Bere alboan ditu Adartza (1.250 metro), Munhoa (1.021 metro), Aintziaga (905 metro), Autza (1.306 metro), Adi (1.450 metro) eta Izpegi mendatea (672 metro).</p>\r\n<p>\r\n	<br />\r\n	Nafarroaren Eguna ospatzen dute apirilaren amaieran, Nafarroako batasuna aldarrikatzeko, eta abuztuko lehenengo astebruuan herriko jaiak ospatzen dituzte.</p>\r\n<p>\r\n	<br />\r\n	Ondasun nagusien artean XII. mendean sortutako Doneztebe eliza, 1955ean eraikitako Etxautz gaztelua, eta Urdozeko eliza eta Andra Mariko eliza, XVII eta XVIII. mendekoak urrenez urren.</p>\r\n', '', '1.607', '43°10&#8242;02&#8243;N 1°20&#8242;43&#8243;W', '', '', 'baigorri.jpg', 43.17523193359375000000, -1.34586811065673828125, 16, 'hybrid', 43.17466735839843750000, -1.34727144241333007812, 8);");
	tx.executeSql("INSERT INTO `herriak` VALUES(75, '2012-02-02 11:44:02', 'Bidaxune', 6, '<p>\r\n	Nafarroa Beherean dago kokatuta udalerri hau, Agramont eskualdean, Bidaxuneko kantonamenduan, honen burua da gainera. 2008ko erroldaren arabera, 1250 biztanle bizi ziren bertan.</p>\r\n<p>\r\n	<br />\r\n	Biduze ibaiak eta honen ibaiadar diren Lihurri eta Lagrabek zeharkatzen dute. Ibai hori oso garrantzitsua izan zen.<br />\r\n	Herriko ondare historikoan aipagarria da 1329an eraikitako Agramondarren gaztelua. Euskal Herriko gaztelu ikusgarrienetakoa izan zen eta gaur egun berritze lanetan dago.</p>\r\n', '', '1.250 ', '43°29&#8242;N 1°8&#8242;W ', 'http://www.bidache.net/', '', 'bidaxune1.jpg', 43.48284912109375000000, -1.14107608795166015625, 16, 'hybrid', 43.48273849487304687500, -1.14123487472534179688, 8);");
	tx.executeSql("INSERT INTO `herriak` VALUES(76, '2012-02-02 11:46:11', 'Donazaharre', 6, '<p>\r\n	Nafarroa Beherean dago kokatuta udalerria da, Garazi eskualddean dago hain justu. Ondasun nagusien artean daude XIII. Mendeko Donibane eliza eta Harrieta gaztelua, eta XII. mendeko Orreagako monstareioa. Ikusteko lekuen artean daude Arkeologia-aztarnategi erromatarra, Haritzaldea, Maiotenea eta Arsoritzea baerriak, La Madeleine gurutzea eta San Piarresen eliza.</p>\r\n<p>\r\n	<br />\r\n	Udalerriko jaiak abuztuako lehenengo igandean ospatzen dira.</p>\r\n', '', '855', '43°9&#8242;57&#8243;N 1°11&#8242;32&#8243;W', '', 'Abuztuko lehenengo igandean', 'donazaharre.jpg', 43.16512298583984375000, -1.19416236877441406250, 16, 'hybrid', 43.16481018066406250000, -1.19390487670898437500, 7);");
	tx.executeSql("INSERT INTO `herriak` VALUES(77, '2012-02-02 11:57:48', 'Donibane Garazi', 6, '<p>\r\n	Nafarroa Beherean, Donejakue Bidean, kokatuta dagoen hiribua da. Udalerriak mugakide ditu Izpura, Donazaharre, Zaro, Eiheralarre, eta Uharte Garazi. Udalerritik pasatzen da Errobi ibaia, udalerriaren erditik. Herriko ondasun nagusien artean daude XV. mendean sortutako Alde Zaharra, Andre Mariaren atea eta zubia, XIII. Mendeko Mansart etxea, eta Espainiako karrika.</p>\r\n<p>\r\n	<br />\r\n	Herrian Joan Maiorgakoa martiriaren omenezko eguna ospatzen dute uztailaren 15ean.</p>\r\n', '', '1.521', '43°09&#8242;47&#8243;N 1°14&#8242;11&#8243;W ', '', 'Uztailaren 15ean', 'donibane-garazi.jpg', 43.16167831420898437500, -1.23810768127441406250, 15, 'hybrid', 43.16236877441406250000, -1.23793601989746093750, 7);");
	tx.executeSql("INSERT INTO `herriak` VALUES(78, '2012-02-02 12:13:45', 'Maule-Lextarre', 7, '<p>\r\n	Maule, gaztelu-gotorleku bat zuen muino baten magalean sortu zen. Herriko ondasun garrantzitsunen artean daude XIV. Mendean eraikitako Andra Maria eliza, 1765eko merkatua, Mauleko gaztelua eta XIII. Mendean eraikitako Saint Jean de Berraute eliza.</p>\r\n<p>\r\n	<br />\r\n	Mauleko jaiak uztailaren 14an ospatzen dira. Lau egunez luzatzen da jaieguna, eta hainbat ekintza egiten dituzte bertan: pilota, musika, dantza, fanfarreak eta zezensuzkoak besteak beste.</p>\r\n', '1841', '3.228', '43°13&#8242;12&#8243;N 0°53&#8242;23&#8243;W', 'http://www.mauleon-licharre.fr/index.php/eu', 'Uztailaren 14an', 'maulelextarre2.jpg', 43.22434997558593750000, -0.88272571563720703125, 15, 'hybrid', 43.22010803222656250000, -0.89037322998046875000, 8);");
	tx.executeSql("INSERT INTO `herriak` VALUES(79, '2012-02-02 12:18:55', 'Tutera', 5, '<p>\r\n	Nafarroako hegoaldean, Tuterako eskualdean, dago kokatuta udalerria. Mugakideak ditu Castejon, Arguedas, Cintruenigo, Fustiñana, Ribaforada, Ablitas, Muschante eta Cascante. Ipar-ekialdean Ebro ibaia dago eta mendebaldetik ekialdera udalerria zeharkatzen dute Queiles eta Mediavilla ibaiek.&nbsp;</p>\r\n<p>\r\n	<br />\r\n	41.845 hektareako eremua duen Bardeetako Natura Parkea bertan dago, herrigunearen ekialdean.</p>\r\n<p>\r\n	<br />\r\n	Tuterako jaiak Santa Ana egunaren inguruan ospatzen dira, uztailaren 24an hasiz, eta Aste Santua, eta San Joan jaiak ere ospatzen dituzte.</p>\r\n', '', '35.429', '42°3&#8242;50&#8243;N 1°36&#8242;20&#8243;W', 'http://www.tudela.es', 'Santa Ana. Abuztuaren 24an', 'tutera.jpg', 42.06031799316406250000, -1.60336017608642578125, 15, 'hybrid', 42.06025314331054687500, -1.60503387451171875000, 9);");
	tx.executeSql("INSERT INTO `herriak` VALUES(80, '2012-02-02 12:31:16', 'Barkoxe', 7, '<p>\r\n	Zuberoan, Basabürün, dago kokatuta udalerria eta bertan dago Haranbeltzeko basoaren zati bat. Herriaren mugakideak dira Sohüta, Ospitalepea, Jeruntze, Ustasu, Josibaia, Sangoine, Eskiula, Zalgize-Doneztebe, Arrokaiaga, Aramitze, Iruri, Landa, Montori eta Atharratze-Sorhplüze.</p>\r\n<p>\r\n	Euskal Herriko azken herria da, Biarnorekin mugan.</p>\r\n', '', '741', '43°11&#8242;N 0°46&#8242;W', '', '', 'barkoxe.jpg', 43.18895339965820312500, -0.77050209045410156250, 16, 'hybrid', 43.18867111206054687500, -0.76922321319580078125, 8);");
	tx.executeSql("INSERT INTO `herriak` VALUES(81, '2012-02-02 12:32:39', 'Bildoze-Onizepea', 7, '<p>\r\n	Zuberoako iparraldean dago kokatuta, Pettarrakan, eta lurraldeko laugarren udalerri jendetsuena da. Uhaintzadi ibaiak zehazten du ekialdeko muga. Udalerriaren mugakideak dira Ezpeize-ündüreine, &Uuml;rrüstoi-Larrabile, Ainharbe, Maule-Lextarre, Sohüta eta Berrogaine-Larüntze.</p>\r\n<p>\r\n	<br />\r\n	Herriko jaiak abuztuko lehenego asteburuan ospatzen dituzte.</p>\r\n', '1842', '739 ', '43°14&#8242;30&#8243;N 0°52&#8242;48&#8243;W', '', '', 'bildoze-onizepea.jpg', 43.24137496948242187500, -0.87978601455688476562, 16, 'hybrid', 43.24110412597656250000, -0.88082456588745117188, 8);");
	tx.executeSql("INSERT INTO `herriak` VALUES(82, '2012-02-02 12:48:36', 'Atharratze-Sorholüze', 7, '<p>\r\n	Zuberoako udalerria da, Basabürüko hiriburua hain zuzen ere. Udalerri honen mugakideak dira Barkoxe, Aloze, Iruri, Montori eta Liginaga-Astüe. Benito Lertxundik ezagunago egin zuen udalerri hau \"Atharratzeko Jauregia\" abestiarekin.</p>\r\n<p>\r\n	<br />\r\n	Udalerria zeharkatzen dute Oloroeko ibaiaren ibaiadar diren Joos, Laritolle eta Uhaintzadi erreka eta beren ibaiadarrek, eta Jaga errekak.</p>\r\n<p>\r\n	<br />\r\n	Heriko ondasun nagusien artean daude XIX. mende amaieran eraikitako Atharratzeko elitza eta Maidalena mendian dagoen Maidalena ermita.</p>\r\n', '1859. Urtean batu ziren bi herriak', '616', '43°7&#8242;N 0°51&#8242;W', '', '', 'atharratze-sorholuze.jpg', 43.11668014526367187500, -0.86311340332031250000, 16, 'hybrid', 43.11678695678710937500, -0.86374425888061523438, 7);");
	tx.executeSql("INSERT INTO `herriak` VALUES(83, '2012-02-02 12:50:55', 'Sohüta', 7, '<p>\r\n	Zuberoako ipar-ekialdean, Pettarra eskualdean, dago kokatuta udalerria.</p>\r\n<p>\r\n	Haranbeltzeko basoaren zati bat bertan dago eta monumentu historiko izendatutako hainbat gotorleku aurkitu dituzte bertan.</p>\r\n', '', '1.161 ', '43°13&#8242;N 0°52&#8242;W', '', '', 'sohuta1.jpg', 43.23107147216796875000, -0.86976528167724609375, 16, 'hybrid', 43.23067855834960937500, -0.86827182769775390625, 8);");
	tx.executeSql("INSERT INTO `herriak` VALUES(84, '2012-02-02 13:02:16', 'Altsasu', 5, '<p>\r\n	Arakilgo korridorearen ekialdean dago kokatuta herria. Hegoaldean Urbasa mendia dauka eta herritik gertu daude, Aralar eta Andia mendilerroak&nbsp; eta Beriain mendia. Herriaren azalera gehienean basoak daude eta baita larrem, baratz eta arboladiak ere.</p>\r\n<p>\r\n	<br />\r\n	Monumentu garrantzitsuenen artean daude Altsasuko Intxaurrondo monumentua, Basaluzeko dorrea eta Dorre txikia. Eliza nagusia Jasokundearen eliza da, errenazimendu garaikoa da eta hiru nabe ditu pilareen gainean eta burualde poligonala.</p>\r\n<p>\r\n	<br />\r\n	Neolitoko eta Eneolitoko aztarnak eta trikuharriak aurkitu dira udalerrian. Lehen aipamen idatziak XII. mendekoak dira.</p>\r\n', '', '7.655', '42°53&#8242;43&#8243;N 2°10&#8242;8&#8243;W', 'http://www.alsasua.net/eu/', 'Iraileko gurutzea. Irailaren 13tik 17ra', 'altsasu.jpg', 42.89445495605468750000, -2.16964960098266601562, 16, 'hybrid', 42.89005279541015625000, -2.16464996337890625000, 8);");
	tx.executeSql("INSERT INTO `herriak` VALUES(85, '2012-02-02 13:04:30', 'Irurtzun', 5, '<p>\r\n	Nafarroako udalerri hau Sakana bailaran dago, eta Arakil eta Larraun ibaiak lotzen diren tokian dago kokatuta. Ondare nagusiari dagoionean Erga mendiaren tontorrean dagoen baseliza eta XIX. mendearen azken hamarkadan eratutako San Martin eliza daude herrian. Irurtzungo jaiak uztaileko azken aurreko asteburuan ospatzen dira sei egunez. Gainera, azaroaren 11n herriko patroiaren, San Martinen, jaiak ospatzen dira.</p>\r\n', '1312', '2.485', '42°54&#8242;40&#8243;N 2°4&#8242;12&#8243;W', 'http://www.etxarriaranatz.com', '', 'irurtzun.jpg', 42.91898727416992187500, -1.82688474655151367188, 16, 'hybrid', 42.91938018798828125000, -1.82723665237426757812, 9);");
	tx.executeSql("INSERT INTO `herriak` VALUES(86, '2012-02-02 13:06:08', 'Berriozar', 5, '<p>\r\n	Nafarroan, Iruñerriko eskuadearen iparraldean, dago kokatuta udalerria da, Iruñatik lau kilometro ingurura. Mugakide ditu Berriobeiti, Ezkabarte, Antsoine eta Iruñea. Ondare nagusiari nagokionez, ondorengoak hartu daitezke kontutan besteen artean: alde zaharreko San Esteban eliza, Ezpeleta Jauregia, Garbilekua eta udalerriaren alde zaharrean dauden barroko estiloko zenbait eraikin.</p>\r\n', '', '8.931', '42°50&#8242;10&#8243;N 1°40&#8242;17&#8243;W', ': http://www.berriozar.es/entrada/sel_id.htm', 'San Isidro, maiatzaren 15ean.\r\nSan Esteban, abuztuaren 26an.\r\n', 'berriozar.jpg', 42.83481597900390625000, -1.67266845703125000000, 15, 'hybrid', 42.83629226684570312500, -1.67445802688598632812, 8);");
	tx.executeSql("INSERT INTO `herriak` VALUES(87, '2012-02-02 13:07:46', 'Barañain', 5, '<p>\r\n	Nafarroako erdialdean, Iruñetik hiru kilometro ingurura eta itsasoaren mailatik 400 metro ingurura, dagoen udalerria da. Elortz eta Arga ibaien artean eratutako lautadan dago kokatuta. Jatorriko herritik kontserbatzen den eraikin bakarra herriko gune zaharrean XVI. mendeko San Esteban eliza&nbsp; alde zaharrean dago, eta honen barnean mende berean Miguel Altunak egindako margoak daude. Herrian ekaineko azken astean ospatzen dira jaiak, San Pablo patroiaren omenez.</p>\r\n', '', '21.552', '42°48&#8242;17&#8243;N 1°41&#8242;9&#8243;W', 'http://www.baranain.es/eu/index.aspx', 'San Pablo. Ekaineko azken astean.', 'baranain.jpg', 42.80799484252929687500, -1.68054342269897460938, 14, 'hybrid', 42.80465698242187500000, -1.68512248992919921875, 8);");
	tx.executeSql("INSERT INTO `herriak` VALUES(88, '2012-02-02 13:10:37', 'Burlata', 5, '<p>\r\n	Nafarroako erdialdean, Iruñerri eskualdean, dago kokatuta udalerria.&nbsp; Udalerriak mugakide ditu Atarrabia, Ezkabartere, Iruñea, Ugarte eta Eguesibar, eta Iruñeatik hiru kilometro ingurura dago. Itsasoaren mailatik 420 metrotara dago kokatuta.</p>\r\n<p>\r\n	<br />\r\n	Herriko jaiei dagokienez, abuzturen 14tik abuztuaren 21era ospatzen dira.</p>\r\n', '', '18.195', '42°49&#8242;N 1°36&#8242;W', 'http://www.burlada.es/entrada/sel_ID.htm', 'Abuztuaren 14tik 21era.', 'burlata.jpg', 42.82502365112304687500, -1.61396026611328125000, 15, 'hybrid', 42.82585906982421875000, -1.61532068252563476562, 9);");
	tx.executeSql("INSERT INTO `herriak` VALUES(89, '2012-02-02 13:13:47', 'Zangoza', 5, '<p>\r\n	Nafarroako ekialdean dago kokatuta udalerria. Herriaren mugakideak dira Ledea, Xabier, Kaseda, Sos del Rey Cat&oacute;lico, eta Oibar. Herriko ondasun nagusien artean daude&nbsp; XI. mendeko aztarna erromantikoak dituen Santa Maria la Real eliza, jatorri erromanikoa duen Santiago Nagusiaren eliza, XIV. mendean eraikitako Karmengo Amaren komentua, XI. mendean eraikitako Santa Maria zubia eta XV. mendeko Gendulainko konteen jauregia esaterako.</p>\r\n<p>\r\n	<br />\r\n	Herriko jaiei dagokienez, irailaren 11ren inguruan ospatzen dira.</p>\r\n', '1122', '5.254', '42°34&#8242;40&#8243;N 1°16&#8242;57&#8243;W', 'http://www.sanguesa.es', 'Irailaren 11ren inguruan', 'sanguesa.jpg', 42.57433700561523437500, -1.28216028213500976562, 16, 'hybrid', 42.57498168945312500000, -1.28154659271240234375, 8);");
	tx.executeSql("INSERT INTO `herriak` VALUES(90, '2012-02-02 13:16:34', 'Bera', 5, '<p>\r\n	Nafarroako iparraldean dagoen kokatuta,&nbsp; Bidasoa ibaiaren alboan dago, eta Lapurdi eta Gipuzkoarekin egiten du muga.</p>\r\n<p>\r\n	<br />\r\n	Herriko ondasun nagusien artean daude Altzateko plaza, Karnaxenea, San Esteban eliza eta San Migel zubia.</p>\r\n<p>\r\n	<br />\r\n	Herrian hainbat jai ospatzen dira: Inauteriak; Bestaberri eguna, egun horretan prozesioa egiten dute herrian zehar; eta Lurraren eguna ere ospatzen dute, besteak beste artisauak, gazta eta sagardo lehiaketak, bertako produktuen erakusketak eta dantzak egoten dira egun horretan.</p>\r\n', '', '3.762', '43°16&#8242;54&#8243;N 1°41&#8242;5&#8243;W', 'http://www.bera.es/eu/', 'San Esteban. Abuztuaren 2tik 6ra', 'bera2.jpg', 43.28039550781250000000, -1.68275356292724609375, 15, 'hybrid', 43.28070449829101562500, -1.68271064758300781250, 8);");
	tx.executeSql("INSERT INTO `herriak` VALUES(91, '2012-02-02 13:18:33', 'Leitza', 5, '<p>\r\n	Nafarroako iparraldean, Leitzaldea eskualdean, dagoen herria da. Gipuzkoako mugatik oso gertu dago eta Iruñeatik 50 kilometro ingurua. Mendiz inguratuta dago, eta horietan garaiera altuena duetenak Guratz (955 metro) eta Korneta (1.054 metro). Mugakide ditu Goizueta, Larraun, Ezkurra, Basaburua, Areso eta Berastegi.</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	Herriko festak abuztuaren 10etik 15era ospatzen dituzte; San Tiburtzioren omenez, abuztuaren 11.&nbsp; Horrez gain, inauteriak ere ospatzen dituzte, urtarrileko laugarren ingandean, larunbatetik asteartera bitartean.</p>\r\n', '', '2.953', '43°4&#8242;45&#8243;N 1°54&#8242;52&#8243;W', 'http://www.leitza.net/eu/index.aspx', 'Abuztuaren 10etik 15era', 'leitza.jpg', 43.07879257202148437500, -1.91462516784667968750, 16, 'hybrid', 43.07835388183593750000, -1.91497707366943359375, 8);");
	tx.executeSql("INSERT INTO `herriak` VALUES(92, '2012-02-02 13:23:53', 'Lesaka', 5, '<p>\r\n	Nafarroako ipar-mendebaldean dago kokatuta herria. Irun, Goizueta, Arantza, Igantzi, Bera, Etxalar eta Oiartzun ditu mugakide. Mendiz inguratuta dago herria, eta Aritxulegi mendatetik gertu San Anton urtegia dago, Lesakatik Oiartzunera bidean.</p>\r\n<p>\r\n	<br />\r\n	Lesakatik Oiartzunera bidean ere Aginako trikuharria, harrespilak eta tumulua aurki daitezke, eta bertan ikus daiteke Jorge Oteizak Aita Donostiari eginiko oroitarria.</p>\r\n<p>\r\n	<br />\r\n	Herriko festan San Ferminetan, uztailaren 7an ospatzen dira. Horrez gain, azaroko bigarren amabostaldian udazkeneko azoka ospatzen da. Olentzeroren inguruan, gabonetan, egiten diren festak kontutan izanda zenbait ikerlarik pertsonaia mitikoaren sorrera bertan kokatu dute.</p>\r\n', '', '2.803', '43°14&#8242;57&#8243;N 1°42&#8242;14&#8243;W', 'http://www.lesaka.net/eu/', 'San Fermin. Uztailaren lehenengo astean', 'lesaka.jpg', 43.24660873413085937500, -1.70298814773559570312, 16, 'hybrid', 43.24655914306640625000, -1.70306110382080078125, 9);");
	tx.executeSql("INSERT INTO `herriak` VALUES(93, '2012-02-02 13:26:35', 'Igantzi', 5, '<p>\r\n	Nafarroako ipar-mendebaldeanm Iruñeko merindadean, dagoen udalerri honek 2011ko erroldaren arabera 635 biztanle ditu.&nbsp; Igantzik Lesaka, Etxalar eta Arantza ditu mugakide.</p>\r\n<p>\r\n	<br />\r\n	Interes turistikoa duen lekua da San Juan Xar parke naturala, leize batean kapera bat dago bertan. Mezak ematen dituzte batzuetan. Berezitasun bat du gainera, Iberiar penintsulako beste gune batean ere hazten ez den zuhaitza hazten delako bertan: xarma (Carpinus betulus).</p>\r\n<p>\r\n	<br />\r\n	Herriaren ondare nagusi dira XVIII: mendean eraikitako estilo goitikoko San Migel Arkanjeluaren eliza eta XVI. mendeko Errukietako Ama Birjinaren baseliza.</p>\r\n<p>\r\n	<br />\r\n	Herriko jaiak San Migeletan, irailaren 29an ospatzen dira.</p>\r\n', '', '635', '42°13&#8242;32&#8243;N 1°41&#8242;57&#8243;W', 'http://www.igantzi.es/eu/index.aspx', '', 'igantzi.jpg', 43.22517776489257812500, -1.69985532760620117188, 16, 'hybrid', 43.22518920898437500000, -1.70052909851074218750, 9);");
	tx.executeSql("INSERT INTO `herriak` VALUES(94, '2012-02-02 13:30:04', 'Elizondo', 5, '<p>\r\n	Nafarroako udalerria da, Baztango herri nagusia hain justu. Udalerriko ondasun nagusien artean daude XV. mende amaierako Puriosenea etxea, egun Jorge Oteizaren Baztango Museo Etnografikoaren gunea dena; XVII. mendean eraikitako barroko estiloko Arotzaren jauregia; eta estilo bereko Baztango Balleko etxea.</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	Jorge Oteizaren Baztango Museo Etnografikoan herrigunean bertan dago kokatuta, eta XIX. mendeko ohiturak erakustea du helburu.</p>\r\n<p>\r\n	<br />\r\n	Herriko jaiak uztailaren 25ean ospatzen dira, eta horrez gain, udaberriko feriak ospatzen dituzte Aste Santuen ondorengo ostiralean, eta udazkeneko feriak San Lukas (urriak 18) ondorengo ostiralean.</p>\r\n', '', '3.315', '43°08&#8242;42&#8243;N 1°31&#8242;12&#8243;W', 'http://www.elizondo-baztan.com/', 'Santiago. Uztailaren 25ean', 'elizondo.jpg', 43.14489746093750000000, -1.51821613311767578125, 15, 'hybrid', 43.14383316040039062500, -1.51782989501953125000, 8);");
	tx.executeSql("INSERT INTO `herriak` VALUES(95, '2012-02-02 13:32:21', 'Ultzama', 5, '<p>\r\n	Nafarroako iparraldean, Iruñeko eskualdean, dago kokatuta udalerria. Esnekiak ekoiztegatik da ezaguna, eta baita bertan dagoen golf zelaiagatik ere. Ultzamako udalerria osatzen dute ondorengo herriek: Alkotz, Arraitz-Orkin, Auza, Eltso, Eltzaburu, Gorrontz-Olano, Gerendiain, Ilarregi, Iraizotz, Larraintzar, Lizaso, Suarbe, Urritzola-Galain eta Zenotz.</p>\r\n', '', '1.706', '43°0&#8242;0&#8243;N 1°40&#8242;41&#8243;W', 'http://www.ultzama.es/eu/index.aspx', '', 'ultzama.jpg', 42.99968719482421875000, -1.67350530624389648438, 16, 'hybrid', 42.99862289428710937500, -1.67429924011230468750, 8);");
	tx.executeSql("INSERT INTO `herriak` VALUES(96, '2012-02-02 13:41:44', 'Baztan', 5, '<p>\r\n	Nafarroa Garaian, ipar-mendebaldean hain justu, dago kokatuta udalerria. Hamabost herrik osatzen duten arren honen herri nagusia Elizondo da.</p>\r\n<p>\r\n	<br />\r\n	Ondorengo udalerriak ditu mugakide: Donamaria, Bertizarana, Etxalar, Erroibar, Esteribar, Lantz, Anue, Ultzama,, Urdazubi, Zugarramurdi eta Lapurdi.</p>\r\n<p>\r\n	<br />\r\n	Bertan dago Jorge Oteizaren Baztango Museo Etnografikoa, hain justu Baztango herri nagusian, Elizondon, dago kokatuta.</p>\r\n', '', '8.075', '43°12&#8242;N 1°28&#8242;W', 'http://www.baztan.es/eu/', '', 'baztan.jpg', 43.18991088867187500000, -1.46959304809570312500, 13, 'hybrid', 43.19727706909179687500, -1.46519851684570312500, 8);");
	tx.executeSql("INSERT INTO `herriak` VALUES(97, '2012-02-02 13:44:52', 'Iruñea', 5, '<p>\r\n	Nafarroako hiriburua da eta Euskal Herriko hirugarren hiri handiena. Itsasoaren mailatik 449 metro ingurura dago eta Arga ibaiak ingurutik pasatzen da. Ingurune menditsuan dago ondorengo mendiak baititu alboan: Izaga (1.353 metro), Ezkaba (892 metro), Campamento (528 metro), Badostaingo kaskoak (590 m), Taxoare (668 m), Gazolatz (534 metro), Errakobide (466 metro), Santa Lutzia (470 metro) eta Cascajo (450 metro) mendiek. Udalerrian bi museo daude Iruñeko planetarioa eta Nafarroako museoa.</p>\r\n<p>\r\n	<br />\r\n	Udalerriko jai garrantzitsu edo ezagunenak San Ferminak dira, hauek uztailaren 6tik 14ra bitartean ospatzen dira. Horietaz gainera, auzoetan ere ospatzen dituzte festak.</p>\r\n', 'K.a. 74. urtea', '197.932 ', '42°49&#8242;00&#8243;N 1°38&#8242;34&#8243;W', 'http://www.pamplona.net/verPagina.asp?idPag=1&idioma=2', 'San Ferminak. Uztailaren 6tik 14ra', 'irunea.jpg', 42.81479644775390625000, -1.64434432983398437500, 13, 'hybrid', 42.81795883178710937500, -1.64430999755859375000, 7);");
	tx.executeSql("INSERT INTO `herriak` VALUES(100, '2012-02-03 16:25:21', 'Zizur Nagusia', 5, '<p>\r\n	Nafarroan, Iruñerriko eskualdean, Iruñetik bost kilometro ingurura dago kokatuta udalerria. Honen mugakide dira Oltza, Iruñea, Barañain, ZIzun Zendea eta Galar Zendea.</p>\r\n<p>\r\n	<br />\r\n	Udalerrian ez dago ondare historiko gehiegi XIV. mendean eraikitako San Andres eliza gotikoa da aspalditik dagoen eraikin bakarra.</p>\r\n<p>\r\n	<br />\r\n	Jaiei dagokienez, irailaren 14an hasita, bost egunez, ospatzen dituzte jai patronalak, azaroaren 30etik gertuen dagoen asteburuan Jai txikiak izeneko festak ospatzen dituzte San Andresen omenez, eta horietaz gainera urtean zehar beste zenbait jai ospatzen dira bertan.</p>\r\n', '', '13.871', ' 42°47&#8242;12&#8243;N 1°41&#8242;27&#8243;W', ': http://www.zizurmayor.es/eu/index.aspx', 'Gurutze Santua. Irailaren 14an', 'zizur-nagusia.jpg', 42.78488159179687500000, -1.68828964233398437500, 15, 'hybrid', 42.78683090209960937500, -1.69085168838500976562, 8);");
	tx.executeSql("INSERT INTO `herriak` VALUES(101, '2012-02-03 16:27:02', 'Doneztebe', 5, '<p>\r\n	Nafarroako ipar-mendebaldean, Malerreka eskualdean, dago kokatuta udalerria. Bidasoa ibaia eta honen ibaiadarra pasatzen dira bertatik. Udalerriak mugakide ditu Sunbilla, Oitz, Donamaria, Bertizarana eta Elgorriaga.</p>\r\n<p>\r\n	<br />\r\n	Mendiz inguratua dagoen udalerria da, beste zenbaiten artean ondorengo gailurrak ditu inguruan Aranzoko, Askin, Kaxerna, Bertizko Jaurerria, Ekaitza, Otetxon, eta Mendaur.</p>\r\n<p>\r\n	<br />\r\n	Ekainaren 20an, San Pedroren omenez ospatzen dira jai nagusiak udalerri honetan. Horrez gain, maiatzean udaberri azoka ospatzen da eta San Marin ondorengo astean udazkenekoa.</p>\r\n', '', '1.679 ', '43°7&#8242;54&#8243;N 1°40&#8242;11&#8243;W', 'http://www.doneztebe.es/eu/index.aspx', '', 'doneztebe.jpg', 43.13281250000000000000, -1.67202472686767578125, 15, 'hybrid', 43.13243484497070312500, -1.66885030269622802734, 9);");
	tx.executeSql("INSERT INTO `herriak` VALUES(102, '2012-02-03 16:28:45', 'Lodosa', 5, '<p>\r\n	Nafarroako hego-mendebaldean dago kokatuta udalerria. Sesma, Errioxako Autonomia Erkidegoa, Karkar eta Sartaguda ditu mugakide.</p>\r\n<p>\r\n	<br />\r\n	Herriko pikillo piperrak oso ezagunak dira eta jatorrizko izendapena dute gainera. Udalerriko eraikin nagusia 1533an eraikitako San Migel eliza da.</p>\r\n<p>\r\n	<br />\r\n	Jai ospetsuenen artean daude iraileko hirugarren igandean ospatzen den Angustiaseko Ama Birjinaren omenezko jaiak; Lodosako&nbsp; jai patronalak, Ementerio eta Zeledonioren omenez uztailaren 30etik abuztuaren 4ra ospatzen direnak; eta otsailaren 3an San Blas jaiak.</p>\r\n', '', '4.993 ', '42°25&#8242;17&#8243;N 2°4&#8242;43&#8243;W', 'http://www.lodosa.es', 'Angustiasko Ama Birjina. Iraileko hirugarren astean', 'lodosa.jpg', 42.42295074462890625000, -2.07851886749267578125, 15, 'hybrid', 42.42380142211914062500, -2.07771205902099609375, 8);");
	tx.executeSql("INSERT INTO `herriak` VALUES(103, '2012-02-03 16:30:11', 'Lekunberri', 5, '<p>\r\n	Nafarroako ipar-mendebaldean, Larraungo eskualdean, Aralarreko mendilerroaren azpian, kokatuta dagoen udalerria da. 1900. Urte inguruan Larraun eta Lekunberri banandu egin ziren eta udalerri independientea eratu zuten.</p>\r\n<p>\r\n	<br />\r\n	Aralar mendilerroan eta honen inguruan aztarna megalitiko asko daude eta bertan dago erromaniko estiloko lehenengo satutegia.</p>\r\n', '', '1.473 ', '43°0&#8242;11&#8243;N 1°53&#8242;37&#8243;W', 'http://www.lekunberri.net/index.php?id=eu', '', 'lekunberri.jpg', 43.00367355346679687500, -1.89617156982421875000, 15, 'hybrid', 43.00105285644531250000, -1.89107322692871093750, 8);");
	tx.executeSql("INSERT INTO `herriak` VALUES(104, '2012-02-03 16:32:05', 'Zugarramurdi', 5, '<p>\r\n	Nafarroako ipar-mendebaldean dago kokatuta udalerria, tsasoaren mailatik 136 metro ingurura.</p>\r\n<p>\r\n	<br />\r\n	Udalerria ezaguna da bertan Zugarramurdiko leizeak edo Sorginen Leizeak daudelako. Sarrera nagusiak 100 metro luze, 20 metro zabal eta 30 metro ditu garaieran. Ondasun historiko horretaz gain 1781. urtean eraikitako Jasokunde eliza eta Errosarioko Andra Mariaren baseliza ere badaude udalerrian.</p>\r\n<p>\r\n	<br />\r\n	Udalerriko festak Andre Mariaren omenez ospatzen dira abuztuaren 15ean.</p>\r\n', '', '211 ', '43°16&#8242;9&#8243;N 1°32&#8242;28&#8243;W', 'http://www.zugarramurdi.es/?lang=eu', 'Andre Maria. Abuztuaren 15ean', 'zugarramurdi.jpg', 43.26895523071289062500, -1.54012441635131835938, 16, 'hybrid', 43.26919174194335937500, -1.54078960418701171875, 9);");
	tx.executeSql("INSERT INTO `herriak` VALUES(105, '2012-02-03 16:33:32', 'Tafalla', 5, '<p>\r\n	Nafarroako erdialdean, Erriberako eskualdean, dago kokatuta udalerria. Udalerrian ondasun erlijioso eta zibil asko daude. Besteen artean XIII. Mendean eraikitako estilo erromanikoko Santa Maria eliza, 1157. urteko San Pedro eliza, XI. mendeko San Nikolas baseliza, Mencoen jauregia, Faltzesko markesen jauregia eta XVIII. mendean eraikitako Feriako markesarena.</p>\r\n', '', '11.390 ', '42°31&#8242;44&#8243;N 1°40&#8242;25&#8243;W', 'http://www.tafalla.es', 'Ama Birjinaren omenez. Abuztuaren 14tik abuztuaren 20ra', 'tafalla.jpg', 42.52907943725585937500, -1.67408466339111328125, 15, 'hybrid', 42.53070831298828125000, -1.67310619354248046875, 7);");
	tx.executeSql("INSERT INTO `herriak` VALUES(106, '2012-02-03 16:37:55', 'Atarrabia', 5, '<p>\r\n	Nafarroan, Iruñerriko Mankomunitatean, Iruñetik lau kilometro ingurura dago kokatuta udalerria. Mugakide ditu Ezkabarte, Uharte, Burlata eta Iruñea.</p>\r\n<p>\r\n	<br />\r\n	Herriko ondare nagusiaren artean daude XVI. mendean eraikitako San Andres eliza, eta Atarrabiako kale nagusian dagoen Antso VI.aren monumentua. Kale Nagusian eta Serapio Uitzi kalean gainera XX. mendeko eraikinak ikus daitezke.</p>\r\n<p>\r\n	<br />\r\n	Udalerriko jaiak Nafarroaren eguna (abenduaren 3a), Atarrabiaren eguna (iraileko lehenengo igandea), herriko jaiak (urriko lehenengo igandea), eta San Andres eguna (azaroaren 30a)</p>\r\n', '', '10.487 ', '42°49&#8242;51&#8243;N 1°36&#8242;31&#8243;W', 'http://www.villava.es/sel_id.htm', 'Atarrabiaren eguna, iraileko lehen igandean ', 'atarrabia.jpg', 42.83037567138671875000, -1.60906791687011718750, 16, 'hybrid', 42.83057785034179687500, -1.60869026184082031250, 9);");
	tx.executeSql("INSERT INTO `herriak` VALUES(107, '2012-02-03 16:39:45', 'Lizarra', 5, '<p>\r\n	Nafarroan dago kokatuta udalerria, Juramendi mendiaren magalean, Ega ibaiak zeharkatzen du alderik alde. Honen mugakideak dira Allin, Deierri Aiegi, Aberin eta Bilatorta.</p>\r\n<p>\r\n	<br />\r\n	Lizarran ondare historikoaren parte dira Puyko Andre Mariaren basilika, XVI. mendera arte gotorleku garrantzitsua izan zen Lizarrako Gaztelua, eta antzinako parrokia baten oinarriaren gainean Erdi Aroan eraikitako San Pedro de la Rua parrokia, besteak beste.</p>\r\n<p>\r\n	<br />\r\n	Lizarrako jaiak abuztuko lehenengo astean, hilaren 5aren inguruan, ospatzen dira.</p>\r\n', '', '14.251', '42°40&#8242;19&#8243;N 02°01&#8242;52&#8243;W', 'http://www.estella-lizarra.com/eu/index.aspx', 'San Andres. Uztaileko azken astean edo abuztuko lehen astean', 'lizarra.jpg', 42.67118835449218750000, -2.03255653381347656250, 16, 'hybrid', 42.67004776000976562500, -2.02992582321166992188, 8);");
	tx.executeSql("INSERT INTO `herriak` VALUES(108, '2012-02-03 16:41:09', 'Etxarri-Aranatz', 5, '<p>\r\n	Etxarri Aranatz Nafarroako udalerria da, hedaduraz Sakana eskualdeko bigarrena, Altsasuren ondoren. 2.460 biztanle zituen 2009ko urtarrilaren 1eko erroldan.</p>\r\n<p>\r\n	Udalerria bi herrik osatzen dute: Etxarri Aranatzek eta Lizarragabengoak; azken hori 1842ko erroldan udalerri independente gisa agertzen da.</p>\r\n', '', '67', '42°46&#8242;50&#8243;N 1°49&#8242;31&#8243;W', 'http://www.etxarriaranatz.com/', 'Abuztuko lehen asteburuan. ', 'etxarri.jpg', 42.90780639648437500000, -2.06453919410705566406, 17, 'hybrid', 42.90751266479492187500, -2.06475162506103515625, 8);");
	tx.executeSql("INSERT INTO `herriak` VALUES(109, '2012-02-03 16:42:48', 'Corella', 5, '<p>\r\n	Nafarroan, Errioxako mugan, dagoen udalerria da. Alhama ibaia dauka albo batean, eta Kastejon, Tutera, Fitero eta Zentroniko ditu mugakide.</p>\r\n<p>\r\n	<br />\r\n	Ondare nagusien artean daude Erdi Aroan eraiki eta gerora barrokoan berritze-lanak egin zizkioten San Migel eliza, estilo barrokoko Ama Birjina Arrosariokoaren eliza, eta XVI. mendean eraikitako eta egun era guztietako artelanak biltzen dituen Arrese etxea.</p>\r\n', '', '8.123', '42°6&#8242;N 1°47&#8242;W', 'http://www.corella.es/', '', 'corella.jpg', 42.11429977416992187500, -1.78544998168945312500, 15, 'hybrid', 42.11434555053710937500, -1.78513669967651367188, 8);");
	tx.executeSql("INSERT INTO `herriak` VALUES(110, '2012-02-03 16:43:54', 'Etxalar', 5, '<p>\r\n	Nafarroan, Lapurdiko mugan, dago kokatuta udalerria, Iruñeko merindadearen iparraldean hain justu. Bera, Baztan, Igantzi, Lesaka, Bertizarana eta Urepele (Lapurdi) ditu mugakide.</p>\r\n<p>\r\n	<br />\r\n	Etxalargo ondasun historiko nagusiaren parte dira 1735. urtean eraikitako Iñarretako gurutzea, Erdiko errota, Harrizko garbitegiak, Herriko enparantza, eta herriaren panoramika eskaintzen duen Akoka-ko begiratokia.</p>\r\n<p>\r\n	<br />\r\n	Udalerriko festak abuztuaren 15ean, Andre Mariaren egunean, ospatzen dituzte.</p>\r\n', '', '826', '42°14&#8242;1&#8243;N 1°38&#8242;14&#8243;W', 'http://www.etxalar.org/', '', 'etxalar.jpg', 43.23326110839843750000, -1.63679122924804687500, 16, 'hybrid', 43.23332214355468750000, -1.63770103454589843750, 9);");
	tx.executeSql("INSERT INTO `herriak` VALUES(111, '2012-02-06 11:14:41', 'Santurtzi', 2, '<p>\r\n	Abra ertzean, Seantes mendiaren magalean, Nerbioi ibaiaren alboan dago kokatuta, eta historian zehar arrantzarekin lotura estua izan du herriak, horren ondorioz, &ldquo;villa de las sardinas&rdquo; deitu izan diote.Euskal Herriko bederatzigarren hiri populatuena da. Ortuella, Zierbena eta Portugaleterekin egiten ditu mugakide. Iparraldean Bizkaiko Golkoa du.</p>\r\n<p>\r\n	<br />\r\n	Santurtziko leku interesgarrien artean 1902an eraikitako Oriol Jauregia; Merkataritza-Portua; Itsasoko Amaren Parrokia; Udaletxea, Cristobal Murrietari Monumentua; eta Eskulturen museoa daude.</p>\r\n', '1075', '47.076', '43°19&#8242;46&#8243;N 3°1&#8242;59&#8243;W', 'http://www.santurtzi.net/eu-ES/Orrialdeak/default.aspx', 'Karmenak. Uztailaren erdialdean.', 'santurtzi.jpg', 43.32795715332031250000, -3.03115367889404296875, 15, 'hybrid', 43.32828140258789062500, -3.03180599212646484375, 8);");
	tx.executeSql("INSERT INTO `herriak` VALUES(112, '2012-02-06 11:17:40', 'Portugalete', 2, '<p>\r\n	Sestao, Trapagaran, Santurtzi eta Urtuella dira hiri industrial honen mugakideak. &ldquo;Bizkaiko Zubia&rdquo; deritzonak Portugalete Getxorekin lotzen du. 1893ko burdinazko zubi esekia UNESCOk Gizateriaren Ondare izendatu zuen.</p>\r\n<p>\r\n	<br />\r\n	Euskal Herriko zortzigarren herri populatuena da, eta dentsitateari dagokionez handiena duena. Beste zenbait guneren artean bisitagarriak dira&nbsp; Areilza doktorearen parkea, Alde Zaharra, El Saltillo Jauregia, Industria museoa, eta Marometroa.</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	San Antonio Jaiak (ekaina), Giazko Andra Mari (uztailak 1), San Kristobal Jaiak (uztailak 10), Santiago Eguna (uztailak 25), Roke Deuna (abuztuak, 14tik 17ra), San Nikolas (Irailak 12), Gurutzea (irailak 18tik 20ra) eta Azeta (urria) opastzen dituzte besteak beste.</p>\r\n', '1322', '47.742', '', 'http://www.portugalete.org/portal_localweb/?null&language=eu', 'Roke deuna: Abuztuaren 14, 15, 16 eta 17an.', 'portugalete.jpg', 43.31899642944335937500, -3.02351474761962890625, 15, 'hybrid', 43.32067871093750000000, -3.01981115341186523438, 9);");
	tx.executeSql("INSERT INTO `herriak` VALUES(113, '2012-02-06 11:57:27', 'Barakaldo', 2, '<p>\r\n	Bizkaiko herri populatuenetakoa da, Bilboren atzetik, eta Euskal Herriko hiri nagusien artetik bosgarren da. Ibaizabal ibaiaren mendebaldean dago; Alonsotegi, Bilbo, Trapagaran eta Sestao ditu mugakide. Argalario mendia du mendebaldean eta Galindo eta Kadagua ibaiek mugatzen dute ipar eta hegoaldean.&nbsp; Herriaren erdigunea muino batean dago, eta hau pixkanaka jaisten da Ibaizabal ibaira bidean.</p>\r\n<p>\r\n	<br />\r\n	Azken urteetan herriak eraldaketa handia bizi izan du, oinezko guneei garrantzia eman zaie eta enparantzak ere sortu dira. Pasieran aritzeko guneetan ikuskizunak antolatzen dituzte maiz herritarrek.&nbsp; Horrez gain, era askotako ikuskizunak egoten dira hilabete osoan zehar Barakaldoko hainbat antzokitan.</p>\r\n<p>\r\n	<br />\r\n	Herriko jai egunak Karmengo Jaiak dira, eta uztailaren 16an ospatzen dituzte.</p>\r\n', '1052 baino lehen', '100.061', '43°17&#8242;54&#8243;N 2°59&#8242;7&#8243;W ', 'http://www.barakaldo.org/portal/web/barakaldo/barakaldo', 'Uztailaren 16a, Karmengo Ama.', 'barakaldo.jpg', 43.29526138305664062500, -2.99094200134277343750, 14, 'hybrid', 43.29739761352539062500, -2.99125099182128906250, 9);");
	tx.executeSql("INSERT INTO `herriak` VALUES(114, '2012-02-06 11:59:57', 'Sestao', 2, '<p>\r\n	Ibaizabal ibaiaren mendebaldean kokatzen da herria, Nerbioi ibai ertzean; Trapagaran, Barakaldo eta Portugalete ditu mugakide eta erdigunea 83,95 metro dituen muino batean kokatzen da. Ibaizabalez gain, Galindo eta Ballonti ibaiek herria zeharkatzen dute.</p>\r\n<p>\r\n	<br />\r\n	Hiri industriala da ibai-portuari esker. Herria Santa Maria elizaren eta gazteluaren inguruan sortu zen. Eliza gotiko berantiarrekoa da, baina berritze lanak egin izan dizkiotenez, guztiz itxuraldatua dago gaur egun. Arkitekturari dagokionez, oso garrantzitsuak dira XIX. mendean sortutako udaletxe neoklasikoa, hilerriko portada eta Rebonzako zaharren egoitzako tenplua.</p>\r\n<p>\r\n	<br />\r\n	Herriko jaiak San Pedro (ekainaren 29a), San Inazio (uztailaren 31a), Santiago (uztailaren 25a) eta Santa Rosa (abuztuaren 30a) egunetan ospatzen dira.&nbsp;</p>\r\n', '', '28.959 ', '43°18&#8242;35&#8243;N 3°0&#8242;25&#8243;W ', 'http://www.sestao.net/index.php?lang=eu', '- Ekainaren 29an, San Pedro jaiak.\r\n- Uztailaren 31n, San Inazio.\r\n- Uztailaren 25ean, Santiago.\r\n- Abuztuaren 30ean, Santa Rosa.\r\n', 'sestao.jpg', 43.31003189086914062500, -3.00514698028564453125, 15, 'hybrid', 43.30964279174804687500, -3.00721549987792968750, 9);");
	tx.executeSql("INSERT INTO `herriak` VALUES(115, '2012-02-06 12:05:26', 'Getxo', 2, '<p>\r\n	Bizkaiko kostaldeko udalerri honeta dago Bizkaiko Zubia izena duen 1893ko burdinazko zubi esekia, UNESCOk Gizateriaren Ondare izendatua, Areeta Portugaleterekin lotzen duena. Herria ingurune malkartsuan dago eta Sopela, Berango, Erandio eta Leioa ditu mugakide. Areeta, Ereaga, Arrigunaga, eta Azkorri hondartzak bertan daude.</p>\r\n<p>\r\n	<br />\r\n	Galea muturraren inguruan historiaurreko aztarnak aurkitu dira. Hasieran herriko sorgunea Santa Maria monasterioaren ondoan bildu zen. Eliza horretan oraindik XII-XIII. mendeko aztarnak ikus daitezke, berikuntzak egin dizkioten arren.</p>\r\n<p>\r\n	<br />\r\n	Arkitekturari dagokionez, Bilbo babesteko XVIII. mendean eraikitako Galeako gotorlekua, zenbait jauregi eta etxe garrantzitsu daude bertan.</p>\r\n', '1075', '80.089', '43°21&#8242;N 3°0&#8242;W', 'http://www.getxo.net/inicio/getxo_inicial_eu.asp', 'San Isidro. Maiatzaren 12tik 14ra.', 'getxo.jpg', 43.34490585327148437500, -3.00922393798828125000, 14, 'hybrid', 43.34446716308593750000, -3.00618553161621093750, 9);");
	tx.executeSql("INSERT INTO `herriak` VALUES(116, '2012-02-06 12:15:25', 'Bermeo', 2, '<p>\r\n	Arrantza tradizio handia duen herria da, eta bertako ekonomia arrantzaren inguruan oinarritzen da, batez ere, arrain-kontserbagintzan. Bermeoko portuak Erdi Aroan garrantzia handia izan zuen, Euskal Herriko portuetan ezagunena izatera heldu zen itsasbazterreko arrantzaren alorrean.</p>\r\n<p>\r\n	<br />\r\n	Aketx eta Izaro uharteak ditu parean eta Matxitxako lurmuturra albo batean. Izaro irlan estilo gotikoa duen San Frantziskoko komentua dago.&nbsp; Horretaz gain, turismo gune ezaguna da, besteen artean Gaztelugatxeko Donienegatik.</p>\r\n<p>\r\n	<br />\r\n	Herriko jaiak Irailaren 8an hasten dira eta 16an amaitu. Egun garrantzitsuenak hilaren 8 eta 9a dira, \"Andra Mari lehen eguna\" eta \"Arrantzale eguna\". Arrantzale egunean mahonezko gona eta galtzekin jazteko ohitura dago.</p>\r\n<p>\r\n	<br />\r\n	Bermeotarrak dira besteak beste Nestor Basterretxea (artista), Josu Erkoreka (EAJko politikaria), Izaskun Bilbao (EAJko politikaria) eta Agurne Anasagasti (telebistako aurkezlea).</p>\r\n', '1236', '17.078', '43°25&#8242;14&#8243;N 2°43&#8242;20&#8243;W', 'http://www.bermeokoudala.net', 'Andramari. Irailaren 7tik 16ra bitartean. ', 'bermeo1.jpg', 43.42007446289062500000, -2.72400856018066406250, 16, 'hybrid', 43.42042922973632812500, -2.72262239456176757812, 8);");
	tx.executeSql("INSERT INTO `herriak` VALUES(117, '2012-02-06 12:19:49', 'Ea', 2, '<p>\r\n	Bizkaiko kostaldean dagoen udalerri honetan pasa zituen bere azken egunak Gabriel Aresti olerkariak, eta horrek olerki batzuk idaztera ireki zion bidea. Hori dela eta, Aresiren omenez, 2003 urtetik uztailero \"Eako poesia egunak\" antolatzen dituzte.</p>\r\n<p>\r\n	<br />\r\n	Arrantza izan du jarduera nagusi historian zehar herri honek XVI. mendetik. Ea ibaiak herria bitan banatu ostean bertan itsasoratzen da. Herriko bi aldeak lau zubik lotzen dituzte.</p>\r\n<p>\r\n	<br />\r\n	Ea XVI. mendean sortu zen eta Natxituako eta Bedaroako auzoko arrantzaleek kokaleku iraunkor bihurtu zuten Ea ibaiaren itsasadarrak osatzen duen portu naturala.</p>\r\n<p>\r\n	<br />\r\n	Natxituako auzoan, Natxituko lapatza harrizko hondartza dago.</p>\r\n<p>\r\n	<br />\r\n	EAko jaiak San Joan egunean, ekainaren 24an, ospatzen dira.&nbsp; Aritza hondartzaren erdian su handi bat pizten dute eta herritarrek suaren ingruruan dantzatzen dute danbolin eta txistuaren laguntzarekin.&nbsp;</p>\r\n', 'XIX. mendean', '896', '43°22&#8242;50&#8243;N 2°35&#8242;5&#8243;W ', 'http://www.eakoudala.net/eu-ES/Orrialdeak/default.aspx', 'San Juan Bautista. Ekainaren 24a.', 'ea.jpg', 43.38070678710937500000, -2.58444786071777343750, 16, 'hybrid', 43.37997436523437500000, -2.58482122421264648438, 10);");
	tx.executeSql("INSERT INTO `herriak` VALUES(118, '2012-02-06 12:21:36', 'Bakio', 2, '<p>\r\n	Bizkaiko itsasertzean dagoen herria da, Estepona eta Ondarre ibaiek zeharkatzen dute eta Bermeo, Lemoiz, Maruri-Jatabe eta Mungia ditu mugakide. San Pelaio eliza arkupez inguratuta dagoen XII. mendeko eraikin errokantikoa da.</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	Bizkaiko Txakolinaren ekoizle nagusienetakoa da eta baita probintzian tradiziorik handiena duen herria ere.</p>\r\n<p>\r\n	<br />\r\n	San Jose (maiatzaren 19an), San Joanak (ekainaren 24an), San Pelaioak (ekainaren 26an), Andra Mari (abuztuaren 15ean)&nbsp; eta San Joan Dolozak (abuztuaren amaieran) dira herriko jai nagusiak.</p>\r\n', '', '2.542', '43°25&#8242;40&#8243;N 2°48&#8242;41&#8243;W', ': http://www.bakio.org/eu-ES/Orrialdeak/default.aspx', 'Andra Mari. Abuztuaren 15a', 'bakio.jpg', 43.42752456665039062500, -2.80898094177246093750, 15, 'hybrid', 43.42679977416992187500, -2.80875778198242187500, 9);");
	tx.executeSql("INSERT INTO `herriak` VALUES(119, '2012-02-06 12:23:26', 'Plentzia', 2, '<p>\r\n	Plentzia-Mungia eskualdean kokatuta dagoen Bizkaiko udalerri hau XIII. mendean sortu zuen Diego Lopez Harokoak, Bizkaiko jaunak, Gaztelako merkantzien garraioan giltzarri izango zen puntu bihurtzeko asmoz.&nbsp; Gorliz, Lemoiz, Gatika , Urduliz eta&nbsp; Barika ditu mugakide herriak.</p>\r\n<p>\r\n	<br />\r\n	Plentzia erdigunetik urrundu ahala altuagoak diren zenbait munoz dago inguratuta; garaienak Zabala-Ondo eta Kukutxa dira.</p>\r\n<p>\r\n	<br />\r\n	Erromatarren garaiko aztarnak aurkitu dira herrian, horiek azaltzen dute garai horretan bertan jendea bizi izan zela.&nbsp; Jatorrizko eliza XV. mende amaierakoa da, eta elementu gotiko-errenazentistak ditu.</p>\r\n<p>\r\n	<br />\r\n	2009ko maiatzaren 26an, Eusko Jaurlaritzak monumentu-multzo izendatu zuen Plentziako hirigune historikoa, Sailkatutako Kultura Ondasuna.</p>\r\n', '1299', '4.311', ' 43°24&#8242;21&#8243;N 2°56&#8242;47&#8243;W', ': http://www.plentzia.net/eu_index.php', '', 'plentzia1.jpg', 43.40506362915039062500, -2.94665336608886718750, 16, 'hybrid', 43.40465545654296875000, -2.94953727722167968750, 8);");
	tx.executeSql("INSERT INTO `herriak` VALUES(120, '2012-02-06 12:27:33', 'Sopela', 2, '<p>\r\n	Bizkaiko kostalde malkartsuan dagoen udalerria da, Arrietara, Atxiribil eta Menakoz hondatzak daude bertan eta mugakide ditu Barrika, Berango eta Getxo. Hondartza horietan surf eta bodyboard ikastaroak egiten dira eta parapentean ibiltzeko hegaldiak ere egiten eskaintzen dituzte.</p>\r\n<p>\r\n	<br />\r\n	San Pedro eliza XII. mendean sortu zen baina lekuz aldarazi zuen Sopelako jaunaren alargunak eta muino batera eraman eta XVIII. mendean berriro eraiki zuten.</p>\r\n<p>\r\n	<br />\r\n	San Petri (ekainaren 29a) da Sopelako patroi nagusia, horretaz gain, Larrabasterra auzoko patroia Karmengo Ama ( uztailaren 16a) denez egun hori ere ospatzen dute herrian besteak beste.</p>\r\n', '', '12.693', '43°22&#8242;53&#8243;N 2°58&#8242;56&#8243;W', 'http://www.sopelana.euskadi.net/v43-homea/eu/contenidos/informacion/sopelana_main/eu_sopelana/home_main_1.html', 'Sopelako patroi nagusia San Petri da, herrigunean dagoen elizaren patroia baita (ekainaren 29a) eta Larrabasterra auzoko patroia Karmengo Ama, bertan dagoen baseliza dela eta (uztailaren 16a). Hala ere, beste bi baselizek euren patroia eta jaia ere badaukate, Santa Mariñek uztailaren 18an (arrazoi politikoak zirela eta 20ra atzeratu zena) eta Ander Deunak azaroaren 30ean. Horietaz gain, patroien omenez ez diren hainbat jai ere badaude: Euskararen Eguna, Erdi Aroko Azoka, Koadrilen Eguna...', 'sopelana.jpg', 43.38016128540039062500, -2.98240184783935546875, 15, 'hybrid', 43.37892913818359375000, -2.98256063461303710938, 9);");
	tx.executeSql("INSERT INTO `herriak` VALUES(121, '2012-02-06 12:29:11', 'Zalla', 2, '<p>\r\n	Bizkaian, Enkarterriko eskualdean, Salcedo haranean dago kokatuta udalerri hau. Udalerriaren inguruan daude Artegi, La Cabaña, Basoaga, Espaldaseca, Riyosa eta Ilso mendiak, ingurune menditsua Kadagua ibaiak banatzen du.</p>\r\n<p>\r\n	Burgos probintziarekin egiten du muga hegoaldean. Erromatarren garaiko aztarnak aurkitu dira bertan, eta besteak beste Gaztelatik Bizkairako galtzada bat bertatik pasatzren zela uste da.</p>\r\n<p>\r\n	Ondare eta monumentuen artean&nbsp; Terreros Dorrea, Casa Pinta, San Pataleon Baseliza eta San Pedro Zarikete daude.</p>\r\n<p>\r\n	La Mellako San Antonio ermita XVII. Mendean sortu zen. San Miguel eliza aldiz barrokoa da eta Erdo Aroko dorretxeak aurki daitezke arkitektura zibilean.</p>\r\n', 'XII. mendean', '8.258', '43°12&#8242;56&#8243;N 3°7&#8242;54&#8243;W	', 'http://www.zalla.org/eu-ES/Orrialdeak/default.aspx', 'Irailaren 29an Mikel Deuna ospatzen da, eta asteburuz tokatzen bada, festa polita izan ohi da.\r\nUrriaren lehen igandean El Rosario ospatzen da, eta hurrengo astelehenean, Gangas egunean, merkatu ederra izaten da goizean.', 'zalla.jpg', 43.21271133422851562500, -3.13335657119750976562, 16, 'hybrid', 43.21282196044921875000, -3.13224935531616210938, 9);");
	tx.executeSql("INSERT INTO `herriak` VALUES(122, '2012-02-06 12:33:45', 'Zornotza', 2, '<p>\r\n	1951ko urtarrilaren 26an eratu zen gaur egungo herria, Zornotza eta Etxano herriak batu zirenean. Ibaizabal ibaiak zeharkatzendu herria eta mendiz inguratuta dago: Betzuenburu, Kañometa, Gorritxueta, Bizkargi, Urrimendi, Arrezuriaga eta Torreburu. Gaur egun Etxano auzoa legez ezagutzen dugunak 50. hamarkadara arte bere udaletxea izan zuen.</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	Herriko pertsona ezagunen artean daude Xabier Amuriza bertsolaria, Jon Idigoras Herri Batasunako politikaria, Mikel Urdangarin abeslaria eta Andres Espinosa mendizalea.</p>\r\n<p>\r\n	<br />\r\n	Eraikin ikusgarrienen artean daude Sabino Arana kalean dagoen neoklasiko estiloko Cancelada Jauregia, ingeles estiloko Harrison Etxea, eta Barroko garaiko apaingarriz hornituta dagoen Canuto Basterraren Etxea. Horietaz gain, eraikin zibil eta erlijioso garrantzitsuak ere badaude herrian.</p>\r\n', '', '18.140', '43°13&#8242;9&#8243;N 2°44&#8242;3&#8243;W 	', 'http://www.amorebieta-etxano.net/', 'Zornotzako jaiak uztailaren 15etik (Karmengo Andra Mariaren egunaren bezpera) uztailaren 27ra (Santanatxu eguna) ospatzen dira.', 'zornotza.jpg', 43.21903228759765625000, -2.73289203643798828125, 15, 'hybrid', 43.21793746948242187500, -2.73473739624023437500, 9);");
	tx.executeSql("INSERT INTO `herriak` VALUES(123, '2012-02-06 12:40:47', 'Gernika-Lumo', 2, '<p>\r\n	Gernika Euskal Herriaren sinboloetako bat da, eta Urdaibai erreserba naturalaren atarian kokatua dago, itsasadarraren bi parteak elkartzen dituen herria.</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	1937ko apirilaren 26an herriak zibilen aurkako airetiko bonbardaketak pairatu zituen. Gertakari triste honek Gernika izena mundu osora zabalduko zuen, tragedia irudikatu nahi izan zuen Pablo Picasso margolariak herriaren izena jarri baitzion sufrimenduaren isla den Gernika margolanak. Kondor legioko hegazkinek erabat suntsitu zuten herria eta egun bisitariak aurkituko duena, azken hamarkadetan berreraikitako herri dotorea da.</p>\r\n<p>\r\n	<br />\r\n	Herri osoan zehar daude gertakari horren oroigarriak, Picasso margolari ospetsuaren zeramikazko &ldquo;Guernica&rdquo; horma-irudia, Gernikako Bakearen Museoa Fundazioa, Gernikako Arbola ospetsua, eta Europako herrien Pakea besteak beste.</p>\r\n', '1366ko apirilaren 28a', '16.442', '43°19&#8242;1&#8243;N 2°40&#8242;36&#8243;W	', 'http://www.gernika-lumo.net/eu_index.asp', 'Andra Mari eta San Roke deuna: Herriko Jaiak (abuztuak 15 eta 16) .\r\n\r\nBartolome Deuna: Errenteria auzoko jaiak (abuztuak 10)', 'gernika-lumo.jpg', 43.31621551513671875000, -2.67980575561523437500, 15, 'hybrid', 43.31705856323242187500, -2.67790460586547851562, 9);");
	tx.executeSql("INSERT INTO `herriak` VALUES(124, '2012-02-06 12:43:53', 'Mundaka', 2, '<p>\r\n	Betidanik izan du itsasoarekiko lotura estua eta, horrek eragin zuen munduari irekita egotea. Gaur egun Bizkaiko Golkoarekin lotura estua du herriak eta betidanik bertako jendea protagonista izan da Euskal Herriko nabigazioaren eta arrantzaren historian.</p>\r\n<p>\r\n	<br />\r\n	Monumentu garrantzitsuenen artean XI. mendean sortutako Andra Mari eliza, XIX, mendeko Santa Katalina ermita, Portua, erreka itsasoratzen denean sortzen dituen olatuak ikusteko Talako Begiratokia eta Portuondoko Begiratokia daude.</p>\r\n<p>\r\n	<br />\r\n	Azken urteetan gainera, ezagun egin da bertan dauden olatuen ondorioz, &ldquo;ezkerreko olatua&rdquo; deritzona, Europako onenetakoen artean baitaukate 4metroko altuera eta 400 metroko luzeera izan dezaken olatua.</p>\r\n', '', '1.939 ', '43°24&#8242;26&#8243;N 2°41&#8242;54&#8243;W', 'http://www.mundaka.org/eu-ES/Orrialdeak/default.aspx', '', 'mundaka.jpg', 43.40766525268554687500, -2.69845247268676757812, 16, 'hybrid', 43.40741348266601562500, -2.69906187057495117188, 9);");
	tx.executeSql("INSERT INTO `herriak` VALUES(125, '2012-02-06 13:05:35', 'Muskiz', 2, '<p>\r\n	Bizkaiko kostaldeko mendebaldean, Bilboaldean, dago kokatuta. Barbadun ibaiak&nbsp; zeharkatzen du udalerria eta La Arena hondartza dago bertan. Mugakide ditu Galdames, Abanto-Zierbena, Zierbena, eta Urdialaitz. Udalerria mendiz dago inguratuta, besteak beste, Mello (626 m.), Corbera haitza, (361 m.), Carrascal (269 m.), Ramos (229 m.) eta Janeo (203 m.) dira.</p>\r\n<p>\r\n	<br />\r\n	Monumentu nagusien artean ditu Muñatonesko San Martin gaztelua, Soroskundeko Amaren baseliza, El Pobaleko burdinola eta El Pobaleko zubi erromanikoa.</p>\r\n', '', '7.408 ', '43°19&#8242;24&#8243;N 3°7&#8242;18&#8243;O', 'http://www.muskiz.org/home-eu?set_language=eu', 'Ekainak 24 - Donibane Egunean dira herriko jaiak', 'muskiz.jpg', 43.32136917114257812500, -3.12522411346435546875, 15, 'roadmap', 43.32366180419921875000, -3.12212133407592773438, 9);");
	tx.executeSql("INSERT INTO `herriak` VALUES(126, '2012-02-06 13:09:39', 'Ondarroa', 2, '<p>\r\n	Bizkaiko ipar-ekialdean,&nbsp; Lea-Artibai eskualdean,&nbsp; kokatuta dagoen herri hau oso herri malkartsua da. Herriko mendiak Arrigorri, Santa Cruz,&nbsp; Arrikuntzalde, Pertika, Kalamendi, Urtzelai&nbsp; eta Mustutxuru dira. Etxebizitza gehienak eta portua udalerriko gunerik garrantzitsuenean, Errenterian, kokatzen dira.&nbsp;</p>\r\n<p>\r\n	<br />\r\n	Arigorri hondartza dago herrian eta ondasun nagusien artean Alde Zaharra, Andra Maria eliza, Udaletxe zaharra, Zubi Zaharra eta Itsas Aurre zubia daude.</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	Herriak arrantzale historia handia du, eta bertako portuan mugimendu handia topatuko du bisitariak.</p>\r\n', '1327ko irailaren 27an', '8.852 ', '43°19&#8242;50&#8243;N 2°25&#8242;40&#8243;W', 'http://www.ondarroa.net/', 'Abuztuaren erdialdean.', 'ondarroa.jpg', 43.32080841064453125000, -2.42151975631713867188, 16, 'hybrid', 43.32088088989257812500, -2.42163562774658203125, 9);");
	tx.executeSql("INSERT INTO `herriak` VALUES(127, '2012-02-06 13:12:26', 'Lekeitio', 2, '<p>\r\n	Bizkaiko kostaldean, Lea-Artibai eskualdean, dagoen udalerria da. Honek mugakide ditu Ispaster, Mendexa eta Amoroto. Bizkaiko arrantza portu eta gune turistikorik garrantzitsuenetakoa da. Horren ondorioz, udalerriko sektore indartsua da turismoa.</p>\r\n<p>\r\n	<br />\r\n	Mendiz inguratuta dago udalerria, Otoio eta Lumentza dira alboan dituen mendiak, eta Lea ibaiak zeharkatzen du herria.&nbsp; Ibai hau Mendexa eta Lekeitio udalerrien artean itsasoratzen da. Herriko ondasun nagusien artean daude Abaroa Jauregia, Lekeitioko talaia eta Jasokundeko Andre Mariaren basilika.</p>\r\n', '1325', '7.438 ', '43°21&#8242;44&#8243;N 2°29&#8242;46&#8243;W', 'http://www.lekeitio.com/eu-ES/Orrialdeak/default.aspx', 'San Antolinak irailaren 1etik 8ra. Antzar eguna irailaren 5ean. ', 'lekeitio.jpg', 43.36437606811523437500, -2.49986171722412109375, 15, 'hybrid', 43.36558914184570312500, -2.50322628021240234375, 9);");
	tx.executeSql("INSERT INTO `herriak` VALUES(128, '2012-02-06 13:20:17', 'Durango', 2, '<p>\r\n	Bizkaiko barnealdean, hego-ekialdean ekialdean hain justu, dago kokatuta udalerria, Iurreta, Izurtza, Amorebieta-Etxano, Dima eta Abadiñoren mugan. Ibaizabal ibaia pasatzen da bertatik eta bertan sortzen dira Mañaria ibaia eta Larrinagatxu erreka.&nbsp; Udalerriaren inguruan daude Mugarra (960 metro) eta Neberondo (453 metro) mendi ederrak.</p>\r\n<p>\r\n	<br />\r\n	Herriko eraikin garrantzitsuenen artean daude 1440 urteko Kurutziagako gurutzea, 1483 urteko Lariz dorrea, Tabirako San Pedro eliza eta estilo gotiko eta errenazentista duen Uribarriko Andra Mariren basilika.</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	Bertan egiten da Durangoko Euskal Disko eta Liburu Azoka eta milaka euskaltzale biltzen dira abendu hasieran.</p>\r\n', '', '28.226 ', '43°10&#8242;N 2°38&#8242;W', 'http://www.durango-udala.net/portalDurango/p_1_final_Principal_1.jsp?language=eu', 'San Faustoak. Durangoko patroiaren jaiak dira eta urriaren 13aren inguruan ospatzen dira, Durangaldeko jai-egutegiko garrantzitsuenak eta azkenak direlarik.', 'durango.jpg', 43.16888046264648437500, -2.63307094573974609375, 15, 'hybrid', 43.16897201538085937500, -2.63284349441528320312, 9);");
	tx.executeSql("INSERT INTO `herriak` VALUES(129, '2012-02-06 13:28:03', 'Ermua', 2, '<p>\r\n	Bizkaiko ekialdean dago kokatuta herria, Debabarrena eskualdean. Eibar, Zaldibar eta Mallabia ditu mugakide. Oiz eta Urko mendien artean dago kokatuta, 1.026 eta 730 metro dituzte urrenez urren.</p>\r\n<p>\r\n	<br />\r\n	Heriko ondare interesgarrien artean daude Santiago Eliza, 1890eanL. Urgellesek eginiko grabatuak erakusten dituena, eta XVI. mendean eraikitako Lobiano Jauregia.</p>\r\n<p>\r\n	<br />\r\n	Udalerrian ondorengo jaiak ospatzen dituzte: Santiago Jaiak, uztailaren 24tik 27ra bitartean; Euskal jaiak, maiatzeko lehenengo igandean eta San Martin azoka, San Martin egunetik gertuen dagoen larunbatean.</p>\r\n', 'XIII. mende amaieran', '16.274', '', 'http://www.ermua.es/eu_index.asp', 'Santiago. Uztailaren 14tik 27ra.', 'ermua.jpg', 43.18745422363281250000, -2.50383138656616210938, 16, 'hybrid', 43.18680953979492187500, -2.50212335586547851562, 9);");
	tx.executeSql("INSERT INTO `herriak` VALUES(130, '2012-02-06 13:33:39', 'Mungia', 2, '<p>\r\n	Bizkaiko barrualdean dago kokatuta Mungia, itsasotik 20 metro inguruko garaieran. Bakio, Bermeo, Derio, Zamudio, Gamiz-Fika, Meñaka, Loiu, Gatika eta Jatabe ditu mugakide udalerri honek.</p>\r\n<p>\r\n	<br />\r\n	Sollube (684 m), Jata (592 m), Gondramendi (217 m), Tallu (342 m) eta Berreaga (366 m) mendiak ditu inguruan. Butroi ibaia udalerritik pasatzen da, baita Butroi ibaiaren ibaiadar diren Atxuri, Trobika, Lauromendi, Atebarri edo Mantzorriko errekak ere.</p>\r\n<p>\r\n	<br />\r\n	Ondare arkitektoniko eta arkeologikoez gain arkitektura zibil eta erlijiosoaren parte diren eraikin aipagarriak daude herrian.&nbsp; San Pedro eta Andra Mari elizak esaterako horren adibide dira.</p>\r\n', '', '16.737', '43°21&#8242;17&#8243;N 2°50&#8242;50&#8243;W', 'http://www.mungia.org/euskara/portada.php', 'San Pedroak. Ekainaren amaieran.', 'mungia.jpg', 43.35367584228515625000, -2.84751892089843750000, 15, 'hybrid', 43.35435867309570312500, -2.84651899337768554688, 9);");
	tx.executeSql("INSERT INTO `herriak` VALUES(131, '2012-02-06 13:36:26', 'Elorrio', 2, '<p>\r\n	Bizkaiko hego-ekialdeko muturrean dago kokatuta, Udalaitz mendiaren magalean, eta Gipuzkoa eta Arabaren mugakidea da. Udalerri mugakideak ditu Berriz, Zaldibar, Arrasate, Aramaio, Elgeta, Bergara, Atxondo eta Abadiño.</p>\r\n<p>\r\n	<br />\r\n	Ondare historikoko jauregi, ermita eta lan artistiko ugari daude bertan. Hirigunea gune historiko artistiko izendatu zuen Eusko Jaurlaritzak 1964. urtean. IX. Mensean sortutako Argiñetako nekropolia, Don Telloren ataria, Esteibar-Arauna, Arabio, Lariz, Urkizu eta Olazabal jauregiak, eta Arriola Kultur Aretoa udalerriko ondare historikoaren parte dira.</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	Udalerrian guztira hogedi ermita daude, hainbat auzotan banatuta. Elizei dagokienez, 1053an eraiktiako gotiko estiloko San Agustin eliza eta estilo bereko Sortzez Garbiaren basilika aipagarriak dira.</p>\r\n', '1356', '7.249', '43°8&#8242;&#8243;N 2°32&#8242;&#8243;O', 'http://www.elorrio.net/html/home/default.asp', 'Ferixa Nausikuak. Iraileko lehenengo igandetik aurrera astebetez', 'elorrio.jpg', 43.12971115112304687500, -2.54191875457763671875, 15, 'hybrid', 43.13022613525390625000, -2.54166984558105468750, 10);");
	tx.executeSql("INSERT INTO `herriak` VALUES(132, '2012-02-06 17:17:38', 'Markina-Xemein', 2, '<p>\r\n	Bizkaian dagoen udalerria da, Lea-Artibai eskualdekoa, bertan bat egiten dute Artibai eta Urko ibaiek. Amoroto, Berriatua, Aulesti, Ziortza-Bolibar, Etxebarria, Eibar, Mallabia eta Berriz ditu mugakide. Ingurune menditsuan dago kokatuta eta Gipuzkoarekin muga egiten du.</p>\r\n<p>\r\n	<br />\r\n	Eraikin nagusien artean daude XI. mendean eraikitako Xemeingo Andra Mariaren Zeruratzea eliza, 1851. urtean eraikitako Xemeingo hilerria, barroko estiloko Mesedeetako lekaimeen konbentua, Patrokua eta Solartekua jauregiak, eta Antxia eta Ugarte dorreak.</p>\r\n<p>\r\n	<br />\r\n	Herriko jaiegun nagusiak Andre Mariaren omenez ospatzen dira uztaialren 16ean. Horretaz gainera, irailaren 29an Mikel goiaingeruaren jaia ere ospatzen dute, eta auzoetan beste zenbait festa ere ospatzen dituzte.</p>\r\n', '1952', '4.967 ', '43°16&#8242;13&#8243;N 2°29&#8242;48&#8243;W', 'http://www.markina-xemein.com/eu-ES/Orrialdeak/default.aspx', 'Andre Maria. Uztailaren 16 inguruan', 'markina-xemein.jpg', 43.26779174804687500000, -2.49724102020263671875, 15, 'hybrid', 43.27041625976562500000, -2.49827098846435546875, 9);");
	tx.executeSql("INSERT INTO `herriak` VALUES(133, '2012-02-06 17:21:53', 'Balmaseda', 2, '<p>\r\n	Bizkaiko mendebaldean, Enkarterriko eskualdean, dago kokatuta udalerria. Leku menditsuan dago kokatuta; Kolitza, Canto, Terreros eta Garbea gailurren alboan dago Ibarra bertatik Kadagua ibaira hedatzen da. Artzentales, Sopuerta, Zalla eta Burgos probintzia ditu mugakide.</p>\r\n<p>\r\n	<br />\r\n	Jai egunei dagokienez, jai patronalak Seberino Deunaren egunean urriaren 23an ospatzen dituzte, horretaz gainera, Inauteriak, Erdi Aroko Azoka, Kolitza mendira igoera ospatzen dira, baita uztailaren 16an Karmengo eguna ere.</p>\r\n<p>\r\n	<br />\r\n	Herriko ondasun nagusien artean daude XIV. mendean eraikitako estilo gotikoko Seberino Deunaren parrokia, XV. mendeko Donibane eliza eta XII. mendean eraiki zuten herrira sartzeko zubia.</p>\r\n', '1199', '7.583 ', '43°11&#8242;34&#8243;N 3°11&#8242;49&#8243;W', ': http://www.balmaseda.net/eu-ES/Orrialdeak/default.aspx', 'San Severinoak. ', 'balmaseda.jpg', 43.19350814819335937500, -3.19469952583312988281, 16, 'hybrid', 43.19327163696289062500, -3.19621229171752929688, 9);");
	tx.executeSql("INSERT INTO `herriak` VALUES(134, '2012-02-06 17:25:55', 'Galdakao', 2, '<p>\r\n	Bilboaldean dago kokatuta Galdakao, Gangurengo mendilerroaren magalean. Zamudio, Lezama, Larrabetzu, Zaratamo, Zeberio, Amorebieta-Etxano, Lemoa, Bedia, Etxebarri eta Basauri ditu mugakide.</p>\r\n<p>\r\n	Ingurune malkartsuan dago udaleria, Arrezurriaga (329 metro), Txispamendi (221 metro) , Santa Maria (476 metro), Upo (556 metro) eta Mandoia (640 metro) mendiak ditu inguruan. Ibaizabalek udalerria zeharkatzen du.</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	Bertako ondare nagusien artean daude XIII. Mendean eraikitako estilo erromanikoko Andra Mari eliza, Isasi Goikoa dorrea, Torrezabaleko zubia.</p>\r\n<p>\r\n	<br />\r\n	Jai egunei dagokienez, Gurutze Santuaren omenez ospatzen dituzte irailaren 14aren inguruan. Horretaz gain, inauteriak eta ekainaren 23an San Joan eguna ere ospatzen dituzte.</p>\r\n', '', '29.049 ', '43°13&#8242;46&#8243;N 2°50&#8242;38&#8243;W 	', 'http://www.galdakao.net/4DACTION/NW_HomePageWeb/22837', 'San Juanak. Ekainaren 23an.', 'galdakao.jpg', 43.22988510131835937500, -2.84503936767578125000, 15, 'hybrid', 43.23017883300781250000, -2.84471750259399414062, 9);");
	tx.executeSql("INSERT INTO `herriak` VALUES(135, '2012-02-06 17:33:15', 'Bilbo', 2, '<p>\r\n	Bizkaiko hiriburua da, eta Euskal Herriko hiri populatuena, bertatik pasatzen da Ibaizabal ibaia. Hiru mendik inguratzen dute hiriburua: Pagasarri, Artxanda eta Arnotegik.</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	Bilboko portuak garrantzi itzela izan du lurraldearen garapenean, eta inguruko labe garaietatik ekarritako mea bertan itsasoratzen zen. Hala, Europarekin kontaktua egin zuen hiria izan da Bilbo eta horren eragina hirian nabarmena da. 80. eko hamarkadan abiatutako hiriaren erreformatzeak, Ibaizabalen errestauratzaearekin abiatuta, eta Euskalduna ontziolaren itxierarekin bultzatuta, \"Bilbo efektua\" eragin zuen eta garai bateko industriagunea egun turismora eskaintzen da, hiriaren ikur den Guggenheim Bilbao museoa harribitxi duela.</p>\r\n<p>\r\n	<br />\r\n	Ondorengo museoak aurki daitezke Bilbon: Guggenheim Bilbao Museoa, Arte Ederretako Museoa, Bilboko Itsasadarra Itsas Museoa, Arkeologia museoa, Bilboko Artelan Berreginen Museoa, Eliz Arteari buruzko museoa, Euskararen Etxea, Euskal Museoa eta Aste Santuko Irudien Museoa.</p>\r\n', '1300ko ekainaren 15a', '352.700 ', '43°15&#8242;34&#8243;N 2°55&#8242;28&#8243;W', 'http://www.bilbao.net/cs/Satellite?language=eu&pagename=Bilbaonet%2FPage%2FBIO_home', 'Bilboko Aste Nagusia, abuztuaren erdialdean.', 'bilbo.jpg', 43.26024627685546875000, -2.92685270309448242188, 14, 'hybrid', 43.25643157958984375000, -2.92326927185058593750, 10);");
	tx.executeSql("INSERT INTO `herriak` VALUES(136, '2012-02-07 15:46:56', 'Gasteiz', 1, '<p>\r\n	Arabako Lautadaren mendebaldean dagoen hiria 1980ko maiatzaren 23tik Euskal Autonomia Erkidegoko hiriburua da. Udalerriko ingurunea nahiko laua da. Hirian zenbait museo daude, besteen artean Arabako Arte Eder Museoa, Artium Museoa, Arabako Armen Museoa, Natur Zientzien Museoa eta Arabako Heraldika Museoa.</p>\r\n<p>\r\n	<br />\r\n	Hirigune historikoan hiru eliza eta katedral gotiko bat daude. Horrez gain, Sokaren Etxea ere estilo berekoa da,&nbsp; Erdi Aroko arkitektura zibilaren adibidea. Hirigune historikotik kanpo aurki daiteke besteak beste Foruen enparantza, Eduardo Txillida eskultoreak eta Peña Gantxegi arkitektoak egina.</p>\r\n', '1181', '239.592 ', '42°50&#8242;49&#8243;N 2°40&#8242;22&#8243;W', 'http://www.vitoria-gasteiz.org', 'Andre Maria Zuriaren Jaiak. Abuztuaren 4tik 9ra', 'gasteiz.jpg', 42.84671783447265625000, -2.67371654510498046875, 14, 'hybrid', 42.84665679931640625000, -2.67137765884399414062, 7);");
	tx.executeSql("INSERT INTO `herriak` VALUES(137, '2012-02-07 15:49:16', 'Agurain', 1, '<p>\r\n	Agurain Arabako ermandade historikoa da, gaur egun bakarra den arren 1567an Opaku, Alangoa, Arrizabalaga eta Egileorrek osatzen zuten. Ermandadea Gaztelako koroaren menpe zegoen erakundea zen, egungo lurralde historikoen sorrera ezarri zuen.</p>\r\n<p>\r\n	Iturrieta mendia eta Urbasako mendizerraren magalean dago Agurain. Santa Maria eliza nabarmentzen da herrian,&nbsp; XV. mendean sortutako gotiko glamigeroa. Honez gain, historiaurreko aztarnak aurki daitezke herriko hainbat gunetan eta &ldquo;Sorginetxe&rdquo; izeneko trikuharria Arrizabalaga herrixkan topatuko du bisitariak.</p>\r\n<p>\r\n	Iberiar penintsula eta Europako iparraldea lotzen dituen A-1 errepidea bertatik pasatzen da. Herria Erdi Arotik da leku estrategikoa, bertatik pasatzen zelako Done Jakue bidearen barne ibilbidea zelako.</p>\r\n', '', '4.884 ', '42°51&#8242;9&#8243;N 2°23&#8242;22&#8243;W', 'http://www.agurain.com/', 'Ekainaren 23an, Donibane egunez.\r\nAbuztuaren 15ean, Jasokundeko Andre Mariaren egunez\r\nUrriko lehen igandean, Arrosarioko Andre Mariaren egunez.', 'agurain.jpg', 42.84945297241210937500, -2.38984131813049316406, 15, 'hybrid', 42.85253524780273437500, -2.38966965675354003906, 9);");
	tx.executeSql("INSERT INTO `herriak` VALUES(138, '2012-02-07 15:50:51', 'Amurrio', 1, '<p>\r\n	Amurrion produzitzen da Arabako Txakolin gehiena, bertan baitaude bi upeltegi nagusiak. Garrantzia hori kontutan izanik &ldquo;Txakolinaren Eguna&rdquo; ospatzen dute maiatzean. Herriko festak abuztuaren 11tik 17ra bitartean ospatzen dira.</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	Abuztuaren 15ean Ama Birjinaren Jasokundearen eguna ospatzen da, abuztuaren 16an San Roke eta 17an San Anton.</p>\r\n<p>\r\n	<br />\r\n	Gasteiz eta Laudioren ostean Arabako herri populatuena da Aiaraldeko eskualdean, nerbioi ibaiaren ertzean dagoen herria. Gaur egun udalerriko ekonomia jarduera nagusiak industria, merkataritza eta zerbitzuak dira. Andra Mariaren Jasokundearen&nbsp; eliza ganga gotikoz estalita dagoen eraikuntza angeluzuzena da.</p>\r\n', '', '10.014 ', '43°3&#8242;9&#8243;N 3°0&#8242;4&#8243;W', 'http://www.amurrio.org/eu/', '', 'amurrio.jpg', 43.05233383178710937500, -3.00097870826721191406, 15, 'hybrid', 43.05274200439453125000, -3.00115036964416503906, 8);");
	tx.executeSql("INSERT INTO `herriak` VALUES(139, '2012-02-07 15:54:50', 'Oion', 1, '<p>\r\n	Arabako hego-ekialdean, Arabako Errioxa eskualdean dago kokatuta, Gasteiztik 58 kilometro ingurura eta Logroñotik 4 kilometrora.</p>\r\n<p>\r\n	<br />\r\n	Ondasun nagusien artean daude XVIII. mendean sortutako barrroko estiloko Oiongo Jasokundeko Andre Mariaren eliza, eta baita Kondeen Jauregia eta Marques del Puertoren Jauregia ere.</p>\r\n<p>\r\n	<br />\r\n	Herriko jaiak Done Bikendi eta Done Anastasiotan, urtarrilaren 21ean eta 22an ospatzen dira.</p>\r\n', '', '3.284 ', '42°30&#8242;22&#8243;N 2°26&#8242;13&#8243;W', 'http://www.ayuntamientodeoyon.com', 'Done Bikendi eta Done Anastasioren jai patronalak, urtarrilaren 21 eta 22an.', 'oion.jpg', 42.50506210327148437500, -2.43630003929138183594, 15, 'hybrid', 42.50651550292968750000, -2.43726563453674316406, 7);");
	tx.executeSql("INSERT INTO `herriak` VALUES(140, '2012-02-07 15:59:33', 'Kanpezu', 1, '<p>\r\n	Arabako ekialdean dago kokatuta. Mendi artean dago kokatuta, Kodesko mendilerroa dauka iparraldean, eta Ega ibaiak herria zeharkatzen du.</p>\r\n<p>\r\n	<br />\r\n	Herriko ondasun nagusien artean daude Antoñanako hiribildua, San Joan de Pierolako frantziskotar komentuaren hondakinak eta Satikurutze Kanpezuko Ibernalo Andre Mariaren eliza.</p>\r\n<p>\r\n	<br />\r\n	Herriko jai nagusiak Bujandan, uztaileko lehen asteburuan;&nbsp; Santikurutze Kanpezun Inauteri-arteartean, eta iraileko lehenego astebruuan; eta Urbisun ekaineko lehen asteburuan eta abuztuaren erdialdean ospatzen dira.</p>\r\n', '', '1.126', '42°40&#8242;19&#8243;N 2°20&#8242;59&#8243;W', 'http://www.campezo.org/index.php?ididioma=2', 'Iraileko lehen asteburuan', 'kanpezu.jpg', 42.69364929199218750000, -2.39634990692138671875, 17, 'hybrid', 42.69199371337890625000, -2.37017154693603515625, 7);");
	tx.executeSql("INSERT INTO `herriak` VALUES(141, '2012-02-07 16:02:08', 'Aramaio', 1, '<p>\r\n	Bizkaia eta Gipuzkoarekin muga egiten duen herria da, biekin du harreman handia bere kokalekua dela eta</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	Menditsua da eta basoz inguratuta dago herria. Arangio, Gantzaga, Orixol, Murugain, Jarindo, Izpizte eta Udalaitz mendiek inguratzen dute herria. Azken bi horien artean Besaide mendia dago, bertan dagoen monumentu batek azaltzen du Araba, Gipuzkoa eta Bizkaia probintziak elkartzen diren tokia.</p>\r\n<p>\r\n	<br />\r\n	Ezagunak dira bertako kobazuloak.</p>\r\n', '', '1.496 ', '43°3&#8242;10&#8243;N 2°33&#8242;51&#8243;W', 'http://www.aramaio.org', 'San Martinak, uztaileko lehen aste bukaeran ospatzen dira.', 'aramaio.jpg', 43.05216598510742187500, -2.56554937362670898438, 16, 'hybrid', 43.05246353149414062500, -2.56509876251220703125, 8);");
	tx.executeSql("INSERT INTO `herriak` VALUES(142, '2012-02-07 18:21:09', 'Guardia', 1, '<p>\r\n	Arabako Errioxa eskualdean dagoen udalerria da, Gasteiztik 64 kilometro ingurura. Guardiatik 500 metro ingurura, La Hoya deituriko muinoan, Burdin Aroko aztarnategi bat aurkitu zen. Horren alboan museo bat eraiki zuten eta gaur egun bisitau daiteke. Horrez gain, herriko alde zaharrean k.a. III eta II. Mendeetako aztarnak aurkitu dituzte.&nbsp; Erdi Aroko hirigintza zaharra egokien mantendu duen herria izan da, eta horretaz gain, Santiago Calatrava arkitektoak eginiko Ysios upategia dago bertan.</p>\r\n<p>\r\n	<br />\r\n	Herriko jaiak Jon Lepamoztu Deunaren egunean ospatzen dira, abuztuaren 29an.</p>\r\n', '1164', '1.546', '42°33&#8242;15&#8243;N 2°35&#8242;3&#8243;W', 'http://www.laguardia-alava.com/?idioma=eu7', '', 'guadria.jpg', 42.55347061157226562500, -2.58498811721801757812, 16, 'hybrid', 42.55468750000000000000, -2.58470916748046875000, 8);");
	tx.executeSql("INSERT INTO `herriak` VALUES(143, '2012-02-07 18:26:16', 'Bastida', 1, '<p>\r\n	Errioxa Arabar eskualdean dagoen udalerria da, Gasteiztik 45 kilometro ingurura. Udalerria orokorrean laua den arren ingurune menditsuan dago. Kantabria mendilerroaren hegoaldean dago kokatuta da, Toloño mendiaren magalean, eta Ebro ibaiaren ezkerraldean.</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	Ondasun nagusien artean daude XVI eta XVIII. mendeetan sortutako&nbsp; pizkunde eta barroko estiloko Jasokundeko Andra Mariaren eliza,&nbsp; eta antzinako harresiaren atean kokatuta dagoen Larrazuriaren Arkua.</p>\r\n<p>\r\n	<br />\r\n	Herriko jaiak abenduaren 24an ospatzen dira, Artzainen Festa deritzonan.</p>\r\n<p>\r\n	<br />\r\n	&nbsp;</p>\r\n', '1196', '1.470', '', 'http://www.labastida-bastida.org/eu', 'Erlikien jaia. Abuztuaren 6a eta 12a bitarteko lehenengo igandean. ', 'bastida.jpg', 42.58936691284179687500, -2.79492259025573730469, 15, 'hybrid', 42.59052276611328125000, -2.79320597648620605469, 8);");
	tx.executeSql("INSERT INTO `herriak` VALUES(144, '2012-02-07 18:30:19', 'Dulantzi', 1, '<p>\r\n	Arabako erdi-ekialdean, Agurain eskualdean, Gasteiztik 12 kilometro ingurura dagoen udalerria da.</p>\r\n<p>\r\n	<br />\r\n	Ondasun historikoen artean daude XVI. mendean eraikitako Dulantziko San Blasen parrokia-eliza, XVIII. mendeko Aialako Andre Mariaren baseliza, eta Kultura Ondasun izendatuta dagoen Gaonatarren etxea.</p>\r\n<p>\r\n	<br />\r\n	Herriko jaiak San Blas egunean, urtarrilaren 3an ospatzen dira; eta horietaz gainera, irailean Santa Mariaren omenezko festa ospatzen da, Tripafina izeneko pertsonaiaren jaitsierarekin.</p>\r\n', '1025', '2.803 ', '42°50&#8242;39&#8243;N 2°30&#8242;43&#8243;W', 'http://www.alegriadulantzi.com/eus/index.php', 'San Blas eguna. Urtarrilaren 3an', 'dulantzi.jpg', 42.84212112426757812500, -2.51346445083618164062, 15, 'hybrid', 42.84158706665039062500, -2.51191949844360351562, 9);");
	tx.executeSql("INSERT INTO `herriak` VALUES(145, '2012-02-07 18:36:37', 'Iruña Oka', 1, '<p>\r\n	Araba erdialdean, Arañako eskualdean, kokatuta dagoen udalerria Iruña eta Langariz Oka udalerriak batzean sortu zen, 1976an.</p>\r\n<p>\r\n	<br />\r\n	Ondasun historikoen artean daude Erromatarren garaiko aztarnategi den Iruña Veleia, Trespondeko zubia, Santa Katalinako lorategi botanikoa, eta San&nbsp; Pelaio baseliza.</p>\r\n<p>\r\n	<br />\r\n	Herriko jaiei dagokienez, Mandaita ospatzen da ekainaren 24an, San Joan egunean; Olabarri abuztuko azken asteburuan; eta Trasponde maiatzaren 26 inguruko asteburuan.</p>\r\n', '1976', '2.966 ', '42°49&#8242;02&#8243;N 2°48&#8242;41&#8243;W  ', 'http://irunadeoca.eu/', 'San Joanak. Ekainaren 24an', 'iruna-oka.jpg', 42.81713485717773437500, -2.81009531021118164062, 15, 'hybrid', 42.81897735595703125000, -2.80872201919555664062, 9);");
	tx.executeSql("INSERT INTO `herriak` VALUES(146, '2012-02-07 18:45:03', 'Eltziego', 1, '<p>\r\n	Arabar Errioxan, Arabako hego-ekialdean dago kokatuta. Mugakide ditu Eskuernaga, Navaridas, Guardia, Ceniceroeta Mañueta. Ondasun nabarmenen artean daude Eusko Jarularitzak monumento historiko-artistiko izendatutako San Andres Eliza, eta Herederos del Marques de Riscal izeneko upategi, spa eta hotela.</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	Upeltegi ugaritasunaren ondorioz, ardoaren kultura errioxa arabarrean oso garrantzitsua da eta horietako asko bisitatu daitezke kultura horren berri izateko.</p>\r\n', '', '', '', '', '', 'eltziego.jpg', 42.51489639282226562500, -2.61875987052917480469, 16, 'hybrid', 42.51513671875000000000, -2.61836290359497070312, 8);");
	tx.executeSql("INSERT INTO `herriak` VALUES(147, '2012-02-07 18:48:58', 'Legutio', 1, '<p>\r\n	Bizkaiko muga ondoan Arabako iparraldean, Zuiako eskualdean dagoen udalerria da.&nbsp; Santa Engrazia, Legutio eta Urrunagako urtegiak ditu alboan.</p>\r\n<p>\r\n	<br />\r\n	Herriko ondasun nagusien artean daude XVI-XVIII. mende artean eraikitako San Blasen parroquia, Euskal Buztingintza Museoa eta bertan dagoen Olerrieta baserriko labela, Eusko Jarularitzak mundumendu izendatu zuena.</p>\r\n', '1333', '1.045', '42°58&#8242;42&#8243;N 2°38&#8242;34&#8243;W', 'http://www.legutiano.net/euskara.asp', 'San Blas, otsailaren 3an.\r\nKofradien jaia, iraileko Ama Birjinaren egunaren hurrengo igandean.\r\n\r\n', 'legutio.jpg', 42.97896575927734375000, -2.64315319061279296875, 15, 'hybrid', 42.97851181030273437500, -2.64289569854736328125, 8);");
	tx.executeSql("INSERT INTO `herriak` VALUES(148, '2012-02-07 18:54:59', 'Trebiñu Konderria', 1, '<p>\r\n	Gasteizko hegoaldean dago kokatuta udalerri hau, Aiuda ibaiaren haranean. Mugakide ditu Gasteiz, Berantevilla, Zanbrana, Urizaharra, Lagran, Lañuko Komunitatea, Bernedo, Argantzun, Erribereagoitia eta Armiñon.</p>\r\n<p>\r\n	<br />\r\n	Bisitariak Goiz-Erdi Aroan Lañoko harkaitzean zulatuta egindako santutegiak ikus ditzake. Ermitei dagokioenez, Panguako San Formeriorena, XIII. mendeko ermita-santutegi gotortu handia aipatzekoa da.</p>\r\n', '', '1.461 ', '42°44&#8242;5&#8243;N 2°44&#8242;50&#8243;W', 'http://www.xn--condadodetrevio-crb.es/', '', 'trebino.jpg', 42.73466873168945312500, -2.74668502807617187500, 17, 'hybrid', 42.73498916625976562500, -2.74673867225646972656, 9);");
	tx.executeSql("INSERT INTO `herriak` VALUES(149, '2012-02-08 17:19:30', 'Areatza', 2, '<p>\r\n	Bizkaian dago Areatza, Arratia-Nerbioiren albo batean eta Gorbeia mendiaren magalean.</p>\r\n<p>\r\n	<br />\r\n	Udalerriko erdigunean antzinako eraikinak daude eta horregatik monumento-multzo izendatu zuten 1996. urtean. Eraikuntza garrantzitsuen artean dago XV. mende amaieran eraikitako Gortazar edo Riscal jauregia, XIX. mendean Luis Bengoecheak eraikitako Udaletxea, Aska iturri neoklasikoa, XVII. mendean eraikitako Iturrimorrokoa, eta 1730ean eraikitako Zubizarra.&nbsp; XIV. mendean eraikitako San Bartolomeren parrokia ere ikus dezake bisitariak, eta bide batez jakin, horren omenez ospatzen direla herriko jaiak abuztuaren 24an.</p>\r\n', '', '1.142', '43°07&#8242;16&#8243;N 02°46&#8242;04&#8243;W', 'http://www.areatza.net', 'San Bartolomeak, abuztuaren 24an.', 'areatza.jpg', 43.12117385864257812500, -2.77002453804016113281, 16, 'hybrid', 43.12129974365234375000, -2.76871562004089355469, 9);");
	tx.executeSql("INSERT INTO `herriak` VALUES(150, '2012-02-08 17:22:35', 'Leioa', 2, '<p>\r\n	Bizkaian dago kokatuta udalerria, Nerbioi ibaia du albo batean, eta Getxo eta Erandio ditu mugakide.</p>\r\n<p>\r\n	Udalerriko zenbait lekutan XIV-XV.&nbsp; mendeetan eraikitako dorretxeak daude. Besteak beste errenazentista estiloko bat Ondizen, eta beste bat Altzagan. Horietan barroko estiloko jauregiak aurki daitezke. Ondasun erlijiosoari dagokionean, gotiko eta barroko estiloko San Joan Batailatzearen eliza dago bertan.</p>\r\n<p>\r\n	EHUko campus nagusia bertan kokatua dago eta 11.000 ikasle baino gehiagok ikasten dute bertan.</p>\r\n', '1526', '30.454', '', 'http://www.leioa.net', '', 'leioa.jpg', 43.32484817504882812500, -2.98546171188354492188, 15, 'hybrid', 43.32628250122070312500, -2.98740363121032714844, 9);");
	tx.executeSql("INSERT INTO `herriak` VALUES(151, '2012-02-08 17:56:52', 'Basauri', 2, '<p>\r\n	Basauri Nerbioi ibaiaren albo batean kokatuta dago. Etxebarri, Galdakao, Arrigorriaga eta Zaratamo ditu mugakide.</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	Udalerriko ondare historikoaren parte dira 1501ean eraikitako San Migel Goiangeruaren eliza eta XII. mendean eraikitako gotiko estiloko Ariz familiaren dorretxea.</p>\r\n', '', '42.452', '43°14&#8242;23&#8243;N 2°53&#8242;8&#8243;W', '', 'San Migelak, irailaren 29an.\r\nSan Faustoak, urriaren 13an.', 'basauri.jpg', 43.22217559814453125000, -2.88566803932189941406, 15, 'hybrid', 43.22264480590820312500, -2.88712716102600097656, 10);");
	tx.executeSql("INSERT INTO `herriak` VALUES(152, '2012-02-08 18:04:09', 'Erandio', 2, '<p>\r\n	Erandio Bilbo ondoan dago, eta Asua eta Nerbioi ibaien alboan dago. Urduliz, Laukiz, Loiu, Berango, Lemoiz, Getxo eta Sondika ditu mugakide.</p>\r\n<p>\r\n	<br />\r\n	Ondare historiko nagusiaren artean dago 1100. urtean eraiki eta XV. mendean berritu zuten estilo gotikoko Santa Maria eliza, bertan Martiartuko jauna eta bere emaztearen hilobiak daude.</p>\r\n', '', '24.262', '43°18&#8242;17&#8243;N 2°58&#8242;23&#8243;W', 'http://www.erandioko-udala.net/', '', 'erandio.jpg', 43.30648803710937500000, -2.95199704170227050781, 15, 'hybrid', 43.30288314819335937500, -2.95066666603088378906, 9);");
	tx.executeSql("INSERT INTO `herriak` VALUES(153, '2012-02-08 18:19:54', 'Urnieta', 3, '<p>\r\n	Adarra mendiaren magalean dago Urnieta, mugakide dituela Hernani, Elduain, Andoain, eta Lasarte-Oria. Udalerriko zenbait lekutan historiaurreko aztarnak daude, besteak beste, haitzuloak, trikuharriak, eta Gipuzkoako harrespila handiena.</p>\r\n<p>\r\n	<br />\r\n	Done Jakue bidea pasatzen da bertatik, eta herritarrek diote beti pasa izan dela udalerritik Santiago de Compostelara (Galizia, Espainia) zijoan bigarren mailako bide bat. Hori dela eta, monolito bat eraiki zuten duela urte batzuk.<br />\r\n	Udalerriko eliza nagusia gotiko estiloko San Migel eliza da. Horren omenez, San Migel festak ospatzen dituzte irailaren 29an.</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	Bertan bizitu ziren Xabier Lete eta bere emazte Lourdes Iriondo musikariak.</p>\r\n', '1615', '6.145', '43°14&#8242;52&#8243;N 1°59&#8242;30&#8243;W', 'http://www.urnieta.org', 'Irailak 29. San Migelen ohoretan Herriko Jaiak', 'urnieta.jpg', 43.24779129028320312500, -1.99128901958465576172, 15, 'hybrid', 43.24807357788085937500, -1.99060237407684326172, 10);");
	tx.executeSql("INSERT INTO `herriak` VALUES(154, '2012-02-13 12:59:58', 'Muskildi', 7, '<p>\r\n	Nafarraoa Behereko mugan kokatua, Biduze ibaia eta Pagolako uhaitza igarotzen dira udalerritik, hauetara isurtzen diren hainbat errekekin batera.</p>\r\n<p>\r\n	Han dago San Antonioren omenezko kapera &ndash;Nafarroako Karlos IIak eraikitzea agindu zuena&ndash;. XIX. mendean berriztatu zen.</p>\r\n', '', '249 (2007an)', '43°11&#8242;52&#8243;N 0°57&#8242;53&#8243;W', '', '', 'muskildi-300.jpg', 43.19651794433593750000, -0.96618229150772094727, 17, 'hybrid', 43.19670867919921875000, -0.96576923131942749023, 8);");
	tx.executeSql("INSERT INTO `herriak` VALUES(155, '2012-02-13 13:18:04', 'Santa Grazi', 7, '<p>\r\n	Santa Grazi edo Urdatx ezaguna da bertan dauden Kakuetako arroila eta Done Jakue bideko eliza <span class=\"mw-redirect\">erromanikoa</span> direla eta. Artzain-herrixka sakabanatu hau xiberotarren xarma ongien gordetzen duten horietakoa da. Mendiartean 630 metrotan kokatua, bere izena III. mendean Iruñean sekulako martirioak jasan zituen Santa Graziana hispaniar noblearengandik datorkio.</p>\r\n<p>\r\n	Bestalde, Santa Grazi, historikoki, Zuberoa eta Nafarroako Erronkariren arteko pasabiderik zaharrenetakoa izan da, Santiagoko Bidearen zati izateaz gain. Horren froga, eta baita herrixkaren arima ere, 1085eko eliza erromanikoa da. Leireko Monasterioaren aginduz erromesentzako ospitale gisa eraikia, atzeko mendixken aurrean solemnitate handiko itxura islatzen du, hainbeste mendetan zehar inguru bakarti hauetako errege izan denaren solemnitatea alegia.</p>\r\n<p>\r\n	Elizaren alboan hilerri interesgarria daukagu. Bertan, elizaren ataldearen alboan, hilarri diskoidal zaharren artean landutako gurutze eder bat aurkituko dugu. Azkenik, elizaldearen ondo-ondotik Ehujarreko Arroilara eramango gaituen bidezidorra abiatzen da.</p>\r\n<p>\r\n	Inguetan kakuetako arroila bisitatzea benetan gomendagarria da.</p>\r\n', '1085', '212', '43°00&#8242;54&#8243;N 0°51&#8242;10&#8243;W', '', '', 'santa-grazi2.jpg', 43.01219177246093750000, -0.85038203001022338867, 15, 'hybrid', 42.99894714355468750000, -0.82349556684494018555, 7);");
	tx.executeSql("INSERT INTO `herriak` VALUES(177, '2012-02-24 15:46:29', 'Irisarri', 6, '<p>\r\n	Lakako erreka udalerritik igarotzen da. Baita azken honen ibaiadar batzuk ere: Elhurreko erreka, Oihanhandia erreka, Gatarriko erreka eta Larhaneko ura.</p>\r\n<p>\r\n	1607. urtean, Irisarriko jaunak, Martin de Larreak, Ospitalearen berreraikuntzari ekin zion, ospitalea Nafarroa Behereko eraikinik ederrenetarikoa bihurtuz. Otoizlekua ere berriztatua izan zen eta Donibane eliza bihurtu. Eliza sakonki eraldatua izan zen 1860an.</p>\r\n<p>\r\n	Urtero herrian egiten den ahari azokak ospe handia du</p>\r\n', '1150', '728', '43°15&#8242;29&#8243;N 1°13&#8242;57&#8243;W', '', '', 'irisarri.jpg', 43.25778961181640625000, -1.22992312908172607422, 16, 'hybrid', 43.25740432739257812500, -1.23254096508026123047, 8);");
	tx.executeSql("INSERT INTO `herriak` VALUES(158, '2012-02-13 16:25:40', 'Urepele', 6, '<p>\r\n	Aldudeko ibarrean kokatua, Baigorri-Ortzaize eskualdean. Herri menditsua da; mendirik altuena Adi (1.450 m) da. &#39;Xalbador Eguna&#39; ospatzen da bertan urtero, herrikoa zen bertsolariaren omenez.</p>\r\n<p>\r\n	Eraikin nabarmen batzuk: Inharabia, Jabola, Tanburin borda eta Xaliesenea baserriak (XVIII. mendekoak dira);<span class=\"new\"> Xalbadorenea</span> baserria; eta Ama Birjinaren Jasokundearen eliza (1841. urtekoa).</p>\r\n', '1862', '365', '43°4&#8242;15&#8243;N 1°24&#8242;54&#8243;W', '', '', 'urepel-300.jpg', 43.06982421875000000000, -1.41610431671142578125, 17, 'hybrid', 43.07053756713867187500, -1.41663002967834472656, 8);");
	tx.executeSql("INSERT INTO `herriak` VALUES(159, '2012-02-13 16:33:47', 'Izturitze', 6, '<p>\r\n	Bertan daude Izturitze eta Otsozelaiako leize famatuak. <span class=\"testua\">Hego Europako santutegi paleolitikorik garrantzitsuenetako batzuk dira, eta barruan, milioika urtetan zehar sortzen joan den eta azken 50.000 urteetan populatua izan den altxor magikoa aurkituko dugu.</span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<span class=\"testua\">Haitzuloak hiru dira: Izturitze (lehenbiziko mailan), Otsozelaia (harantzago), eta Erburua (1975ean aurkitua eta izen bereko ibaiak zeharkatzen duena). Lehenbiziko biak turismorako prestatuak daude, zubi, eskailera eta abarrekin, baina hirugarrena zientzialarientzat erreserbatua dago. Izturitzekoan, betidanik ezaguna eta populatua, antzinako gizakiek egindako animalien marrazkiak ikus ditzakegu. Otsozelaiako haitzuloan, berriz, zailagoa da pintura horietara iritsi eta ikustea, baina ordea, estalaktita-estalagmitek sortutako itxurak are ikusgarriago eta harrigarriago agertzen zaizkigu: &ldquo;Amatxi Laminen egoitzan&rdquo;, &ldquo;Buda-ren Tenplua&rdquo;, &ldquo;Pagoda txinatarra&rdquo;, &ldquo;Aita Noel&rdquo;, &ldquo;Antzoki Apaindura&rdquo;... izeneko forma harrigarri bezain ederrek aho zabalik utziko zaituztete.</span></p>\r\n', '', '364', '43°22&#8242;1&#8243;N 1°12&#8242;14&#8243;W', '', '', 'izturitze-300.jpg', 43.36650848388671875000, -1.20704317092895507812, 17, 'hybrid', 43.36668014526367187500, -1.20669984817504882812, 11);");
	tx.executeSql("INSERT INTO `herriak` VALUES(156, '2012-02-13 13:26:19', 'Larraine', 7, '<p>\r\n	Larrañe Zuberoako hego-mendebaldean dago, Basabürüan. Udalerria bera oso menditsua da; han daude Orhi (2.022m), <span class=\"new\">Belozkarre</span> (1.173m), <span class=\"new\">Salhagaina</span> (1.053m) eta Barazea (1.893m) mendiak. Bertan daude baita ere Holtzarteko arroil ezagunak.</p>\r\n', '', '209', '43°1&#8242;3&#8243;N 0°57&#8242;32&#8243;W', '', '', 'larrane2.jpg', 43.01852798461914062500, -0.95519763231277465820, 17, 'hybrid', 43.02085876464843750000, -0.95979493856430053711, 7);");
	tx.executeSql("INSERT INTO `herriak` VALUES(175, '2012-02-24 13:04:45', 'Altzürükü', 7, '<p>\r\n	Arbaila eskualdean dago eta Zuberoan kokatua dagoen arren, Nafarroa Behereko mugan dago. Altzürüküko altxor nagusienetariko bat bere ondare geologikoa da, bere eremu karstikoa hain zuzen ere. Udalerrian oraindino esploratzeke dauden leize eta kobazulo asko daude. Horietatik ezagunenetariko bat N&eacute;b&eacute;l&eacute; izenekoa da, 1972tik aurrera esploratua.</p>\r\n', '', '254 ', '', '', '', 'altzuruku2.jpg', 43.14894485473632812500, -0.93377947807312011719, 16, 'hybrid', 43.14977645874023437500, -0.93398332595825195312, 8);");
	tx.executeSql("INSERT INTO `herriak` VALUES(176, '2012-02-24 13:22:21', 'Idauze-Mendi', 7, '<p>\r\n	Uhaitzandi ibaia udalerritik igarotzen da, bertara isurtzen diren Arangorena (12,5 km) eta Uhalteberri errekekin batera.</p>\r\n<p>\r\n	Euskal Herria Zuzenean (EHZ) jaialdia 2002. urtetik 2008. urteen bitartean Mendi herrian ospatu izan da urtero.</p>\r\n', '', '260', '43°10&#8242;55&#8243;N 0°54&#8242;52&#8243;W', '', '', 'indauze-mendi5.jpg', 43.18091964721679687500, -0.91493284702301025391, 16, 'hybrid', 43.18170166015625000000, -0.91467535495758056641, 7);");
	tx.executeSql("INSERT INTO `herriak` VALUES(178, '2012-02-24 15:57:26', 'Izpura', 6, '<p>\r\n	Izpura Donibane Garazi ondoan kokatuta dagoen udalerria da.</p>\r\n<p>\r\n	Bisitariak San Lorentzo eliza, Larrea jauregia, Laustania etxea eta Faustin Bentaberri eraikina ikus litzake bertan. Irulegiko Arnoa ere ekoizten da bertan.</p>\r\n', '', '618', '43°10&#8242;19&#8243;N 1°13&#8242;59&#8243;W', '', '', 'izpuraa.jpg', 43.17052841186523437500, -1.23370134830474853516, 16, 'hybrid', 43.17065811157226562500, -1.23460793495178222656, 8);");
	tx.executeSql("INSERT INTO `herriak` VALUES(160, '2012-02-14 08:38:46', 'Zumarraga', 3, '<p>\r\n	Urola ibaiaren arroaren goiko aldean kokatua, Argixao, Beloki, Irumugarrieta, Izazpi eta Oleta mendiek inguratzen dute. Antioko ermita nabarmentzekoa da. Euskal erromanikoaren erakusgarri, Zumarragako lehen parrokia izan zen 1576an Andre Maria eliza eraiki zen arte, eta inguru honetako eraikinik zaharrenetakoa da. Uztailaren 2an Santa Isabel eguneko erromeria egiten da bertan, eta Irrintzi taldeko kideek Ezpata-dantza eskaintzen dute ermita barruan eta kanpoaldean.</p>\r\n<p>\r\n	Abenduaren 13an, bestalde, Santa Lutziko feria egiten da Zumarragan eta Urretxun.</p>\r\n', '1661', '10.324', '43°4&#8242;N 2°19&#8242;W', '', '', 'zumarraga-300.jpg', 43.08781051635742187500, -2.31326293945312500000, 15, 'hybrid', 43.08875274658203125000, -2.31369209289550781250, 10);");
	tx.executeSql("INSERT INTO `herriak` VALUES(161, '2012-02-14 15:06:12', 'Urretxu', 3, '<p>\r\n	Urola Garaian kokatua, Irimo mendiaren magalean. Urola ibaiak banatzen ditu Urretxu eta Zumarraga. Eraikin nabarmenetako batzuk dira: San Martin Tours-koaren eliza (XVI. mendekoa); Corral-Ipeñarrieta jauregia (XVII. mendekoa), gaur egungo udaletxea dena;<b> </b>Areitzaga jauregia (XVII. mendekoa); Ipeñarreta jauregia (XVII. mendekoa), Irimo mendiko magalean kokatua; eta handik metro gutxira,<b> </b>Santa Barbarako ermita (XV. mendekoa). Baselizari itsatsita aterpetxe-taberna bat dago.</p>\r\n<p>\r\n	Herriko festak (Santa Anastasiak) irailaren 20aren bueltan ospatzen dira. Abenduaren 13an, bestalde, Santa Lutziko feria egiten da Urretxun eta Zumarragan.</p>\r\n', '1383', '6.957', '43°5&#8242;N 2°18&#8242;W', '', '', 'urretxu-300.jpg', 43.09257888793945312500, -2.31335258483886718750, 16, 'hybrid', 43.09222030639648437500, -2.31618499755859375000, 9);");
	tx.executeSql("INSERT INTO `herriak` VALUES(162, '2012-02-14 15:13:05', 'Legazpi', 3, '<p>\r\n	Urola Garaian kokatua, Aizkorri mendiaren magalean, 450 metroko garaieran. Herri industriala da, batez ere burdinaren eraldaketari lotua. <span class=\"new\">Lenbur</span> (Legazpi, Natura eta Burdina) egitasmoa daramate aurrera 1994az geroztik, natura baliabideak eta burdinaren inguruko kultura babestu eta erabiltzeko asmoz. Telleriarte auzoan, Legazpiko erdigunetik 3 kilometrora, Mirandaolako burdinola dago.</p>\r\n', '1608', '8.684 (2011)', '43°3&#8242;18&#8243;N 2°20&#8242;6&#8243;W', '', '', 'legazpi-300.jpg', 43.05435943603515625000, -2.33364081382751464844, 15, 'hybrid', 43.05527114868164062500, -2.33376955986022949219, 8);");
	tx.executeSql("INSERT INTO `herriak` VALUES(163, '2012-02-14 15:25:39', 'Ordizia', 3, '<p>\r\n	Gipuzkoa erdi-hegoaldean kokatua, Goierri eskualdeko hiriburua da. Gipuzkoako biztanle dentsitaterik handienetarikoa du eta Oria ibaiak zeharkatzen du. Udalerria inguratzen duten mendiak: Murumendi (848 m), Odeta-Goeña (784 m) eta Paola (734 m).</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	Suteen, gerren eta herriaren beraren aurrerapenaren ondorioz, eraldakuntza handia jasan du. Halere, oraindik ere <span class=\"mw-redirect\">Erdi Aroko</span> tipologia gordetzen du neurri handi batean, bide-trazadurari eta lurzatiketari dagokienez. Eraikin aipagarrienetako bat Jasokundeko Andre Mariaren eliza da. XIII. edo XIV. mendeetan sortua, egungo eliza XVI. mendekoa da eta dorrea barrokoa.</p>\r\n', '1256', '9.811 (2011)', '43°03&#8242;14&#8243;N 2°10&#8242;46&#8243;W', '', '', 'ordizia-300.jpg', 43.05452728271484375000, -2.17727875709533691406, 16, 'hybrid', 43.05422973632812500000, -2.17708563804626464844, 8);");
	tx.executeSql("INSERT INTO `herriak` VALUES(164, '2012-02-14 15:31:29', 'Astigarraga', 3, '<p>\r\n	Buruntzaldea eskualdean kokatua, Urumea ibaiak zeharkatzen du. Betidanik sagardoari loturik egon da.</p>\r\n<p>\r\n	Badira han 20 sagardotegi baino gehiago: Akelenea, Artola, Alorrenea, Arrizabalaga, Astarbe, Bereziartua, Buenabentura, Etxeberria, Gartziategi, Gurutzeta, Irigoien, Larrarte, Lizeaga, Mendizabal, Mina, Oialume Zar, Oiarbide, Petritegi, Rezola, Sarasola eta Zapiain.</p>\r\n', '', '4.709 (2010)', '43°16&#8242;59&#8243;N 1°56&#8242;46&#8243;W', '', '', 'asti3.jpg', 43.28177261352539062500, -1.94706642627716064453, 16, 'hybrid', 43.28146362304687500000, -1.94820368289947509766, 10);");
	tx.executeSql("INSERT INTO `herriak` VALUES(165, '2012-02-14 15:47:03', 'Villabona', 3, '<p>\r\n	Gipuzkoako ekialdean kokatua, Tolosaldea eskualdean, Oria ibaiaren ibarrean. Eraikin aipagarrienetako bat Amasa auzoan dagoen San Martin parrokia-eliza da, XVI. eta <span class=\"mw-redirect\">XVIII. mendeen</span> artean eraikitakoa. Herriaren erdialdean daude, bestalde, udaletxea (<span class=\"mw-redirect\">XIX. mendekoa da eraikina) eta Jesusen Bihotza parrokia-eliza.</span></p>\r\n', '1615', '5.822 (2010)', '43°11&#8242;19&#8243;N 2°3&#8242;13&#8243;W', '', '', 'villabona-300.jpg', 43.18969345092773437500, -2.05232477188110351562, 15, 'hybrid', 43.19028854370117187500, -2.05198144912719726562, 10);");
	tx.executeSql("INSERT INTO `herriak` VALUES(166, '2012-02-15 13:40:32', 'Izaba', 5, '<p>\r\n	Nafarroako ipar-ekialdean kokatua,&nbsp;<span class=\"mw-redirect\">Erronkariko </span>herririk populatuena da. Pirinioetako lehen bi mila metroak igotzeko &ldquo;Oinarrizko Kanpamendutzat&rdquo; kalifika genezake. Izan ere, mendizale askok eta askok herri hori aukeratzen dute inguruko gailurretara iristeko: <span class=\"mw-redirect\">Lakora</span> (1.877m), Txamantxoia (1.941m), Hiru Erregeen Mahaia (2.426m)...</p>\r\n<p>\r\n	Iparralderantz eginda Belagua mendatea dago. Izaba eta Santa Grazi (Zuberoa) lotzen ditu mendate horrek. San Martingo Harria dago han, Auñamendi (frantsesez Anie; 2.507 metro), Arlas (2.044 m) eta <span class=\"mw-redirect\">Lakora</span> (1.877 m) mendietarako abiapuntua.</p>\r\n<p>\r\n	Neguan ipar eskia egiteko aukera ematen du, eta urte osoan mendira joateko aukera franko.</p>\r\n<p>\r\n	Jai egunetan herrian anitz jende biltzen da eta animazio ugari egoten da. Lau ezpa bortz jatetxe eta hainbat taberna daude. Eta ostatzeko aukera anitz ere bai.</p>\r\n', '', '492 (2010)', '42°51&#8242;0&#8243;N 0°55&#8242;0&#8243;W', '', '', 'izaba-300.jpg', 42.86113739013671875000, -0.92364490032196044922, 17, 'hybrid', 42.86079788208007812500, -0.92434227466583251953, 8);");
	tx.executeSql("INSERT INTO `herriak` VALUES(167, '2012-02-15 13:48:03', 'Otsagabia', 5, '<p>\r\n	Nafarroako ipar-ekialdean kokatua,&nbsp;Zaraitzu ibarreko herririk populatuena da. Muskilda santutegia herriko gain batean dago. Iparraldean Abodiko mendilerroa dauka, herria eta <span class=\"mw-redirect\">Iratiko oihana</span> banatzen dituena.</p>\r\n', '', '626 (2010)', '42°54&#8242;24&#8243;N 1°5&#8242;21&#8243;W', 'http://www.ochagavia.com/index.php?lang=eu_ES', 'Irailaren 8an, Herriko Jaiak.\r\nIrailaren 17an, Muskildako Andre Maria.\r\n', 'otsagabia-300.jpg', 42.90571975708007812500, -1.08997523784637451172, 17, 'hybrid', 42.90443038940429687500, -1.08892381191253662109, 7);");
	tx.executeSql("INSERT INTO `herriak` VALUES(168, '2012-02-15 13:56:17', 'Burgi', 5, '<p>\r\n	Nafarroako ipar-ekialdean kokatua, <span class=\"mw-redirect\">Erronkariko ibarrean,</span> Eska eta Binies ibaiek bat egiten duten lekuan. Han ospatzen da urtero jende mordoa biltzen duen <a href=\"http://www.turismo.navarra.es/eus/organice-viaje/recurso.aspx?o=3070\&\" target=\"_blank\" title=\"Lotura leiho berrian\">Almadiaren Eguna</a>.</p>\r\n', '', '235 (2010)', '42°43&#8242;16&#8243;N 1°0&#8242;3&#8243;W', '', '', 'burgi.jpg', 42.71993255615234375000, -1.00354588031768798828, 17, 'hybrid', 42.72035217285156250000, -1.00521957874298095703, 9);");
	tx.executeSql("INSERT INTO `herriak` VALUES(169, '2012-02-15 14:03:07', 'Erronkari', 5, '<p>\r\n	I<span class=\"mw-redirect\">zen bereko ibarreko</span> herri nagusia da. <span class=\"mw-redirect\">Eska</span> ibaiak herria zeharkatzen du. Bere harrizko landa-etxeek Pirinio estiloko oso herri polita osatzen dute. XVII-XVIII. mendeko jauretxeen artetik Gambra, Lopez eta Sanz aipatuko ditugu. Monumentuei dagokienez, XVI. mendeko San Esteban eliza nabarmentzen zaigu, gurutze latinoko planta eta XVII. mendeko erretaula landua dituela.</p>\r\n', '', '258 (2009)', '42°48&#8242;31&#8243;N 1°57&#8242;19&#8243;W', '', '', 'erronkari-300.jpg', 42.80900955200195312500, -0.95586413145065307617, 17, 'hybrid', 42.80833435058593750000, -0.95623964071273803711, 7);");
	tx.executeSql("INSERT INTO `herriak` VALUES(170, '2012-02-15 15:11:44', 'Artziniega', 1, '<p>\r\n	Arabako ipar-mendebaldean kokatua, Aiaraldea eskualdean. Artziniegako Erdi Aroko hiribildua monumentu-multzo izendatu du Eusko Jaurlaritzak. Eraikin aipagarrienetako batzuk: Zabalgoitia hotela, 1906an Juan Zabalgoitia indianoak eraikia, eta Eusko Jaurlaritzak monumentu izendatua; Museo Etnografikoa; eta Ortiz Molinillo de Velascotarren dorretxea, XVI. mendekoa.</p>\r\n', '1272', '1.764 (2007)', '43°7&#8242;16&#8243;N 3°7&#8242;41&#8243;W', 'http://www.artziniegakoudala.com/index.php?option=com_k2&view=item&layout=item&id=41&Itemid=26&lang=eu', 'Irailaren 8an Arteko Andra Mariren ohorezko jaiak ospatzen dira. Erdi Aroko azoka irailaren lehen igandean egiten da, jaiekin batera.', 'artziniega-300.jpg', 43.12131500244140625000, -3.13032269477844238281, 16, 'hybrid', 43.12201309204101562500, -3.13026905059814453125, 8);");
	tx.executeSql("INSERT INTO `herriak` VALUES(171, '2012-02-15 15:26:36', 'Laudio', 1, '<p>\r\n	Arabako ipar-mendebaldean kokatua, Aiaraldea eskualdean, Nerbioi ibarrean. Industriagune garrantzitsua da, eta Arabako bigarren herririk populatuena da, Gasteizen ondoren. Bertako eraikin aipagarrienetako batzuk dira: Ugarte dorretxea, <span class=\"mw-redirect\">XV. mendekoa</span>, antzinean Laudion zeuden hamar dorreen artean zutik dirauen bakarra; Ermuko Andre Mariaren santutegi gotikoa; Katuxa jauregi barrokoa; Santa Luziaren baseliza, XV. mendekoa; eta Etxebarri baserria, XVII. mendekoa. Azken hau EAEko Kultura Ondarearen Zerrenda Orokorrean sartu zuten, kultura ondasun gisa, <i>monumentu</i> izendapenaz.</p>\r\n', 'X. mendean edo lehenago', '18.494 (2011)', '43°9&#8242;N 2°57&#8242;W', 'http://www.laudiokoudala.net/general/turismo_e.shtml', 'Abuztuaren 15ean San Rokeko jaiak ospatzen dira.\r\n\r\nDolumin ostirala: Aste Santuaren aurreko ostiralean ospatzen da. Abereen feria garrantzitsua egiten da.\r\n', 'laudio-300.jpg', 43.14241027832031250000, -2.96075296401977539062, 15, 'hybrid', 43.14248657226562500000, -2.96113920211791992188, 9);");
	tx.executeSql("INSERT INTO `herriak` VALUES(172, '2012-02-15 15:41:31', 'Zuia', 1, '<p>\r\n	Araba iparraldean kokatua, Zuia-Gorbeialdea eskualdeko udalerririk handiena da. Gorbeiaren magalean dago, eta hura igotzeko abiapuntua da. Zuiako udalerria 13 herrik osatzen dute: Murgia, Sarria, Bitoriano, Ametzaga, Markina, Domaikia,<span class=\"mw-redirect\"> Gilierna</span>, Lukiano, Jugo, Aperregi, Zarate, Altube eta Ziorraga.</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	Eraikin aipagarrienetako batzuk:<b><span class=\"mw-redirect\"> </span></b><span class=\"mw-redirect\">Oroko Santutegia</span> (eraikin nagusia <span class=\"mw-redirect\">XVI. mendekoa</span> da); Jugatxi baseliza; Eztiaren museoa, Murgiko Oregi etxean dagoena; eta Bitoriano herriko 17. zenbakidun baserria, <span class=\"mw-redirect\">XVII. mendekoa</span>, Eusko Jaurlaritzak monumentu gisa izendatua.</p>\r\n', '', '2.401 (2010)', '42°58&#8242;54&#8243;N 2°50&#8242;38&#8243;W', 'http://www.zuia.com/turismo.php', '', 'zuia-300.jpg', 42.95662307739257812500, -2.81919193267822265625, 15, 'hybrid', 42.98417663574218750000, -2.83370041847229003906, 8);");
	tx.executeSql("INSERT INTO `herriak` VALUES(173, '2012-02-15 15:51:46', 'Aiara', 1, '<p>\r\n	Arabako ipar-mendebaldean kokatua. Udalerriak Arespalditza du herriburu eta beste 22 herriz ere osatua dago; populatuena Luiaondo da. Aiaraldeko eskualdea osatzen du Amurrio, Artziniega, Okondo eta Laudio udalerriekin batera.</p>\r\n', '', '2.880 (2011)', '43°5&#8242;0&#8243;N 3°3&#8242;47&#8243;W', 'http://www.aiarakoudala.com/index.php?option=com_k2&view=itemlist&layout=category&task=category&id=11&Itemid=2&lang=eu', '', 'aiara.jpg', 43.07816314697265625000, -3.04378652572631835938, 15, 'hybrid', 43.08408737182617187500, -3.06700372695922851562, 11);");
	tx.executeSql("INSERT INTO `herriak` VALUES(179, '2012-03-18 17:45:07', 'Erriberri', 5, '<p>\r\n	Erromatarren garaitik aurkitutako aztarnen arabera, kristo ondorengo bigarren mendean, Erriberriren herrigune aitzindaria, erromatarren defentsarako gotorleku sarearen guneetako bat zen. Erriberriri (Oligicus) buruzko lehen aipamena 621, urtekoa da, Suintila izeneko errege bisigodoak herria baskoiengandik babesteko. Behe Erdi Aroan, herriak garapen handia izan zuen, Nafarroako erregeengandik jasotako babesa eta bultzadagatik. XV. mendean Carlos III.a Noblea eta Trastamarako Leonorrek, Erriberriko Nafarroako Erregeen Jauregiaren eraikuntza hasi zuten, garai hartako oparotasunaren adierazgarri.</p>\r\n<p>\r\n	1407an Carlos III.a erregeak Erriberriko merindadea sortu zuen, Erriberri hiriburu izanik. Herriaren garapena eten egin zen, Gaztelaren inbasioaren ondorioz eta herriaren gainbeherak XIX. mendea arte iraun zuen.</p>\r\n', '', '3.650', '42°29&#8242;15&#8243;N 1°39&#8242;45&#8243;W', 'http://www.olite.es/es/', 'Erriberriko jaiak irailaren 13tik irailaren 19ra ospatzen dira, Gurutze Santuaren omenez, nahiz eta herriko patroiak ekainaren 29an eta abenduaren 8an ospatzen diren.\r\n\r\nUrtero, Abuztuko asteburu batean Erdi Aroko merkatua antolatzen da.\r\n\r\nBestalde, maiatzaren 22tik hurbilen dagoen larunbatean Santa Brigida baselizarako erromeria egiten da.', 'erriberri.jpg', 42.48094177246093750000, -1.65013945102691650391, 16, 'hybrid', 42.48217391967773437500, -1.65177023410797119141, 8);");
	tx.executeSql("INSERT INTO `herriak` VALUES(180, '2012-03-20 22:42:56', 'Goizueta', 5, '<p>\r\n	Goizueta Nafarroa ipar-mendebaldeko udalerria da. Iruñeko merindadean dago eta 2009ko urtarrilaren 1eko erroldan, herriak 780 biztanle zituen.</p>\r\n', '1186', '780', '43°10&#8242;16&#8243;N 1°51&#8242;51&#8243;W', 'http://www.goizueta.es', 'Abuztuak 14tik 18ra (Amabirjiñak)', 'goizueta1.jpg', 43.17233276367187500000, -1.86361563205718994141, 16, 'hybrid', 43.17105102539062500000, -1.86421644687652587891, 8);");
	tx.executeSql("INSERT INTO `herriak` VALUES(181, '2012-03-21 10:59:13', 'Legorreta', 3, '<p>\r\n	Legorreta Gipuzkoako erdialdean eta Goierriko eskualdean dagoen udalerri euskalduna da.</p>\r\n<p>\r\n	<br />\r\n	Iparraldean Albiztur eta Tolosa, ekialdean Ikaztegieta, Orendain eta Baliarrain, hegoaldean Altzaga eta mendebaldean Itsasondo udalerriak mugakide ditu.</p>\r\n<p>\r\n	<br />\r\n	Oria ibaiak udalerria zeharkatzen du, eta horrekin bat egiten dute Zubiña, Zazpiturrieta eta Aldabe ibaiadarrek.</p>\r\n', '1605', '1400 inguru', '43°5&#8242;5&#8243;N 2°8&#8242;51&#8243;W', '', 'Azintzio Jaiak', 'legorreta3.jpg', 43.08493041992187500000, -2.14822721481323242188, 16, 'hybrid', 43.08579254150390625000, -2.15090942382812500000, 8);");
	tx.executeSql("INSERT INTO `herriak` VALUES(182, '2012-03-21 12:09:57', 'Eskoriatza', 3, '<p>\r\n	Eskoriatza Gipuzkoako hego-mendebaldean dagoen udalerria da, Debagoiena eskualdean. 40.41 km&sup2;-ko azalera du.<br />\r\n	<br />\r\n	Eskoriatzak badu zer eskaini herrira hurbiltzen den bisitariari. Izan ere, herri gunean bertan ikusi daitezkeen zenbait eraikuntza interesgarriez gain, baditugu inguruetan antzinako aztarna garrantzitsuak ere (Asuntzetako tumuluak, Mugarriluzeko iruinarria, ...).</p>\r\n<p>\r\n	Horri guztiari inguruko paraje eta auzoen edertasuna gehituz gero, ezin ukatu bisitatzea merezi duen herria dela Eskoriatza.</p>\r\n', '1630', '4043', '43°1&#8242;0&#8243;N 2°31&#8242;38&#8243;W', 'www.eskoriatza.net', 'San Pedroak', 'eskoriatza-argazki-orokorra_menusize.jpg', 43.01685333251953125000, -2.52672266960144042969, 15, 'hybrid', 43.01707458496093750000, -2.52698016166687011719, 8);");
	tx.executeSql("INSERT INTO `herriak` VALUES(183, '2012-03-23 10:17:38', 'Pitillas', 5, '<p>\r\n	Nafarroako udalerria da. Erriberriko merindadeko herri honek 556 biztanle zituen 2009ko urtarrilaren 1eko erroldaren arabera. Nafarroako hiriburutik 50 kilometrora dago.</p>\r\n<p>\r\n	Pitillasek Uxue eta Beirerekin egiten du muga iparraldean, Uxuerekin ekialdean, Erriberrirekin mendebaldean, eta Santakara eta Murillo el Cuenderekin hegoaldean.<br />\r\n	<br />\r\n	Pitillasko jaiak San Erramun Jaiogabearen omenez ospatzen dira, abuztuaren 26tik 31ra. Ospakizunak ardoaren inguruan daude antolaturik. Horrez gain, maiatzeko hirugarren igandean, Uraren jaia ospatzen da; eta ekainaren 29an, San Petriren omenezko jaia.<br />\r\n	<br />\r\n	Uxuerako erromeria San Marko egunaren hurrengo igandean izaten da, apirilaren 25aren inguruan. San Domingoren omenezko erromeria maiatzeko bigarren larunbatean izaten da.</p>\r\n', '', '', '42°25&#8242;19&#8243;N 1°37&#8242;20&#8243;O', '', 'Abuztuaren 26-31', '11255poblacion_28.jpg', 42.42165374755859375000, -1.62190854549407958984, 16, 'hybrid', 42.42200088500976562500, -1.62233769893646240234, 8);");
	tx.executeSql("INSERT INTO `herriak` VALUES(184, '2012-03-23 13:12:53', 'Zerain', 3, '<p>\r\n	Zerain Gipuzkoako hegoaldean eta Goierriko eskualdean dagoen udalerri euskalduna da. Landa-turismoan aitzindaria izan da.<br />\r\n	<br />\r\n	Aizkorri-Aratz natura parkearen barnean dago Gatzaga, Eskoriatza, Aretxabaleta, Oñati, Legazpi eta Zegamarekin batera.<br />\r\n	<br />\r\n	Abuztuaren 15ean da Zeraingo elizako zaindari den Jasokundeko Andra Mariren eguna. Festak aurreko egunean hasten dira eta hiru-lau egunez irauten dute. Abuztuaren 16an, San Roke egunarekin amaitzen dira.</p>\r\n', '1615', '244', '43°0&#8242;45&#8243;N 2°16&#8242;24.7&#8243;W', 'http://www.zerain.com/', 'Otsailaren 3an ospatzen da San Blas eguna.\r\nAbuztuaren 14tik 16ra herriko festak.', 'zerain_guipuzcoa3.jpg', 43.01200866699218750000, -2.27154278755187988281, 16, 'hybrid', 43.01172637939453125000, -2.27160716056823730469, 8);");
	tx.executeSql("INSERT INTO `herriak` VALUES(185, '2012-03-23 14:00:46', 'Zaldibia', 3, '<p>\r\n	Zaldibia Goierriko eskualdean dagoen herri euskalduna da. Aralarko mendilerroa bikain ikus diateke bertatik, herriko mugakide naturala baita.</p>\r\n', '1615', '1.504', '43°2&#8242;11&#8243;N 2°8&#8242;59&#8243;W', 'http://www.zaldibia.net/', 'Urriaren 6an ospatzen dira herriko festak Udazkeneko lehen ardiak egun hauetan hiltzen dituzte, eta hauen segoa porru eta arrautzekin nahastuz ‘Zaldibiako Mondejua’ egiten da. ', 'zaldibiako_saturdiren_baseliza.jpg', 43.03782272338867187500, -2.15035772323608398438, 16, 'hybrid', 43.03840255737304687500, -2.15055084228515625000, 10);");
	tx.executeSql("INSERT INTO `herriak` VALUES(186, '2012-03-23 23:40:04', 'Arano', 5, '<p>\r\n	Arano Nafarroa ipar-mendebaldeko udalerri bat da, Gipuzkoarekin muga-mugan dago. Naturaz inguratuta dago erabat.</p>\r\n', '1630', '134', '43°11&#8242;59&#8243;N 1°53&#8242;43&#8243;W', '', 'Uztailak 25 inguruan (Santioak)', '220px-arano.JPG', 43.19978332519531250000, -1.89465188980102539062, 17, 'hybrid', 43.19972229003906250000, -1.89553165435791015625, 8);");
	tx.executeSql("INSERT INTO `herriak` VALUES(189, '2012-03-26 14:21:31', 'Alegia', 3, '<p>\r\n	Alegia Gipuzkoako Tolosaldea eskualdean, Oria ibaiaren ertzean kokaturik dagoen udalerria da.</p>\r\n<p>\r\n	<br />\r\n	Kokapenak eta komunikazio-sistemak eskualde txiki baten erdigune bihurtzen dute Alegia, RENFEren geltokia hor baitago, Donostia - Gasteiz autobidea ondoan du eta bertatik igarotzen dira Altzo, Amezketa, Ugarte, Bedaio, Orendain, Abaltzisketa, Larraitz eta Aldabara doazen errepideak.</p>\r\n', '', '1779', '43°5&#8242;54&#8243;N 2°5&#8242;44&#8243;W', 'http://alegia.eu', 'Uztailak 16', 'alegia.jpg', 43.10075759887695312500, -2.09667921066284179688, 16, 'hybrid', 43.10006713867187500000, -2.09642171859741210938, 8);");
	tx.executeSql("INSERT INTO `herriak` VALUES(188, '2012-03-26 13:48:49', 'Algorta', 2, '<p>\r\n	Algorta Bizkaiko kostaldean kokatuta dagoen Getxoko auzoa da. Getxo osatzen duten auzo guztietatik populatuena da.</p>\r\n<p>\r\n	Hondartza bi ditu: Ereaga eta Arrigunaga.</p>\r\n<p>\r\n	Algortako Portu Zaharra Algortaren jatorria den auzoa da. Tradizionalki arrantzale-auzoa izan da hau, Elexalde auzoa, kontraz, baserritar-auzoa izan delarik. XVIII. mendean sortu zen eta maldan gora hedatu zen heinean, Algorta eratu zen.<br />\r\n	<br />\r\n	Gaur egun jada, arrantzarik ez bada egiten ere, arrantzale auzo kutsu handia du oraindik, etxe zuri eta kale estu eta malkartsuekin. Portuan bertan, txalupa txiki batzuk baino ez dira geratzen.<br />\r\n	<br />\r\n	Hiru kaleren inguruan eraikitako auzoa da: Kale Berria, Erribera eta Portu Zaharra. Aretxondo eta Karitate kaleek besteak beste lotzen dute auzoa Algortako erdigunearekin.<br />\r\n	<br />\r\n	Asteburuetan getxoztar askoren elkargune izan ohi dira bertako taberna eta jatetxeak, hori dela eta, asteburuetan bizitasun handia du auzoak.</p>\r\n', '1075', '40000', '43°21&#8242;10&#8243;N 03°00&#8242;42&#8243;O', 'www.getxo.net', 'San Ignazio, Portu Zaharra', 'algorta.jpg', 43.35033416748046875000, -3.01003932952880859375, 15, 'hybrid', 43.27320480346679687500, -2.88940429687500000000, 8);");
	tx.executeSql("INSERT INTO `herriak` VALUES(190, '2012-03-26 14:28:47', 'Bedaio', 3, '<p>\r\n	Bedaio, Balerdi (Aralar) mendiaren magalean dagoen Gipuzkoako herria da. Herri txikia izanik, egun, administratiboki Tolosako Udalak kudeatzen du, nahiz eta geografikoki honekin mugarik ez egin. Aralar parke naturalaren sarrera puntua ere bada. Nafarroarekin muga egiten du.</p>\r\n', '', '100', '43°03&#8242;00&#8243;N 2°2&#8242;25&#8243;W', 'http://bedaio.com', 'Irailak 8', 'bedaio.jpg', 43.04996490478515625000, -2.04016685485839843750, 16, 'hybrid', 43.05018615722656250000, -2.04025268554687500000, 8);");
	tx.executeSql("INSERT INTO `herriak` VALUES(191, '2012-03-30 00:17:55', 'Aulesti', 2, '<p>\r\n	Lea Ibarra osatzen duten udalerrietan handiena eta populatuena dugu Aulesti. Erdi Aroko Markina-Lekeitio bidearen erdialdean dago, eta Iluntzar eta Bedartzandi mendiek mugatzen dute herrigunea. Ez du Hiri izaerarik, modu horretan taxututako herria dugun arren. Aulesti elizatea da berez, egitura estu eta luzeko herrigune gotikoa da kontzeptuz, baina hiri balio handiena XVI. mendetik aurrera lortu zuen, elizak eta jauregiek herriko plaza itxuratu zutenean eta udaletxea eraiki zutenean. Herriguneak eta zazpi auzo nagusik osatzen dute udalerria. Hauexek dira auzoak: Goierri, Ibarrola, Narea, Malats, San Anton, Urriola eta Zubero.</p>\r\n', '', '659', '', 'www.aulesti.net', 'San Juanak (Ekainak 24) eta Santa Eufemiak (Irailak 16)', 'aulesti.jpg', 43.29689788818359375000, -2.56221795082092285156, 16, 'hybrid', 43.29608535766601562500, -2.56335520744323730469, 8);");
	tx.executeSql("INSERT INTO `herriak` VALUES(192, '2012-04-01 17:03:16', 'Elantxobe', 2, '<p>\r\n	Kale-harrizko Ogoño izeneko mutur ikaragarriaz babesturik, ekialdean, itsaslabar zakarrek kikildua, Elantxobeko arrantz portu berezia ageri da bisitarien aurrean. 1524. urtean zegoen arrantzale kopuru txiki bat izan zen Elantxoberen jatorria, ondoren inguruko herrietako gizonekin handituko zuen kopuru hori.<br />\r\n	<br />\r\n	Bere izenak, Elantxobe, baserri-gune bat du jatorritzat, hain zuzen ere, Elantxo, Ibarrangeluako barrutian eta bere izena emango zion herritik hurbil kokaturikoak: Elantxobe-behe\".<br />\r\n	<br />\r\n	Itsasora zabalik zegoen portua zeukanez gero, 1783. urtean, iadanik kofradia sortuta zegoela, Kantauri itsasoaren erasoei aurre egiteko portuan zenbait kai eraiki ziren, urteetan zehar gotorlekuak izango zirenak.<br />\r\n	<br />\r\n	1833. urtean Ibarrangeluko udalerritik desanexionatu egin zen eta 1854. urtean, Gernikako Batzar Nagusietako partaide egin zen, 113 zenbakidun jarlekuaz eta botuaz. Juan Delmaek honela kalifikatuko zuen Elantxobe: \"bitxiena, bere egiturarengatik, bera bezalakoa, bertako baserrien kokagunearengatik, eta harrigarria, inguratzen duen natura ikusgarriarengatik\".<br />\r\n	<br />\r\n	Bere herrigunea kokatuta dagoeneko aldats izugarria dela eta, Elantxobek bertara sartzeko hiru errepide ditu, sarreran gurutzatzen direnak. Horietariko bat porturaino doa, beste bat talaiaraino, portu-mailatik 81 m-tara eta hirugarrena Bermokizeraino doa.<br />\r\n	<br />\r\n	Kale estuen artean itsasoari begira zein baino gorago kokatuta dauden etxeek itsasoari atxikitako egunerokotasun leiala erakusten dute.</p>\r\n', '1833', '411', '43°24&#8242;14&#8243;N 2°38&#8242;19&#8243;W', 'www.elantxobe.eu', 'Ekainak 29. S.Pedro', 'elantxobe.jpg', 43.40356445312500000000, -2.63872742652893066406, 16, 'hybrid', 43.40304946899414062500, -2.63949990272521972656, 9);");
	tx.executeSql("INSERT INTO `herriak` VALUES(193, '2012-04-03 10:26:51', 'Antzuola', 3, '<p>\r\n	Gipuzkoako udalerria (Debagoiena), Deskarga mendatearen magalean, Bergara eta Zumarraga artean dago. 27,72 km&sup2;-ko azalera du eta 2008an 2.145 biztanle zituen (antzuolarrak).<br />\r\n	<br />\r\n	1629an Bergaratik banandu zen berrehun urte baino gehiago elkarrekin egon ondoren. Bestela, 1984an herriko talde batek Aiherra (Nafarroa Beherea) udalerriarekin senidetze harremana finkatu zuen.<br />\r\n	<br />\r\n	Mugak: iparraldean Azkoitia eta Bergara, hegoaldean Oñati eta Legazpi, ekialdean Urretxu eta Zumarraga eta mendebaldean Bergara.<br />\r\n	<br />\r\n	Industria da udalerriko ekonomia jarduera nagusia abeltzaintzaren eta nekazaritzaren gainetik.</p>\r\n', '1629', '2100', '43°5&#8242;20&#8243;N 2°22&#8242;52&#8243;W', 'http://www.antzuola.com', 'Uztaileko hirugarren aste bukaeran', 'antzuola.jpg', 43.09766387939453125000, -2.37995624542236328125, 15, 'hybrid', 43.09785079956054687500, -2.38042831420898437500, 8);");
	tx.executeSql("INSERT INTO `herriak` VALUES(194, '2012-04-03 16:32:33', 'Ezkio - Itsaso', 3, '<p>\r\n	Ezkio-Itsaso Gipuzkoako hegoaldean dago. Beasain, Ormaiztegi, Gabiria, Azpeiti eta Zumarraga ditu mugatzat. Zumarraga eta Beasain bitartean dago GI-632 errepidearen alboan.<br />\r\n	<br />\r\n	Herri honek bi hirigune ditu, Ezkio alde batetik, eta Itsaso, bestetik. Santa Lutzia ibaiak zeharkatzen du herria, 3 kilometrotan zehar.<br />\r\n	<br />\r\n	Ezkio-Itsasok 21 km2ko zabalera du eta itsasoaren mailatik 300 metrotik gora dago.<br />\r\n	<br />\r\n	Herriko punturik altuena 965 metroko Izazpi mendia da. Klima ozeanikoa du herriak, uda fresko eta negu epelak izaten dituelarik.<br />\r\n	<br />\r\n	Ezkio- Itsaso 1965.urtean jaio zen Ezkio eta Itsasoren bateratzearen ondorioz. Gipuzkoako bihotzean kokaturiko herri txiki da.<br />\r\n	<br />\r\n	Historikoki baserriz osaturiko herria izan da, beraz lehen sektorean oinarritu izan da Ezkio-Itsasoren ekonomi eremua.</p>\r\n', '1965', '608', '43°4&#8242;55&#8243;N 2°16&#8242;27&#8243;W', 'www.ezkio-itsaso.net', 'Maiatzak 8 eta Abuztuak 24', 'ezkio-itsaso.jpg', 43.07702255249023437500, -2.27152657508850097656, 14, 'hybrid', 43.07242965698242187500, -2.28357505798339843750, 8);");
	tx.executeSql("INSERT INTO `herriak` VALUES(195, '2012-04-06 18:50:12', 'Segura', 3, '<p>\r\n	1256. urtean Gaztelako Erregeak Segura sortzea Probintzia eta Erreinuko bizitza ekonomikoari lotuta zegoen, merkataritza indartzeko San Adriango bidea indartu zen. Erregeak tarte jakinetan, bideetako joan -etorriak babesteko eta errazteko hiribilduak sortu zituzten. Segurak Gipuzkoako probintziaren antolaketan leku nagusia izan zuen. Egun, multzo monumentala izendaturik dagoen hiriguneak, Erdi Aroko xarma mantentzen du.</p>\r\n', '1256', '1471', '', 'www.seguragoierri.net', 'Ekainak 24', 'Segura.jpg', 43.00844573974609375000, -2.25245475769042968750, 16, 'hybrid', 43.00998306274414062500, -2.25288391113281250000, 8);");
	tx.executeSql("INSERT INTO `herriak` VALUES(196, '2012-04-07 15:39:04', 'Lazkao', 3, '<p>\r\n	Lazkao Gipuzkoako udalerria da, Goierri eskualdeko hirugarren herririk handiena. Aralar mendikatearen magalean, Oria ibaira doan Agauntza errekak zeharkatzen du herria. 160 m-ko garaieran dago, eta 11.4 km&sup2;-ko hedadura du.</p>\r\n<p>\r\n	<br />\r\n	Dokumentuetan jaso den aipu zaharrena 1053ko idatzi bat da, eta ingurunea populaturik zegoela adierazten du. 1950 eta 1970eko hamarkaden artean izan zuen hazkunderik handiena, industriaren hazkundearen eraginez. 1980ko hamarkadatik aurrera hazkundea egonkortu da. Egun, 5.000 biztanle inguru ditu herriak.</p>\r\n', '1053', '5407', '43°2&#8242;16&#8243;N 2°11&#8242;14&#8243;W', 'www.lazkao.net', 'Apirilaren 28 (San Prudentzio)', 'lazkao.jpg', 43.03254699707031250000, -2.18577504158020019531, 15, 'hybrid', 43.03392791748046875000, -2.18731999397277832031, 8);");
	tx.executeSql("INSERT INTO `herriak` VALUES(197, '2012-04-11 13:27:16', 'Espejo', 1, '<p>\r\n	Gaubeako haranean (Valle de Valdegov&iacute;a) dagoen herria da. Herri lasaia da eta erosoa da. Eguna pasatzeko edota egun batzuk egoteko toki ederra da, naturaz gozatzeko, bertoko eta inguruko monumentu historikoak bisitatzeko edota lasaitasunez bueltatxo bat emateko.<br />\r\n	<br />\r\n	Omecillo errekak herria zeharkatzen dau, herria bitan banatuz.<br />\r\n	<br />\r\n	XVI eta XVII. mendeetako eraikinak topa ditzakeguz Espejon: Erdi Aroko Etxe-Dorrea, Salazar&#39;tarren Jaun-Etxea edo Eliza Parrokiala.<br />\r\n	<br />\r\n	Nahiz eta herri txikia izan, Espejok badu bere frontoia (estalia), futbol zelaia eta ibai-igerilekua. Honetaz aparte, badira ere janari dendak, aterpetxea, landa-etxeak, tabernak eta Vital Kutxa ofizinak.<br />\r\n	<br />\r\n	Herriko jaiak El Salvador egunean (abuztuak 6) dira baina abuztuko 3. astean ospatzen dira.<br />\r\n	<br />\r\n	Inguruko herri batzuk hauek dira: Tuesta, Villamaderme, Villanañe, Barrio eta Gesaltza-Añana (Salinas de Añana).</p>\r\n', '', '', '', '', 'Abuztuko 3. astean', 'espejo.jpg', 42.80944442749023437500, -3.04633498191833496094, 16, 'hybrid', 42.80868911743164062500, -3.04613113403320312500, 8);");
	tx.executeSql("INSERT INTO `herriak` VALUES(200, '2012-04-18 16:49:37', 'Lagran', 1, '<p>\r\n	Lagran Arabako hegoaldean dagoen udalerria da, Mendialdeako eskualdearen hego-ekialdean eta Kantabria mendilerroaren behekaldean kokatutakoa. Gasteiztik 35 bat kilometrora dago.<br />\r\n	<br />\r\n	Ega ibaia udalerri honetan jaio da, ondoren Nafarroarako bidea hartuz. Lagran ohiko abiapuntua da Peña del Leon eta Cruz del Castillo mendietara igotzeko, Toloño/Kantabriako mendilerroaren oinetan egonda.<br />\r\n	<br />\r\n	Erdi Aroan Araban sortutako 21 hiribilduetako bat da Lagran, baina ez da gorde hari Forua ematen dion agiririk. Hortaz, herriaren sorrera ez dago argia, lehen aipamena 1165. urtekoa bada ere. XV. mendearen bigarren erdialdearen inguruan Nafarroako Erresumatik igaro zen Gaztelako Erresumaren menpe egotera, eta 1515ean Uria izendatu zuten. Salinasko Kondea eta geroago Hijarreko Dukeak herriaren jabeak izan ziren garai hartan.<br />\r\n	<br />\r\n	Ondasun nabarmenak<br />\r\n	<br />\r\n	Lagrango hirigune historikoa, Kultura Ondasun gisa Monumentu Multzo izendatua[1]. Garai bateko harresiaren aztarnak daude gaur egun ikusgai, Portal de Cristo izeneko atean batez ere. Bestela, erdialdean Andra Maria Jaiokundearen Parrokia Eliza platereskoa (Iglesia Parroquial de Nuestra Señora de la Natividad) dugu, XVI. mendekoa. Azkenik, hegoaldean Vianatarren jauregi barrokoa dago.<br />\r\n	<br />\r\n	Lagrango herriko etxeen lur zatiketa gotikoko azken aldiko ohiturari jarraiki egina dago edo, hobeki esan, Pizkunde garaikoei jarraiki, izan ere oraindik ere beren aurreko alde estua gordetzen duten etxeak bi fatxada dauzkate, bakoitza kale banatara. Bi kaleen artean dagoen sestra diferentzia dela-eta, etxeek bi sarrera dauzkate, kale bakoitzean bat, eta etxebizitzarako sarrera nagusi edo zuzena maila goragoko kaletik egiten da. Baina hori ez da kasu guzti guztietan gertatzen eta, gaur egun, badira etxe batzuk ondasunak zatitzearen ondorioz bi kaleetara ematen ez dutenak.<br />\r\n	<br />\r\n	Aipatu beharreko ateen artean honako hauek daude: &laquo;Kristoren Atea&raquo; izeneko kaleko 22. zenbakikoa. Oso sarrera polita da, arku eskartzanoz egina, arkubarnera molduraduna duela. Gainean sarrail-ezkutu bat dauka elizaren inguruko irudiez apaindua, horrek erakusten du garai batean etxe hori apaizen batena izan zela edo, bestela, apaizetxe gisa erabilia izan zela. Atea bi aldiz babestua dago, teilatu hegalak babestua eta, zentzu zabalagoan, sarrerak berak duen eta bi beso motzen gainean eusten den teilatutxoak babestua.<br />\r\n	<br />\r\n	Beste bi ateak erdiko kalearen eskubi aldean daude, &laquo;De los cantones&raquo; kantoitik hurbil. Lehenengoa, 14. zenbakikoa, arku eskartzanoz egindako ate liraina da, arku barnera molduraduna duela, goian aipatutako horren antzekoa. Arkuaren erdiko dobelak sarrail-ezkutu bat dauka, bi giltza gurutzatuen irudia duela.<br />\r\n	<br />\r\n	Bigarrena, 16. zenbakikoa, Pizkunde garaiko ate dintelduna da. Gotiko garaiko zantzuak ere badaude bertan, esate baterako, harri bloke gainean doan eta erliebe finean egina dagoen arku konopiala. Dintelaren gainean erlaitz bat dago, molduraduna, atearen edertasuna are gehiago indartzen duena.</p>\r\n', '', '200', '42°37&#8242;32&#8243;N 2°35&#8242;4&#8243;W', 'http://www.lagran.org/', '', 'lagran2.jpg', 42.62556076049804687500, -2.58333206176757812500, 16, 'hybrid', 42.64835357666015625000, -2.58642196655273437500, 8);");
	tx.executeSql("INSERT INTO `herriak` VALUES(201, '2012-04-24 12:11:06', 'Urkabustaiz', 1, '<p>\r\n	Urkabustaizko Udala zenbait herrik osatua dago: Abezia, Abornikano, Beluntza, Goiuri, Inoso, Izarra, Larrazketa, Oiardo, Ondona, Untzaga, Uzkiano...<br />\r\n	<br />\r\n	Urkabustaiz Arabako ipar-ekialdean kokatzen da, Gorbeialdea osatzen duten herriekin batera, Zuiako Kuadrilaren baitan.<br />\r\n	<br />\r\n	Urkabustaizen 1200-1300 pertsona inguru bizi dira eta jende eta zerbitzu gehien elkartzen dituen herrigunea Izarra da.</p>\r\n', '', '1286', '', '', 'Urriko lehen asteburua', 'urkabustaiz.jpg', 42.95260620117187500000, -2.89941859245300292969, 16, 'hybrid', 42.95251083374023437500, -2.90050220489501953125, 8);");
	tx.executeSql("INSERT INTO `herriak` VALUES(205, '2012-05-05 13:28:20', 'Orbaizeta', 5, '<p>\r\n	Orbaizeta Nafarroako iparraldeko herri txiki bat da, 200 biztanle baino gutxiagokoa. Aezkoa ibarrean dago, eta Irati oihanaren sarrera naturala da.</p>\r\n<p>\r\n	Behin eta berriz suntsituta izan zen XVIII eta XIX garren mendeen gerletan, eta horregatik bere erdiaroko itxura, Aezkoako gainerako herriekin gertatzen den bezala, galtzen joan da. Honat etortzeko, arrazoien artean, ekialdeago aurkitzen ahal ditugun penintsulako gareak aurkitzen ditugu; hiru Orbaizetan, baina ibarreko beste herrietan ere asko ikusiko ditugu.</p>\r\n<p>\r\n	Orbaizetako arma ola, XVIII garren mende bukaeran egina izan zen, eta bere garaian estatuko haundiena. Bonbak, granadak eta kanoi bolak egiten zituzten, eta 100 urte baino gutxiago iraun zuen martxan, behin eta berriz sutsitua izan baitzen. Gaur egun bisitagarria da, eta bere arkupeen argazkia Euskal Herri osoan ospetsua izan da, Benito Lertxundiren diska baten azal izan zelako.</p>\r\n<p>\r\n	Baina bere altxor haundiena Iratiko ohiana da,18.000 ha. oihanez eta larrez. Aezkoako bailararena da, baina Orbaizetatik dugu lau sarreratik bat. Besteak Otsagitik (Zaraitzu), Xiberua (Iratiko txaletak) eta Garazitik (Garaziko txaletak).</p>\r\n', 'XV mendea baino lehen', '190', '', '', 'San pedrotarrak (ekainaren 3an eta asteburuan)', 'orbaizeta.jpg', 42.97478103637695312500, -1.22938895225524902344, 17, 'hybrid', 42.97442626953125000000, -1.22939968109130859375, 8);");
	tx.executeSql("INSERT INTO `herriak` VALUES(203, '2012-04-27 00:08:57', 'Elkarte', 5, '<p>\r\n	Euskal Herriko hiriburua hain gertu izanda (15km-tara), arreta deitzen duen herria da Elkarte. Iruñerriko lautadatik muino batez bereizita, bere inguruak soroz beterik aurkitzen dira, bertako 20 biztanleen diru iturri den gari, olo, garagar, zaldi, behi eta ardiz apainduta.</p>\r\n<p>\r\n	Hiriburuaren etengabeko estres eta ardurak ahazteko leku paregabea dugu, lasaitasuna nabaritzen baita bere zelai, plaza eta baita biztanleengan ere.</p>\r\n<p>\r\n	Beste 9 herrirekin batera Berriobeitiko zendea osatzen du, han duelarik udaletxea, eta parean duen Ballariain herriarekin batera zendeako txikiena da. Herri hauek, Oteitzarekin batera bailaratxo bat osatzen dute, Eltxumendiren (850m) magalean kokaturik, zeina Iruñerria ikusteko tontor paregabea den.</p>\r\n<p>\r\n	Edonor izango da ongi etorria Elkarten, eta oraindik bizirik dirauen herritarren arteko auzolana ezagutuko du, XXI. mendean egonik atzera salto bat egingo du bisitariak.<br />\r\n	Bisitariak euskara adituko du herrian barna, gazte guztiek erabiltzen baitute euskara. Pilotalekua eta ostatu bat daude herrian, jauregi bat eta eliza erromanikoa.</p>\r\n', '1000', '20', '42°52&#8242;10&#8243;N 1°42&#8242;23&#8243;W', 'http://www.berrioplano.es', 'Irailaren bigarren asteburuan', 'elkarte.jpg', 42.86963272094726562500, -1.70622825622558593750, 17, 'satellite', 42.86972045898437500000, -1.70665740966796875000, 8);");
	tx.executeSql("INSERT INTO `herriak` VALUES(204, '2012-05-02 12:06:22', 'Larraga', 5, '<p>\r\n	Larraga Nafarroa erdialde-hegoaldean dago. Bertara heltzeko NA-132 errepidea Lizarra-Tafalla norabidea jarraitu eta orduan, Larragara doan errepidea hartu behar da.<br />\r\n	<br />\r\n	Larragak Mendigorriarekin egiten du muga iparraldean, Oteitzarekin mendebaldean, Artaxoa eta Tafallarekin mendebaldean, eta Berbintzana eta Lerinekin hegoaldean.</p>\r\n', '', '2.121', '42°33&#8242;47&#8243;N 1°50&#8242;55&#8243;W', 'http://www.larraga.es', 'Irailaren 29aren inguruan', 'larraga.jpg', 42.55775833129882812500, -1.84960842132568359375, 16, 'hybrid', 42.55778884887695312500, -1.85003757476806640625, 8);");
	tx.executeSql("INSERT INTO `herriak` VALUES(206, '2012-05-05 19:30:18', 'Ospitalepea', 7, '<p>\r\n	Herri ttipia da Ospitalepea: 78 biztanle ditu, bi jatetxe eta Eliza. Halere, bere tamainarako, bisitari asko ditu herriak.</p>\r\n<p>\r\n	1122an Aragoiko eta Nafarroako erregea jin zen Biarnoko Gaston V adixkidearen ikustera (elgarrekin Zaragoza Moroer hartu zuten 1118an). Ondorioz Elizaren eraikuntza berezi eta ederra: karratuaren oinarrian eraikia, gotiko estiloa baina arkuen lotura erromanikoa, eta abar. Kupulak zortzi alde (Nafarroako Olivan bezala), goiko izarrak zortzi punta eta erdian argiaren sartzeko zulo bat: zortzigerren eguneko Berpiztea?</p>\r\n', '1198', '78', '43°15&#8242;9&#8243;N 0°46&#8242;3&#8243;W', 'http://hopital-saint-blaise.mairie.pagespro-orange.fr/accueilfr.htm', '', '295px-ospitalepea_2006-08-02.JPG', 43.25159835815429687500, -0.76781988143920898438, 16, 'hybrid', 43.25020599365234375000, -0.76354980468750000000, 8);");
	tx.executeSql("INSERT INTO `herriak` VALUES(207, '2012-05-07 11:15:04', 'Arantza', 5, '<p>\r\n	Arantza Nafarroako iparraldean dagoen udalerria da, Bortzirikoa. Udalerri euskalduna da. Arantza, XIII. mendean sortu zuten Nafarroako erregeek. Ibaiaren indarra aprobetxatzeko asmoarekin, burdinola bat eraiki zen, eta herriko etxe zaharrenen izenak, bertatik datoz</p>\r\n<p>\r\n	Arantzan aurkitutako lehen giza aztarnak kristo aurreko 2000. urtekoak dira. Hainbat hilobi eta trikuharri aurkitu dira. Garai hartan bizi ziren ehiztari eta artzain moduan aritzen ziren biztanleen froga.</p>\r\n', '', '641', '43°11&#8242;46&#8243;N 1°43&#8242;33&#8243;W', 'http://www.arantza.eu', 'Abuztuaren 15ean', '300px-arantzako_udaletxea_2011-4-27.JPG', 43.19645309448242187500, -1.72535085678100585938, 16, 'hybrid', 43.19545364379882812500, -1.72612333297729492188, 8);");
	tx.executeSql("INSERT INTO `herriak` VALUES(208, '2012-05-29 21:02:41', 'Zeberio', 2, '<p>\r\n	Zeberio Bizkaiko hegoaldean dagoen udalerri euskalduna da, Nerbioi ibarra eta Arratia artean kokatuta dagoena, bi mendilerroren artean kokatutako harana da, 47,87km&sup2;-ko zabalera daukana.<br />\r\n	<br />\r\n	Ekialdetik mendebaldera, Zeberio ibaiak zeharkatzen du, Nerbioi ibaian amaitu arte.<br />\r\n	<br />\r\n	Haren herri mugakideak hauek dira: iparraldean Ugao-Miraballes, Arrigorriaga, Zaratamo, Galdakao, Bedia eta Igorre; hegoaldean Orozko; ekialdean Arantzazu eta Arteaga; eta mendebaldean Ugao-Miraballes eta Arrankudiaga.</p>\r\n', '', '1.063  (2011eko errolda)', '43°8&#8242;55&#8243;N 2°50&#8242;53&#8243;W', 'www.zeberio.net', 'San Antolin (Zubialden) irailaren 2an (patronala)', 'zeberio.jpg', 43.14950561523437500000, -2.85019207000732421875, 15, 'hybrid', 43.16489410400390625000, -2.86672520637512207031, 8);");
	tx.executeSql("INSERT INTO `herriak` VALUES(209, '2012-06-18 12:16:44', 'Elgeta', 3, '<p>\r\n	Elgeta Gipuzkoako mendebaldean dagoen udalerri txikia eta euskalduna da, Debagoiena eskualdean eta Bizkaiko mugan dagoena. Bidegurutze garrantzitsua da, Eibar 8 kilometrotara, Bergara 6 km-tara eta Elorrio 6 km-tara baitaude. Inguruko hiriburuei dagokienez, Gasteiztik 42 km-tara, Bilbotik 45 km-tara eta Donostiatik 78 km-tara dago.<br />\r\n	<br />\r\n	2009tik UEMAko mankomunitate euskalduneko partaidea da.<br />\r\n	<br />\r\n	Mugakide hauek ditu: Iparraldean Eibar eta Zaldibar (Bizkaia), hegoaldean eta ekialdean Bergara eta mendebaldean Zaldibar eta Elorrio (Bizkaia).<br />\r\n	<br />\r\n	Gipuzkoako herri altuenetariko bat da Elgeta, itsas mailatik 462 metrotara kokatuta, lehen esan bezala bi herrialdeen artean administratiboki, eta hidrologikoki bi isurialdeen banalerroan ere bai, alde batetik Deba ibaia eta bestetik Ibaizabal. Halaber, Ibur, Ubegi, Ubera eta Angiozar errekek udalerria zeharkatzen dute.<br />\r\n	<br />\r\n	Bestela, udalerri honen lurraldean Intxorta, Eguaspeko haitza, Larrabilburu, Azkonabieta, Udalaitz eta Larraluze mendiak daude, larreekin tartekatuta eta pago eta haritz bakan batzuk oraindik ere badaudelarik. zuhaitz mota nagusiena, berriz, Intsinis pinua da.</p>\r\n', '1333', '1150', '43°10&#8242;40&#8243;N 2°27&#8242;10&#8243;O', 'elgeta.org', 'Azoka nagusia', 'elgeta.jpg', 43.13706970214843750000, -2.48679399490356445312, 16, 'hybrid', 43.13667678833007812500, -2.48750209808349609375, 8);");
	tx.executeSql("INSERT INTO `herriak` VALUES(210, '2012-07-23 16:18:27', 'Ligi-Atherei', 7, '<p>\r\n	Basabürüako ibarrezkerrean kokaturik dago eta 232 biztanle bizi dira bertan. Antzinaroan Ligi eta Atherei bi udalerri ezberdin ziren, baina 1843an bat egin zuten.</p>\r\n<p>\r\n	Ligiko zubia edo laminen zubia da herriko hondasunetako bat, zeina kondairaren lamien laguntzarekin eraiki zen, nahiz eta harri bat faltan duen. Ligiko eliza (Julian Deunaren omenez) Erdi Aroaren erdialdean eraiki zen eta XIX. mendean berriztatze sakon bat jasan zuen.</p>\r\n', '1842', '232', '43°4&#8242;5&#8243;N 0°52&#8242;32&#8243;W', '', '', 'ligix.jpg', 43.06726455688476562500, -0.87710422277450561523, 16, 'hybrid', 43.06869125366210937500, -0.87640684843063354492, 7);");
	tx.executeSql("INSERT INTO `herriak` VALUES(211, '2012-07-26 10:18:47', 'Etxebarri', 2, '<p>\r\n	Orain hamarkada batzuk, Etxebarri nekazal eremuko gune txiki bat zen eta herritarrek inguruko industrietan lan egiten zuten. Bilboko eskualdearen pilaketak eta industriak instalatzeko leku faltak, gehitu egin zuten biztanleria eta industria lursaila, orain urte batzuk.</p>\r\n<p>\r\n	50-60 hamarkadatik hona, fabriken zabalkundearekin biztanleria laukoiztu egin da, eta 70ekotik, beste igoera nabarmen bat, herrian bertan instalatuz. 80ko hamarkadan biztanle kopurua egonkortu egin zen eta 90eko hamarkadan beste gorakada berri bat hirigintza hedapen eta Bilbo inguruko egoitza mugimenduari esker. 2011. urtean 10.000 biztanle kopurua gainditu da Etxebarrin.</p>\r\n', '1509', '10.400', '', 'http://www.etxebarri.net', 'San Esteban, Santa Ana, San Antonio', 'etxebarri.jpg', 43.24736785888671875000, -2.88923764228820800781, 15, 'satellite', 43.24904632568359375000, -2.89026260375976562500, 8);");
	tx.executeSql("INSERT INTO `herriak` VALUES(212, '2012-07-27 04:41:00', 'Azkarate', 5, '<p>\r\n	Azkarate Nafarroa mendebaldean eta Araiz Bailararen mendebaldean kokatua dagoen kontzejua da. Tolosatik 20KM-ra eta Iruñeatik 50KM-tara aurkitzen da.<br />\r\n	<br />\r\n	Balerdi mendia (1.197 m) kontzejuaren mendebaldean dago kokaturik, Azkarate izanda tontorrera igotzeko ohiko abiapuntua.<br />\r\n	<br />\r\n	Ondareari dagokionez, Herriko plazan dagoen San Esteban eliza aipatuko dugu eta baita San Fermin ermita ere. Azken hau, XVI. mende bukarako baseliza da.</p>\r\n<p>\r\n	Herriko beste toki \"ezagun\" bat Katarain musika estudioa da. Inguruko talde ezagun asko gerturatzen da grabaketa lanak egitera Jonan Ordorikarekin.</p>\r\n', '', '101', '43°2&#8242;10&#8243;N 2°0&#8242;31&#8243;W', '', 'San Fermin egunaren inguruan, asteburua hartuz.', 'azkarate.jpg', 43.03579330444335937500, -2.00780248641967773438, 16, 'hybrid', 43.03587341308593750000, -2.00899338722229003906, 8);");
	tx.executeSql("INSERT INTO `herriak` VALUES(213, '2012-07-27 04:51:35', 'Arribe', 5, '<p>\r\n	Arribe Nafarroako ipar-mendebaldeko herria da, Araizko udalerriaren eta Arribe-Atallu kontzejuaren herriburua dena.<br />\r\n	<br />\r\n	Iparraldean Orexa (Tolosaldea), hegoaldean Betelu, mendebaldean Azkarate eta ekialdean Gorriti mugakide ditu.<br />\r\n	<br />\r\n	Bertakoa da Urko Aristi Etb.ko eguraldi gizona.<br />\r\n	<br />\r\n	Hegoaldean Aralarko mendilerroa herriaren mugakide naturala dugu, Aralarko Mallo ikusgarriak bertatik bertara daudelarik: Ttutturre (1282 m.), Hirumugarrieta (1431 m.), Aldaon (1411 m.), Uakorri (1306 m.) eta Balerdi (1197 m.).<br />\r\n	<br />\r\n	Ekialdean berriz Elosta (937 m.), Santa Barbara (853 m.) eta Urkieta (854 m.) mendiak daude.<br />\r\n	<br />\r\n	Bestela, Araxes ibaiak herria zeharkatzen du hegoekialdetik iparmendebaldera, Gipuzkoa aldean Oria ibaian amaituko dena eta Aralar eta inguruko mendietako urak jasotzen dituena.</p>\r\n', '', '', '43°2&#8242;14&#8243;N 1°59&#8242;6&#8243;W', 'http://www.araitz.es/?sec=ayuntamiento&lang=eu', 'Egun handia uztailak 29 bada ere, uztaileko 3. asteburuan ospatzen dira.', 'arribe.jpg', 43.03723144531250000000, -1.98505520820617675781, 15, 'hybrid', 43.03723144531250000000, -1.98505520820617675781, 8);");
	tx.executeSql("INSERT INTO `herriak` VALUES(214, '2012-08-05 14:01:54', 'Arrazua-Ubarrundia', 1, '<p>\r\n	Arabako erdialdean kokatua, Zuia-Gorbeialdea eskualdeko udalerri txikiena da. Gsteiztik zazpi bat kilometrora dago. Elgea mendilerroak banatzen du Debagoienatik (Gipuzkoa).<br />\r\n	<br />\r\n	Udalerriko herriak Zadorra ibaiaren eta Uribarri Ganboako urtegiaren ertzetan daude. Arratzua-Ubarrundiako udalerria ondorengo herriek osatzen dute: Arroiabe, Arzubiaga, Betolatza, Dura, Landa, Langara Ganboa, Luku, Mendibil, Uribarri Ganboa, Zirao eta Zurbao.</p>\r\n', '1920', '950 (2011n)', '42°53&#8242;25&#8243;N 2°38&#8242;21&#8243;W', 'http://www.arrazua-ubarrundia.com/', '', 'arrazua.jpg', 42.88980484008789062500, -2.63916659355163574219, 15, 'hybrid', 42.88980484008789062500, -2.63916659355163574219, 8);");
	tx.executeSql("INSERT INTO `herriak` VALUES(215, '2012-08-31 14:29:16', 'Trapagaran', 2, '<p>\r\n	Trapagaran (antzina gaztelaniaz San Salvador del Valle) Bizkaiko udalerria da. Meatzaldeko eskualdean kokaturik dago.</p>\r\n<p>\r\n	Herriaren beheko aldea hirugunea eta Ugarte, Elguero, Zaballa, San Gabriel eta Galindo auzoek hartzen dute. Goiko aldea berriz Larreineta, La Arboleda eta Barrionuevo auzoak osatzen dute. Bertan Bizkaiko meategi-paisaiarik ikusgarriena eskaintzen du. Izan ere zonalde honetan idatzi baita Euskal Herriaren industrializazio eta kapitalismoaren sorreraren lehenengo orriak. Baita langile eta klase borrokarena ere.</p>\r\n', '', '12.317', '43°19&#8242;N 3°3&#8242;W', 'http://www.trapagaran.net/eu-ES/Orrialdeak/default.aspx', 'San Ramonak. Abuztuaren 29, 30 eta 31ean eta irailaren 1 eta 2an.', 'trapagaran.jpg', 43.33851242065429687500, -3.07970786094665527344, 13, 'hybrid', 43.33951187133789062500, -3.09241080284118652344, 8);");
	tx.executeSql("INSERT INTO `herriak` VALUES(217, '2012-10-09 13:28:14', 'Kaseda', 5, '<p>\r\n	Kaseda Nafarroa Garaiko eki hegoaldeko herria da.</p>\r\n<p>\r\n	Zangoza inguruan dago ( 13 km.-ra), ondoan Oibar, Galipentzu, Zare, Peña eta Gabarderal daude. Inguruan Xabier, Leire eta La Oliva daude. Zaraitzuko ardibidea ( altxonbidea) bertatik iragaten da Zarrakaztelurako bidean.</p>\r\n<p>\r\n	Aipagarria da oso herritik 3 km-ra dagoen San Zoilo baseliza, XIV. mendekoa.</p>\r\n<p>\r\n	Kasedako dermioan Viscofan lantegia dago, Euskal Herriko multinazionalik hedatuenetakoa.</p>\r\n', '1129', '1050', '', '', 'otsailaren 4a, irailaren 8a', 'kaseda.jpg', 42.52363967895507812500, -1.36705195903778076172, 16, 'hybrid', 42.52482604980468750000, -1.36665499210357666016, 8);");
	tx.executeSql("INSERT INTO `herriak` VALUES(218, '2012-10-23 18:44:03', 'Zeanuri', 2, '<p>\r\n	Zeanuri Bizkaiko hego-ekialdean dagoen udalerria da, Arratia eskualdean biztanle kopuruz hirugarren egiten duena, Igorre eta Lemoaren ondoren.<br />\r\n	<br />\r\n	Iparraldean eta ekialdean Dimarekin du muga; ipar-mendebaldean Areatzarekin; mendebaldean Orozkorekin; eta hegoaldean Ubide eta Zigoitiarekin, (Araban. Barazar mendateak lotzen du Arabarekin.<br />\r\n	<br />\r\n	Udalerri honetan dago Gorbeia mendia (1.481m) eta haren izeneko parke naturalaren zati handi bat hartzen du.<br />\r\n	<br />\r\n	UEMAko kidea da herria, euskal hiztunen ehuneko handia duela eta.<br />\r\n	Mendebaleko euskararen edo bizkaieraren aldaera bat dute jatorrizko euskalkia.</p>\r\n', '', '1.297 bizt. (2011)', '43°5&#8242;58.83&#8243;N 2°44&#8242;58.57&#8243;W', 'http://www.zeanuri-udala.org/eu-ES/Orrialdeak/default.aspx', 'Maiatzak 15, Isidro Deunaren jaia.     Abuztuak 15, Andre Mariaren Zeruratzea, Zeanuriko jai nagusia.', 'Zeanuri.jpg', 43.09806442260742187500, -2.74896931648254394531, 15, 'hybrid', 43.09743881225585937500, -2.75064301490783691406, 8);");
}

