	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(70, '2012-02-21 16:45:23', 'Juantxo', 'Alde Zaharreko taberna klasikoa. Bertako patata tortilla ezaguna da, eta jende askoko egunetan leporaino beteta egoten da. Giro euskalduna.', '', 'juantxo1.jpg', 43.32272720336914000000, -1.98458790779113770000, 17, 30, 1, 'saapmifree');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(72, '2012-03-14 09:40:02', 'Alboka', 'Donostiako erdialdean dagoen taberna. Bertako giro lasaiak taberna berezia egiten du. Kaña batzuen inguruan lagunekin berriketan egoteko leku paregabea.', '', 'alboka.jpg', 43.31718444824219000000, -1.98389053344726560000, 16, 30, 7, 'saapmifree');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(79, '2012-03-16 12:16:45', 'EHZ Festibala', 'Euskal Herri osoan egiten den festibalik interesgarrienetakoa. Urtero ekaineko azken asteburuan edo uztaileko lehenengoan izaten da eta milaka pertsona biltzen dira, inongo patrozintzaile komertzialik gabeko festa honetara. Giro euskalduna eta gaztea.', 'http://ehz-festibala.eu/', 'mini_logo_ehz_2009.jpg', 43.30817794799805000000, -1.24386906623840330000, 17, 64, 14, 'saapmifree');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(80, '2012-03-16 12:17:04', 'Muxibar', 'Jateko leku aproposa. Entsaladak, pizzak, pasta, ... eta dana primerakua!', '', '', 43.06522369384765600000, -2.49441146850585940000, 17, 44, 1, 'Maite');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(81, '2012-03-16 12:21:19', 'Goiegi jatetxea', 'Kalitate oneko janaria prezio onean. Zerbitzu oso ona.', 'www.goiegi.com/', 'mini_images.jpg', 43.27479934692383000000, -2.01283693313598630000, 14, 37, 1, 'ibereziartua');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(82, '2012-03-16 12:31:13', 'Iru Bide', 'Hernanitik Goizuetarako bidean dagoen jatetxe honek merezi du bisita bat: ez da janari tradizionala, ezta abangoardiakoa ere. Biak batera eskaintzen dituzte (eta prezio ezinhobean).', '', '', 43.25228118896484400000, -1.94664001464843750000, 13, 33, 1, 'boligorria');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(83, '2012-03-16 12:36:56', 'Bodega', 'Musika beltza (soul, reggae...) eta garagardo eskaintza berezia. Taberna historikoa da musikazaleentzat, besteak beste, Negu Gorriak taldeak ohitura zuen bira bati ekin aurretik Hernaniko Bodegan kontzertu sorpresak eskaintzeko.', '', '', 43.26558685302734400000, -1.97465634346008300000, 16, 33, 7, 'boligorria');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(84, '2012-03-16 12:47:49', 'sekaña', 'Usurbilgo Aginaga auzoan, txerrikumea edo arkumea jateko primerako lekua. Aurrez enkargatu behar da.  Umeentzako parkea eta zelai gunea.Ingurune ederra, bazkal aurretik bueltaxka bat egiteko aproposa.', '', 'mini_baserriak_174_3.jpg', 43.27442550659180000000, -2.07092285156250000000, 18, 38, 1, 'olatz');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(85, '2012-03-16 18:25:11', 'Gauze Onak', 'Liburu zaharrak, kartelak, argazkiak... Mota guztietako bitxikeriak aurki daitezke Egiako denda honetan. Eta den-denak modu onean!', '', 'mini_gauze-onak-1-1.jpg', 43.31640243530273400000, -1.97452819347381600000, 19, 30, 12, 'boligorria');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(86, '2012-03-18 18:01:16', 'Urgulleko taberna', 'Donostiako lekurik ikusgarrienetako bat. Urgull mendian, goiko zabaldiko erpin batean dauden eskailera batzuetatik jaitsita heltzen da. Eguraldi ona egiten duen egunetan gozatzeko, badia guztia ikus daiteke bertatik. Prezio merkeak eta giro lasaia.', '', 'mini_urgull.jpg', 43.32395172119140600000, -1.99160456657409670000, 17, 30, 7, 'saapmifree');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(88, '2012-03-19 15:50:07', 'Sastarrain baserri eskola', 'Asteburu pasa eguraldi ederrarekin joateko primerakoa. Bertan ekintza asko daude eta ingurua zoragarria da.', '', 'mini_images-2.jpg', 43.23979568481445000000, -2.25826978683471700000, 15, 50, 17, 'oirureta');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(89, '2012-03-19 16:18:33', 'Huts', 'Mahai-inguru lasaian egoteko. 100 garagardo desberdin dituzte aukeran. Bidezar kalearen behealdean dago.', '', '', 43.09077835083008000000, -2.31287431716918950000, 17, 160, 7, 'jtorner');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(90, '2012-03-19 16:28:29', 'Motx', 'Poteorako ala parrandarako. Gosariak ere ematen dituzte. Hamaika kultur ekitaldi antolatzen da bertan, kontzertu eta bertso saioak tartean. Urretxuko ''aldaparen'' goialdean dago, beste hainbat tabernaren alboan.', '', '', 43.09244155883789000000, -2.31478929519653300000, 18, 161, 7, 'jtorner');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(91, '2012-03-19 16:33:44', 'Muniskue-gaina gune megalitikoa', 'Zoragararria da Onddi magaleko toki hau. Besabi jatetxetik 30 minututan zaude bertan, dena ordekan, haurrekin egiteko oso egokia. Denboran atzera egiteko parada ematen du. Bistak izugarriak dira (Adarra pare-parean) eta kriston pakea dago. Merezi du.', '', 'mini_mulisko.jpg', 43.22823333740234400000, -1.94633746147155760000, 15, 33, 14, 'Urko');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(92, '2012-03-19 16:48:39', 'Saski', 'Poteorako edo afaltzeko primeran, pintxo desberdin pila izaten dituzte eta kazuelitak. Festetan bokatak ere izaten dira. Piruloko plazatik Zumarraga aldera, aldapa gora egin eta eskubitara dago.', '', '', 43.09080505371094000000, -2.31331944465637200000, 20, 160, 7, 'mvierbucher');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(93, '2012-03-19 20:07:46', 'Tolare', 'Jatetxe ona eta modu onekoa. Plater konbinatuen aukera handia dago eta kartara ere jan daiteke. Terraza handia dauka eta eguraldi oneko egunetan, kanpoan oso gustora jaten da. Prezio onak.', 'http://www.tolarejatetxea.com', 'tolare.jpg', 43.28643798828125000000, -1.83510303497314450000, 15, 32, 1, 'saapmifree');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(94, '2012-03-19 21:33:34', 'Rekalde', 'Taberna barruan edo kanpoan beti giro ona aurkituko dezu... Sukaldean on onak dira: pintxoak, hanburgesak, bokadilloak... eta asteazkenero pintxo-pote euro baten truke! Donostiko alde zaharrera mugituaz gero, Rekaldetik pasatzea ez dezu ahaztu behar.', '', 'rekalde.jpg', 43.32453155517578000000, -1.98318243026733400000, 18, 30, 7, 'txarlygraffo');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(95, '2012-03-19 21:46:50', 'Ikatztegieta Jatetxea', 'Amonarena delakoa...ez duzu menurik aukeran, zenbat zareten esan eta itxaron besterik ez duzu egin behar.Jatekoa prest dagoenean amona etorriko zaizu guztiarekin,txikitan bezela...Hiru plater, edaria, kafea, postrea GUZTIRA 10 EUROREN TRUKE!Etxean bezela!', '', '', 43.27370452880859400000, -2.01278328895568850000, 17, 37, 1, 'txarlygraffo');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(96, '2012-03-19 21:56:13', 'Atxega jauregia', 'Leku ederra, zerbitzua behar den bezala, janari ezinhobea. Belardi handi bat du bertan ospakizunak egiteko. Ezkontza eta festa jendetsuak egiteko egokia.', 'http://atxegajauregia.com/', '2693184_15_b.jpg', 43.27099990844726600000, -2.05407857894897460000, 17, 38, 1, 'txarlygraffo');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(97, '2012-03-19 22:36:40', 'Errioguardaenea', 'Bertako produktuak erabiltzen duen jatetxe honetan, euskal giroa eta lasaitasuna topatuko dituzu. Igandeetan enkarguz ez bada itxita dago jatetxea.\r\nMenuak 14,50 € eta 18,00 €tan. Ostegun gauetan eta asteburuetan 25,00 €.\r\nTelefonoa: 943330291.\r\nHernaniko', '', '', 43.26754760742187500000, -1.97581410408020020000, 15, 33, 1, 'Bego');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(98, '2012-03-19 22:42:47', 'Oinddi Taberna', 'Bokata handiak eta onak. Bertan jateko edo eramateko prestatzen dituzte. Baita plater konbinatuak ere. Hernaniko Kale Nagusian topatuko duzu.', '', '', 43.26655960083008000000, -1.97482275962829600000, 19, 33, 7, 'Bego');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(99, '2012-03-19 23:20:57', 'Algortako portu zaharra', 'Algortako portua Euskal Herriko ezezagunetakoa eta ederrenetakoa da. Kale estu aldapatsuak, etxe atari zoragarriak eta oso giro ona. Bertako jaiak inguruko ospetsuenak eta jendetsuenak dira (abuztuaren 10aren bueltan). (Argazkia: Tomas Galindo)', '', 'mini_8163084.jpg', 43.34815979003906000000, -3.01449990272521970000, 12, 115, 14, 'Unai');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(100, '2012-03-20 09:30:39', 'Garraxi Jatetxe Begetarianoa', 'Barazkiak gustoko badituzu edo besterik gabe zer edo zer desberdinaz gozatu nahi baduzu nahiko merke aukera ederra. Hori bai, lan pixka bat egin beharko duzu mahaia zuk zeuk prestatu beharko duzu eta!', '', 'garraxi-1.jpg', 43.31727600097656000000, -1.97145581245422360000, 17, 30, 1, 'eneko');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(101, '2012-03-20 09:36:30', 'Hermandad', 'Arraina jateko primerako jatetxea. \r\nZerbitzariak oso jatorrak dira.\r\nLekua erreserbatuz gero jangela txikian jateko aukera hobea.', 'http://www.hermandadpescadores.com/restaurante_eusk.html', 'mini_restaurante-la-hermandad-de-pescadores_723052.jpg', 43.36812591552734400000, -1.79347717761993400000, 18, 35, 1, 'Maite');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(102, '2012-03-20 10:07:50', 'Belabartzeko ateak', 'Izabatik Belabartzera joateko badagko \"ezkutuko\" bide zoragarri bat. \"Ateas de Belabartze\" deitzen diote (euskararen arrasto ugari daude oraindik Anso eta Hechon bailaretan), harkaitz batzuen artetik pasa behar delako. Erreka ondoko tx', '', 'mini_belarbarze.jpg', 42.86380004882812500000, -0.89710175991058350000, 14, 166, 14, 'Urko');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(103, '2012-03-20 11:06:59', 'Euskararen Interpretazio Zentroa', 'Euskararen Etxean dago Euskararen Interpretazio Zentroa. Berriztu berri dugu eta edukiak aldatu ere. Tren geltoki baten antzera girotuta dagoen gune honetan, euskararen bidaia denboran zehar islatu nahi izan da hizkuntzaren historia eta bilakaera azalduz.', 'http://www.azkuefundazioa.org/web/euskararen-etxea/erakusketa', 'mini_euskalkiak1.jpg', 43.27620697021484400000, -2.95532226562500000000, 9, 135, 14, 'AzkueFundazioa');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(104, '2012-03-20 13:24:25', 'Fox', 'Musika ona, modetatik at. Poteorako zein parrandarako. Kontzertu ugari egiten da bertan, baita bertso saioak, erakusketak eta bestelakoak ere. Urretxuko ''aldaparen'' goialdean dago.', '', '', 43.09228515625000000000, -2.31520223617553700000, 17, 161, 7, 'jtorner');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(105, '2012-03-20 13:29:08', 'Laket', 'Adituak dira ardo kontuetan (dastaketak-eta egiten dituzte). Pintxoak badituzte. Urretxuko ''aldaparen'' goialdean dago, kiroldegiaren parean, Fox eta Motx tabernen erdian.', '', '', 43.09247589111328000000, -2.31511735916137700000, 18, 161, 7, 'jtorner');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(330, '2013-02-22 14:46:03', 'Jai Alai', 'Menuak, bokatak, pintxoak, razioak... leku eta giro atsegina, bazkari-afaritarako zein poteorako. Urretxuko aldapan eskuinetara dago, Udaletxera bidean.', '', '', 0.00000000000000000000, 0.00000000000000000000, 0, 161, 1, 'dioniso');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(107, '2012-03-20 13:42:17', 'Osaba', 'Poteo eta parrandarako. Inguruko tabernarik ''ilunena'', zentzu onean. Giroa, (ez soilik) euskal musika... Osabaren jatorri-markak dira. Zabala da, mahai ugarirekin. Udaberri-uda partean jende mordoa biltzen da bere terrazan. Urretxuko ''aldapan'' dago tabern', '', '', 43.09270858764648400000, -2.31371212005615230000, 17, 161, 7, 'jtorner');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(108, '2012-03-20 13:52:52', 'GazteleKU (gaztetxea)', 'Urretxu-Zumarragako gaztetxea. Garai batean Urola trenaren geltoki izandakoa. 2002an ireki zituen ateak, gaztetxe zaharrari (1998an bota zuten hura) lekukoa hartuta.', 'http://kunkkinzona.net/gazteleku/', 'mini_ku.jpg', 43.09311294555664000000, -2.31315946578979500000, 16, 161, 7, 'jtorner');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(109, '2012-03-20 13:57:14', 'Xabier', 'Iruñazarra taberna. Umeekin familian ogitarteko bat jateko primeran. Iruñeko bihotzean dago, Mercaderes kalean eta azken ihauterietan han izan ginen ogitarteko bat jaten 7 nagusi eta 12 ume. Gu bezala, beste hiru familia zeuden aldameneko ma', 'http://www.irunazarra.org/', 'mini_galeria-01-b.jpg', 42.81479644775390600000, -1.64434432983398440000, 13, 97, 7, 'Xabier');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(110, '2012-03-20 14:09:52', 'Iburreta', 'Kartara 35-40 eurotan afaldu daiteke. Astean zehar eguneko menua ematen dute, eguerditan, 10-11 eurotan. Jantokia gauetan itxi egiten da, ostiraletan, larunbatetan eta jai egun bezperetan izan ezik. Pintxo ugari dituzte aukeran. Tel: 943720819.', '', '', 43.09028625488281000000, -2.31365728378295900000, 17, 160, 1, 'jtorner');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(111, '2012-03-20 14:11:49', 'Larrinetxe (Udala auzoa)', 'Udaberrian sartzen garela Arrasateko auzo honetako taberna hau mundiala da jatorduetarako edo haren terraza ederrean zerbait hartzeko. Jaten ondo ematen dute, baina lekua da paregabea, Udalatxen oinetan, Anboto eta Orixol parean, Besaide... Auzoa bera ere', 'http://www.larrinetxe.com/', '', 43.07829284667969000000, -2.51277923583984380000, 15, 44, 1, 'Xabier');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(112, '2012-03-20 15:20:58', 'Dizebiren graffitiak', 'Artista honek kolorea eman dio herriari eta gutxien espero duzunean topatuko duzu agure bat zuri begira, ume bat jolasean edo tximu batzuk \"Peace, love and graffiti\" esanez, batzuk aipatzearren. Beasain osoan zehar daude. Oso ona da. Arg: Dizebi', 'http://www.dizebi.com', 'mini_dizebi.jpg', 0.00000000000000000000, 0.00000000000000000000, 0, 47, 14, 'saapmifree');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(113, '2012-03-20 15:47:09', 'Arritxu', 'Arritxu taberna. Lagunekin kafea edota trago batzuk hartzeko aproposa. Parranda giro berezia egon ohi da, gauak luzatzeko ikararik ez duten gautxorientzat egokia.', '', 'mini_arritxu.jpg', 43.36407852172851600000, -2.50366497039794900000, 19, 127, 7, 'ale');");
