	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(115, '2012-03-20 21:21:09', 'Pagoa', 'Parranda egiteko inguru hauetako lekurik hoberena. Astebururo joaten naiz eta giro paregabea eta musika elektroniko ona pintxatzen dute. Posibilitate bat ere bada inguruko lokalak itxi ondoren hurbiltzea, eguerdirarte luzatzen baita askotan festa.', 'http://www.facebook.com/people/Pagoa-Oiartzun/100000329077124', 'mini_sin-titulo.png', 43.28594589233398400000, -1.83289742469787600000, 16, 32, 10, 'aitzi');");
