	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(117, '2012-03-21 11:41:44', 'Kataku', 'Altzate kalean dagoen leku honetan bokatak eta pintxo goxoak jan ditzakezu, oso prezio onean. Kontzertuetarako lekua dute eta Beran bazabiltza, ezin duzu leku hau bisitatu gabe utzi! Giro euskalduna.', 'http://www.katakuostatua.blogspot.com.es/', 'mini_seconde5-1.jpg', 43.28162384033203000000, -1.67910575866699220000, 16, 90, 7, 'saapmifree');");
