	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(123, '2012-03-21 22:49:57', 'Goizuetako etxeak', 'GOIZUETAKO ETXEAK\r\n\r\nNekez ikus daitezke erdi aroko egurrezko etxeak, Itzaiaundi baserriaren antzekoak. Garai hartako guden ondorioz suteetan kiskali ziren. Gaur egun gelditzen diren eraikuntzarik aipagarrienak, beraz, XVI-XVIII. mendeetan eginak dira. Ga', 'http://www.goizueta.es/eu/visitenos/visitar/', '', 0.00000000000000000000, 0.00000000000000000000, 0, 180, 14, 'elama75');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(124, '2012-03-21 22:50:54', 'Oxe Guarda', 'Helbidea: KALE NAGUSIA 34\r\n\r\nTelefonoa: 948514019 - 629768534', '', '', 0.00000000000000000000, 0.00000000000000000000, 0, 180, 1, 'elama75');");
