	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(129, '2012-03-22 17:37:29', 'Oria ibaia', 'Gipuzkoako ibai nagusietakoa, angulek famatu egin duten errioa, Benito Lertxundi oriotarrak kantuz edertua, Oria bera da espektakulua. Baldin badaukazu aukerarik txalupa bat eskuratzeko, denik xumeena izanik ere, eta Txokoaldeko zubitik abiatu.', '', 'mini_oria-galardi-parean.JPG', 43.27042007446289000000, -2.06718921661376950000, 16, 38, 14, 'Artaso');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(130, '2012-03-23 10:31:21', 'Pitillasko aintzira', 'Pitillas eta Santakara artean dagoen erretserba naturala honek 250 eta 300 hektarea arteko azalera du. Bertan babes hartzen dute ehundaka hegazti espezie desberdinek eta neguan, Europatik hegoaldera datozen hegazti askok negua bertan igarotzen dute.', '', 'mini_3148fototh1.jpg', 42.40739440917969000000, -1.58328473567962650000, 13, 183, 14, 'saapmifree');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(131, '2012-03-23 10:48:50', 'Lanbroa', 'Lanbroa Taberna. Ogi txigortuaren paradisua. Arratsaldez zabaltzen dute.', '', '', 42.81896972656250000000, -1.64667260646820070000, 16, 97, 7, 'PatxiLar');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(132, '2012-03-23 13:47:58', 'San Marko gotorlekua', 'San Marko Gotorlekua pribilegiozko tokian kokatuta dago, Donostiatik 10 kilometro baino gutxira, naturan peto-peto murgildua. Zalantzarik gabe, Oarsoaldeko begiratoki nagusia da.\r\nLeku oso egokia da haurrekin eta lagunekin eguna pasatzeko, bai oinez, bai ', 'http://www.oarsoaldea-turismo.net/index.php?nora=ikusi\&r=114\&n=2.1.6', '', 43.29743957519531000000, -1.91403329372406000000, 15, 49, 14, 'baratxuri');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(133, '2012-03-23 13:53:47', 'Gaztelu Ostatua', 'Oreretako alde zaharrean kokatutako jatetxea da Gaztelu Ostatua.\r\n\r\nMenua (aste tartean): 9,80€\r\nAsteburuetako menua: 16€\r\n\r\n\r\n1988. urtea zen Romeral izena kendu eta Gaztelu Ostatuarena jarri zidatenean. Orduan iritsi zen niregana Ertzibengoa Gaztelumend', 'http://www.gazteluostatua.com/', 'mini_gaztelu.jpg', 43.31274032592773400000, -1.89904129505157470000, 18, 49, 1, 'baratxuri');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(134, '2012-03-23 16:21:21', 'Aratz', 'Jatetxe ona,baina batez ere Donostiako bokadilorik onenak, eta merke-merke!Giro euskalduna. Atletico Madrileko ultrek hil zuten Aitor Zabaleta bertako semea zen, eta haren omenez artisten dedikatoria asko.Ostiral eta larunbatetan gazte jende asko afaltzen', 'www.aratzjatetxea.com', 'mini_aratz1.jpg', 43.30263137817383000000, -2.00800895690917970000, 15, 30, 1, 'saapmifree');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(135, '2012-03-23 23:49:08', 'Iskibi', 'Iskibi, Urumea ibaiaren bailaran naturaz inguraturiko kanpina da.\r\nGozatu zure aisialdiaz inguru pribilejiatu, natural eta lasai batetan.\r\nZerbitzu hauek eskeintzen dizkizuegu:\r\n\r\nAKANPADA GUNEA\r\nBARBAKOA GUNEA\r\nTABERNA - JANTOKIA\r\nGARBIGAILUA\r\nPUTZU NATU', 'http://www.iskibi.com/', 'mini_iskibi.jpg', 43.19690704345703000000, -1.85598742961883540000, 17, 180, 18, 'Portolo');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(136, '2012-03-24 15:17:43', 'Bergarako gaztetxea', 'Bergarako gaztetxea herriko kartzela zaharrean kokatzen da. Kultur gune eta gazteen kezka eta aisialdiak garatzeko bilgune izateaz gain, ondare historikodun eraikina da.\r\n\r\nBertan urkatu zuten Michael Angiolillo anarkista italiarra.', '', 'mini_31114.jpg', 43.11708450317383000000, -2.41235733032226560000, 18, 53, 11, 'abolibar');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(137, '2012-03-26 09:45:51', 'Kafe Antzokia', 'Bilboko euskaldunon topa-lekuetako bat. Egunean zehar jaten ematen dute, ongi eta merke, baina Kafe Antzokira zerbaitengatik etorri behar baduzu, parranda egitera da. Ostegunetan ikasle giro handia egoten da. Kontzertuak izaten dira ia astebururo. Giro on', 'http://www.kafeantzokia.com/web/', 'mini_kafe-antzokia.jpg', 43.26349639892578000000, -2.92796850204467770000, 16, 135, 10, 'saapmifree');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(138, '2012-03-26 13:11:19', 'Saiaz GetariaHotela', 'XV.Mendeko dorretxea.Getariako alde zaharrean, bere oinarriak itsaslabarraren hertzean finkaturik. Kokapen honek gela bakoitzari aparteko nortasuna eskaintzen dio. 17 geletako batzuk, Gaztetape hondartzara begira, besteak Roke Deuna kalera, denak bereziak', 'www.saiazgetaria.com', 'mini_sarrera.jpg', 43.30369949340820000000, -2.20500659942626950000, 15, 45, 16, 'saiazgetaria');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(139, '2012-03-26 14:40:43', 'Alegiko gaztetxea', '1987. urtean, Alegiako Gazte Asanbladak tren geltoki aldamenean zegoen almazen zaharra okupatzea erabaki zuen, bertan kokatzen da Alegiko gaztetxea.', 'http://alegikogaztetxea.txintxarrisfera.com', 'mini_alegigaztetxea.jpg', 43.10144424438476600000, -2.09762692451477050000, 17, 189, 11, 'bedaio3000');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(140, '2012-03-26 21:07:33', 'Bulebarra', 'Atseden leku eta kultur eta kirol gunearen sarrera, Amezketa ibaiak Oriarekin bat egiten duen gunean kokatua dago. Itsasontzi baten brankaren itxura du. Alvaro Arrietaren proiektua da.\r\nUrtean behin, herriko festetan (Uztailak 16, Karmenak) \"Bulebarr', '', 'mini_bulebarra.jpg', 43.10009002685547000000, -2.09707975387573240000, 16, 189, 14, 'bedaio3000');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(141, '2012-03-27 17:50:20', 'Kanaletako ibilbidea', 'Oso ibilaldi polita Billabona inguruan. 4-5 orduko itzulia da, zailtasunik gabekoa eta marka txuri-horiez oso ondo seinalizatua.\r\nXX. mende hasieran eraikitako kanalak, Amasako baserri ingurua, basoak, errekak eta A-15 autobidea ikusteko aukera ematen du.', 'http://www.tolosaldea.net/tour/index.php?id_saila=99\&id_aita=97\&id_edukia=118\&lang=eu', 'mini_amasa-villabona-pr-118-p_w.jpg', 0.00000000000000000000, 0.00000000000000000000, 0, 165, 19, 'Andriu');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(142, '2012-03-27 17:58:29', 'Saturraran hondartza', 'Oso hondartza berezi eta ederra, Gipuzkoa eta Bizkaiaren arteko muga-mugan. Frankisten kartzela egon zen hemen eta historiaz beteriko gunea da. Eguraldi ona egitena paradisua da bere harkaitz ederrak fondoan. Arg: flickr. Barrutia erabiltzailea (CC)', 'http://eu.wikipedia.org/wiki/Saturraran', 'mini_saturraran2.jpg', 43.32151412963867000000, -2.41051197052001950000, 16, 41, 14, 'saapmifree');");
