	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(165, '2012-04-02 15:35:02', 'Itxaso', 'Heavy metala gustoko badozu, hauxe da zure taberna. Musika onaz aparte, futbolinera, partxisera edota dardoetara jolasteko aukera daukazu. Eta noski, gure Athleticeko partiduak ikusteko aukera bebai. Adituek, \"El Templo\" deitzen deutsie.', '', 'mini_itxaso.png', 43.32587051391601600000, -3.00753426551818850000, 19, 115, 7, 'ibilbao29');");
