	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(167, '2012-04-03 14:53:34', 'Koh Tao', 'kafe bat leku atsegin batean hartu nahi? ohizko pintxo tabernen alternatiba on bat ezagutzeko gogorik? hori eta askoz ere gehiago topatuko duzu Gipuzkoa plazatik oso gertu dagoen lokal honetan. Anbiente ona, mota guztietako jendea. Ezberdina. Merezi du!', 'http://www.facebook.com/pages/Caf%C3%A9-bar-Koh-Tao/162932050386668', '', 43.32195281982422000000, -1.98162674903869630000, 17, 30, 7, 'andonimutiloa');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(168, '2012-04-04 15:50:40', 'Gaztelua', 'Leku zoragarria da eta Maule osoa ikus daiteke bertatik. 1261. urtean eraiki zuten. 1642an, Louis XIII.a Frantziakoak gaztelua lurreratu eta 1648an berreraiki zuten. Frantziako Iraultzan kartzela bihurtu zen. Ikurrina zintzilik, oso leku berezia da', 'http://eu.wikipedia.org/wiki/Mauleko_gaztelua', 'mini_280px-chateau_mauleon.jpg', 43.21922683715820000000, -0.88645935058593750000, 14, 78, 14, 'saapmifree');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(169, '2012-04-07 15:44:19', 'Usurbe Antzokia', 'Usurbe Antzokia, Beasaingo herri antzokia da. Bertan estreinaldiko zinemaz gain, antzerkiak eta musika kontzertuak izan ohi dira.\r\nHerriko erdigunean kokatzen da, udaletxetik 2 minutura.', '', 'mini_ekintz4.jpg', 43.04605865478515600000, -2.19353199005126950000, 16, 47, 11, 'an13sa');");
