	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(171, '2012-04-10 12:32:40', 'Ola', 'Ola sagardotegia da Irungo sagardotegi bakarra. Meaka auzoan dago, Ibarlan, Aiako Harriaren magalean. Mendiz inguratuta, Ibarla bera leku paregabea da bueltaxka bat emateko (bidegorria ere badu, bizikletaz joan nahi izanez gero) eta txango ugariren hasier', 'http://www.olasagardotegia.net/', 'mini_ola.jpg', 0.00000000000000000000, 0.00000000000000000000, 0, 31, 8, 'mikel');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(172, '2012-04-10 15:59:12', 'Igartubeiti Baserri Museoa', 'Igartubeiti Baserri Museoa Gipuzkoako Foru Aldundiaren zerbitzu publikoa da eta euskal baserriaren historia, bilakaera eta bertan bizi izan ziren biztanleen eguneroko bizimodua hurbiltzea du helburu, baliabide hauen bitartez: baserria bera, interpretazio ', 'http://www.igartubeitibaserria.net', 'mini_igartubeiti-baserri-museoa.jpg', 43.07427978515625000000, -2.28481292724609380000, 13, 194, 11, 'Igartubeiti');");
