	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(179, '2012-04-18 10:19:16', 'Foruria', 'Gernikako betiko tabernetariko bat. Bi aukera daukazuz sabela mimatzeko: lagun artean bokata goxoa barran jatea edo asteburuetan lekua badago jangelan eseri ta makailau-entsalada, sarteneko eta antzeko plater zaporetsuak dastatzea. Egin aukera eta gozatu!', '', '', 43.31287765502930000000, -2.67763853073120100000, 18, 123, 1, 'ager');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(180, '2012-04-18 10:33:27', 'Gernikeko Gaztetxie', 'Ibilbide luzea dauka atzetik Astra koordinadorak. Astrako eraikin zaharretik hasi ta gaurko eskola zaharretako gune autogestionaturarte. Herritarren artean eraikitako ametsa: ikastaroak, irratia, multimedia, kontzertuak, antzerki, bertso...zu barik eztau!', 'www.gernikekogaztetxie.net', 'mini_gaztetxie-460x229.jpg', 43.31358337402344000000, -2.67886161804199200000, 18, 123, 11, 'ager');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(187, '2012-04-19 16:32:08', 'Oiharte Nekazal Etxea', 'Oiharte Nekazal Etxea 6 logela bikoitzez osaturiko XVIII. mendeko baserri erabarritua da. Bainugelak logela guztietan, wifi, TV, tximeneta, sukaldea, aparkaleku pribatua... dituelarik bezeroen eskuetara. Sagardotegi bat ere badu, txootx! egiteko.', 'http://www.oiharte.com', 'mini_1.gif', 43.01835250854492000000, -2.27066302299499500000, 17, 184, 17, 'oiharte');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(182, '2012-04-18 12:15:08', 'Santimamiñe eta Oma', 'Gernikatik oso gertu, Kortezubin Santimamiñeko historiaurreko kobak bisita birtual modernoenan ikus daitezke. Inguru bardinean Omako auzo majiko eta misteriotsuan galtzea esperientzia itzela da: baso margotua, errekaren ibilbidea, bolunzulo errotaz', '', 'mini_bolunzulo_errota-2.jpg', 43.34522247314453000000, -2.63809204101562500000, 12, 123, 19, 'ager');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(183, '2012-04-18 14:18:32', 'Betizuaren lagunak', 'Familia-plana, emozioz betetako goiza behiekin,zezenekin,poniekin,... kontaktu zuzenean ta nekazal mundura hurbiltzeko.Saka-perlakua ganadutegia bisitatuz makila eskuan ganaduzaletxo bihurtuko gara... Inf.+:debanatura@hotmail.com.Interesgarria!', 'www.debanatura.net', 'mini_betizuaren-lagunak.jpg', 43.29532623291015600000, -2.35004425048828120000, 12, 54, 19, 'Teleixa');");
