	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(186, '2012-04-19 00:56:06', 'San Fermin', 'San Fermin hauetan etorri Gazpe Peñarekin:\r\n\r\nLokala. Kalea, batez ere Jarauta eta Deskalzosen\r\n\r\nEkintzak:\r\nKalekontzertuak\r\nElektrotxaranga\r\nEntzierro Gaupasero (mozkor baldin bazaude entzierroaren orduan, hator gurekin!)\r\nDantza Herrikoi Parte H', 'www.iruindarrok.net', 'mini_farra-da-bide-bakarra-v3.png', 0.00000000000000000000, 0.00000000000000000000, 0, 97, 10, 'Iruindarrok');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(188, '2012-04-20 11:05:01', 'Otoio mendia', 'Otoio mendia. Eguraldi onarekin kostalde guztia behatzeko leku paregabea. Lekeitiotik igotzeko Talaiako bidea hartu eta asfaltozko bidea bukatutakoak ezkerreko bidea hartu handik gora igo. Ispaster edo Zatikatik ere igo daiteke.', '', 'mini_dscn1535.JPG', 43.37091064453125000000, -2.52822875976562500000, 15, 127, 14, 'ale');");
