	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(190, '2012-04-21 09:20:58', 'Holtzarte', 'Larraineko patar zolan Logibar ostatuan esker har eta aparkalekutik aitzina, zubiaraino bide errexa, euriarekin lohitsua, eta zubitik Oladübiko arroiletaz ikuspegi ederra. Berriz itzul: 3 ordu\r\nSegituz, zubia pasa eta esker, Oladübiko zubiaraino', '', '', 0.00000000000000000000, 0.00000000000000000000, 0, 156, 19, 'Ahozabal');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(191, '2012-04-22 18:01:44', 'Arrano (Herriko taberna)', 'Musika plazan dago, eta goiko pisuan normalean bandera batzuk egoten dira jarrita kalean. Terraza dauka eta jaten ere oso ondo ematen dute: bokadiloak, razioak, arroz bereziak... Baina benetan gomendagarriak etxean egindako pizzak dira. Prezio herrikoiak.', '', 'mini_zarautz-erriko-taberna.jpg', 43.28593063354492000000, -2.17660450935363770000, 19, 39, 7, 'saapmifree');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(192, '2012-04-22 18:19:08', 'San Telmo', 'Hiriko eraikin zaharrenetakoa eraberrituta, Parte Zaharrean. Erakusketa ezberdinak jartzen dituzten, baina batez ere, erakusketa iraunkorra benetan interesgarria da, Donostiako eta Euskal Herriko historia politiko eta kulturala. Asteartetan doan da', 'http://www.santelmomuseoa.com/index.php?lang=eu', 'mini_santelmo.jpg', 43.32519149780273400000, -1.98469519615173340000, 17, 30, 11, 'saapmifree');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(193, '2012-04-22 18:26:45', 'Alaia', 'Ezaguna da Antigua auzoan, Matia kalean bertan, auzoko tortila pintxorik onenetakoak egiteagatik. Bestelako pintxo eta boileria ere izaten dute. Barnean mahai handiak ere badauzka.', '', 'mini_alaia1.JPG', 43.31343078613281000000, -2.00274109840393070000, 17, 30, 7, 'saapmifree');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(194, '2012-04-22 18:31:55', 'Chopper', 'Amara auzoan, Anoeta ondoan dago. Gaztegiroa egoten den arren, ezaguna da motozale  asko ibiltzen delako beren moto ''katxarroekin''. Dekorazioa ere sintonian. Sandwich, hanburgesa eta bokadilo onak izaten dituzte, prezio onean. Kanpoan terraza ere badu.', '', 'mini_chopper.jpg', 43.30451965332031000000, -1.97200298309326170000, 16, 30, 7, 'saapmifree');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(195, '2012-04-24 23:59:24', 'Metro Taberna', 'Erribera kaleko Kupela plazan (saltsa erdian) kokatzen da. Jende jatorrarekin, herri-giroa.\r\n\r\n· Zumaian festa tokatzen denetarako ordutegi zabala\r\n· Pintxo eder eta anitzak (ezberdinak)\r\n· Ogitarteko (''Berezia'' gomendatzen da), burge', '', 'mini_metro_taberna.jpg', 43.29697036743164000000, -2.25682139396667500000, 17, 40, 7, 'Sesma');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(196, '2012-04-25 02:15:59', 'Txokoto', 'Elizondoko zentruan dagoen taberna da, Giro jatorrekoa. Jatetxe moduan ere agitz gustagarria gertatzen da, astetartez menua dago, ta asteburutan karta goiko jantokian.Etxeko moduan sukaldatzen dute, oso komenigarria.', '', 'mini_1343fototh1.jpg', 43.14557266235351600000, -1.51907444000244140000, 18, 96, 1, 'jaimeurrutia57');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(197, '2012-04-25 22:44:48', 'Almadiaren eguna', 'Antzinean, ibaxeko almadikariek egitan ztein korla zÛra ermaitako. Ugaltean beiti xoaitan zren kuekin, Zaragozaraino, eta banak Tortosaraino ere bai, zÛren saltako. Lan gogorra zen gogorrik bada. Orai Burgin urtean egun batean guartan dei nola', 'http://www.almadiasdenavarra.com/quees.php', 'mini_almadikariak-burgin.jpg', 42.71548843383789000000, -1.00421905517578120000, 12, 168, 11, 'uritz');");
