	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(199, '2012-04-27 00:21:10', 'Kultura', 'San Cristobal gotorlekua Ezkaba mendian kokatuta dago, Espainiako Gerra Zibilean kartzela bat izan zena. Natura eta historia uztartzeko aukera aproposa. Ezin da sartu barrura militarren menpe dagoelako, baina badira zirrikituak barrura sartu eta ikusteko.', '', 'mini_fuerte-san-cristobal-3.JPG', 42.83481597900390600000, -1.67266845703125000000, 14, 86, 19, 'elkartetarra');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(200, '2012-04-28 14:08:17', 'Tolosa Indoor Karting', 'Kotxezale guztientzat, adrenalina soltatzeko leku aproposa!! Bota itzazu pike batuk lagun artean, umeekin gidatzeko aukera kart biplazarekin... Seguru errepikatuko duzula!', 'http://www.tolosaindoorkarting.com/', 'mini_ficha_50-dtomaxima-velocidad-100-adrenalina-dos-rondas-de-karts-en-tolosa-indoor-karting.jpg', 43.11719894409180000000, -2.09043622016906740000, 15, 46, 19, 'txarlygraffo');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(201, '2012-04-28 14:15:50', 'Azkizu auzoa', 'Igo zaitez Azkizu auzora eta gozatu bertako leku eta paisaiaz. Inguruan badira jatetxeak, txakolindegiak... Pake santua lortzeko bidea.', '', 'mini_1001997_m.jpg', 43.30327606201172000000, -2.23041248321533200000, 15, 45, 19, 'txarlygraffo');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(202, '2012-04-28 17:02:36', 'Ibilbidea artikutzan barrena', 'Artikutza Etxaldea Goizueta herriaren lurretan dagoen 3.700 hektareako lursaila da. Añarberen goi ibarra den hau, Donostiako Udalarena 1919tik, natura, paisaia, arkeologia eta etnografia aldetik interes handiko ingurunea da.', 'http://www.argia.com/argia-astekaria/1908/artikutza-etxaldea-baso-ederrak-aztarna-historikoen-gordeleku', 'mini_artikutza.jpg', 43.21312332153320000000, -1.79640650749206540000, 18, 180, 19, 'Portolo');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(203, '2012-04-29 11:22:38', 'Goiko kale', 'Ogitarteko, kazuelita, razio eta sandwich aukera ederra prezio onean eta azkar samar. Eserita jateko lekua ez da handia izaten, horregaik udara aldean terrazan mahai ugari jartzen dituzte kanpoaldean. Kokalekua: Urretxuko aldapan goraka goazela eskuinera', '', 'mini_indice.jpg', 43.09297180175781000000, -2.31452202796936040000, 18, 161, 1, 'Alaken');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(331, '2013-03-01 10:34:05', 'IZASKUN JATETXEA', 'Uzturre menditik bueltatxo bat eman ondoren, ez zaitez etxera joan  Izaskun Jatetxean kroketa razio bat jan gabe.Mundialak daude!', 'http://www.restauranteizaskunjatetxea.com/', 'mini_349736-p7280508.JPG', 0.00000000000000000000, 0.00000000000000000000, 0, 46, 1, 'olatz');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(205, '2012-04-29 11:36:52', 'Motx', 'Urretxuko aldapan gora goazela dagoen lehen taberna da. Ekintza ugari antolatzen dituzte, bertso-saioak, hitzaldiak eta kontzertuak, tarteko. Pintxoak egoten dira goizetik, baita zerbeza aukera zabala ere.Rockera ohituta daudenetzako aproposa dena taberna', '', '', 43.09242248535156000000, -2.31481170654296880000, 18, 161, 7, 'Alaken');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(206, '2012-05-02 12:12:45', 'Ibiza', 'Taberna eta jatetxea aldi berean. Gazte jendea ibiltzen da eta futbolina ere badute. Plater konbinatu eta ogitarteko onak. Tabernaria ere oso jatorra da. Herriko futbolzaleak bertan biltzen dira partiduak daudenean.', '', '', 42.55728912353515600000, -1.85118556022644040000, 18, 204, 7, 'saapmifree');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(207, '2012-05-02 12:16:28', 'Katxetas', 'Lizarrako herriko taberna. Jaten ongi ematen dute eta plater konbinatuak dituzte 10 euroan. Ogitarteko eta razioak ere badituzte. Kanpoan mahaiak dituzte toldoarekin. Euskaldun jendea ibiltzen da bertan. Izar kalearen amaieran dago.', '', 'mini_1142fototh1.jpg', 42.67222213745117000000, -2.02942895889282230000, 18, 107, 7, 'saapmifree');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(208, '2012-05-02 12:19:07', 'Roncesvalles', 'Ogitarteko on, handi eta merkeak; pintxoak ere bai. Santiago enparatzaren ondoan. Kanpoan mahaiak ditu.', '', 'mini_83a.jpg', 42.67274093627930000000, -2.03280329704284670000, 17, 107, 7, 'saapmifree');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(209, '2012-05-02 12:26:42', 'El Portillo', 'Platerak, razioak, ogitarteko mordoa, pintxoak... herriko plaza nagusia den Carlos IIIn ondoan. Leku merkea da eta azkar ateratzen dute jatekoa. Kanpoan terraza du. Ez da plazako beste jatetxeetan adina turista jende ibiltzen. Gomendagarria.', '', '', 42.48125076293945000000, -1.65157043933868400000, 19, 179, 7, 'saapmifree');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(210, '2012-05-02 17:33:34', 'Memoriaren Parkea', 'Larragan 2012an Memoriaren parkea ireki zuten herriaren sarreran, guda zibilean egindako sarraskiak (Maravillasen kasua ezaguna da, bertan gertatua) eta ondorengo zapalkuntza oroitzeko. Leku xumea da, baina duintasunez betea.', '', 'mini_arwglsyciaezapg.jpg', 42.56431198120117000000, -1.84912824630737300000, 18, 204, 14, 'saapmifree');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(211, '2012-05-03 00:37:35', 'MATER ITSASONTZI MUSEOA', 'ITSAS GELA elkartea, Euskal itsas ondarea ezagutzera ematen lan egiten duen elkartea da. Horretarako Pasaiako badian itsasoarekin loturiko zenbait jarduera eskaintzen ditu, baina bereziena museoan bilakaturik dagoen MATER ohiko arrantza ontzia da.', 'www.itsasgela.org', 'mini_ninos.jpg', 43.32587814331055000000, -1.92179203033447270000, 15, 34, 11, 'lierni');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(212, '2012-05-04 12:00:56', 'Arditurriko bidegorria + mehategia', 'Oiartzungo Eusko Tren geltokitik hasita, bidegorri hartu eta Oiartzun osoa gurutzatu daiteke Aiako Harriko mendi magaleraino (8 km malda suabe suabe batean). Bertan Arditurriko Meategi zaharrak daude. Bisita gidatuak egiten dira lur azpiko zuloak ikusteko', 'www.arditurri.com', 'mini_1272037349_3.jpg', 43.28428268432617000000, -1.80446147918701170000, 16, 32, 19, 'txomin');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(213, '2012-05-05 11:33:56', 'Lazkaomendi', 'Lazkao eta Zaldibia artea kokatutako auzo zoragarria. Aralarko mendilerroaren bista ikusgarriak ditu eta Txindoki mendia parez pare altxatzen da. Badira bi jatetxe, etxeko janaria (fritoak, entsaladak, kostila..), Ixurrene eta Albitxu. Paseo ezinbestekoa!', '', 'mini_3693817815_6594a1573c.jpg', 43.03245162963867000000, -2.16096997261047360000, 15, 196, 19, 'dmz');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(214, '2012-05-05 11:40:35', 'Narrika Taberna', 'Alde Zaharreko taberna mitikoa, Donostiako bokata onentxuenak nere ustez. Plantxan maisulanez egindako bokatak; Konsti, Bretxa eta Sarriegi (arrautza prejitua urdaiazpiko eta tomate saltxa pikantez,mmmm!). Brabak eta mejilloiak mundialak!!!', '', 'mini_argazki300php.jpg', 43.32379531860351600000, -1.98419094085693360000, 16, 30, 1, 'dmz');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(215, '2012-05-05 18:12:45', 'Xibero ostatua', 'Gurutze Xuriaren ondoan, zabalik asteartetik igande goizeala. Margarita (eüskara+): 05 59 28 43 41', '', 'mini_cafexibero.jpg', 43.21945571899414000000, -0.88976383209228520000, 15, 78, 14, 'Ahozabal');");
