	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(220, '2012-05-05 19:57:47', 'Bolunburuko gunea', 'Zallatik hasi eta Balmasedarako bidean kokatzen dan inguru natural eta historikoa dogu. Ibarra auzoa pasa, eta Balmasedarako bidean trenbidea pasa eta gero aurkitzen den eremu historiko eta naturala. Aisi gunea dogu bertan, kastro historikoa aurrean duela', '', 'mini_bolumburu.jpg', 43.20228576660156000000, -3.16781759262084960000, 16, 121, 14, 'Hezurtxo');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(221, '2012-05-05 20:03:37', 'Terreros dorrea', 'Zallako La Herrera auzoan kokatzen dan dorrea dogu. Terreros dorrea Enkarterrietan aurkitzen diren defentsa dorreetariko hobeen kontsebatzen danatariko bat dogu. Terreros Dorreak, La Herrera-ko monumentu multzoa osatzen du, Urrutia Jauregiarekin bat.', '', 'mini_terreros.jpg', 43.20758819580078000000, -3.17110061645507800000, 16, 121, 14, 'Hezurtxo');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(222, '2012-05-07 11:20:44', 'Arantzako aterpea', 'Herriaren amaieran dago. Eguneko menua izaten dute, asteburuko menu bereziak, kartara... Oso ongi jaten da. Taberna ere badu, eta goiko pisuan aterpea 14 logelarekin. Kanpoan terraza. Giro euskalduna.', 'http://www.arantza.net/', 'mini_arantza_2010-021.jpg', 43.19796371459961000000, -1.72483587265014650000, 16, 207, 1, 'saapmifree');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(224, '2012-05-14 12:40:48', 'Urko mendia', 'Eibar inguruan txangoa egiteko mendi aproposa. 793 metro. Amaña auzotik igo daiteke.', 'http://eu.wikipedia.org/wiki/Urko_mendia', 'mini_urko_4.JPG', 43.18514633178711000000, -2.48094892501831050000, 15, 51, 19, 'txarlygraffo');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(225, '2012-05-14 12:57:59', 'Arrate auzoa', 'Aldapa goran 7km-ra dagoen auzunea. Bertan santutegia, belardi zabalak eta hiruzpalau jatetxe. Umeekin plana egiteko egokia. Urtean zehar Arrateko kotxe rallya eta bizikleta txapelketa desberdinak. Oinezko asko aldapa igotzen, hau ez dut gomendatzen.', 'http://eu.wikipedia.org/wiki/Arrate', 'mini_arrate.JPG', 43.20545959472656000000, -2.44699168205261230000, 13, 51, 19, 'txarlygraffo');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(226, '2012-05-14 13:17:27', 'Ibaetako Trinketea', 'Prezio egokian instalazio paregabeak: jakuzzia, padel pistak, trinketea, piszina-spa, gimnasioa... eta inportantia ere bertakoak oso jatorrak direla esatea. Kirola egin ondoren porroia zerbezaz betea eskainiko dizute, momentuan munduko gauzarik onena!!!!', 'http://www.ddonosti.com/comercio/trinkete-ibaeta', 'mini_trinkete1.JPG', 43.30031204223633000000, -2.01515436172485350000, 17, 30, 19, 'txarlygraffo');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(227, '2012-05-14 13:17:45', 'Egia', 'Bizitza asko daukan auzoa. Kristina Enea parkea, Gasteeszena, Bukowski, jatetxeak (begetarianoak, kreperiak, eguneko menua jateko lekuak...). Familiarekin naiz lagunekin joateko auzo egokia.', '', 'mini_206-031495-115g.jpg', 43.31563949584961000000, -1.97174549102783200000, 15, 30, 19, 'Maite');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(228, '2012-05-14 21:26:21', 'Euskal Museoa', 'Ia 100 urte dituen Euskal Museoa aukera aparta da Baionara bisitan doan edonorentzat. Gida adeitsuek lagunduta, erakusketa arras interesagarri eta entretenigarriak daude ikusgai.', 'www.museebasque.com/eu', 'mini_baionako-euskal-museoa-2.JPG', 43.49116516113281000000, -1.47384166717529300000, 16, 69, 11, 'Simontxo');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(229, '2012-05-15 17:09:28', 'Plazaola bizikletaz', 'Leitza-Lekunberri Plazaolako bidea bizikletaz. 26 bat kilometro (joan etorria). Ze ederra! Iruñeko San Frantzisko eskolatik joan ginen maiatzaren 13an 50 bat lagun, ume eta nagusi.7 urtetik aurrerako umeek egin dezakete. Eraman argia (tunelen batek', 'http://www.plazaola.org/natur-bidea.html', 'mini_plazaola-tunela.JPG', 43.07879257202148400000, -1.91462516784667970000, 16, 91, 19, 'Xabier');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(230, '2012-05-16 12:01:15', 'Arrobi', 'Santa Barbara mendiaren magalean dago ostatua, paraje bikainekin. 3 pertsona bitarteko gelak eskaintzen ditu. \r\nHorrez gain, ostiral eta larunbatetan afariak ematen dituzte prezio onean; plater konbinatuak, entsaladak... Telefonoa: 943 337 211', 'arrobiostatua.com', 'mini_gela.jpg', 43.26728439331055000000, -1.98243141174316400000, 18, 33, 17, 'iratisarasua');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(231, '2012-05-16 15:48:58', 'Manex Kanpinga', 'Senpereko aintziratik oso hurbil kanping txiki eta lasaia.', 'http://www.campingmanex.com/', 'mini_accueil11.jpg', 43.35805892944336000000, -1.54070377349853520000, 15, 65, 18, 'Jule');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(232, '2012-05-16 23:58:30', 'Xoxoteko aterpea', 'Xoxoteko aterpea Izarraitz mendilerroan aurkitzen da. Etxeko janaria eskeintzen dugu eta bertan lo egiteko aukera daukazue baita ere. Oinez igo behar denez, izerdi pixkat bota eta naturaz eta lasaitasunaz gozatzeko aukera ezin hobea da!', 'www.xoxotekoaterpea.com', 'mini_xoxoteko-aterpea.JPG', 43.20717620849609400000, -2.29339599609375000000, 9, 42, 17, 'xoxotekoaterpea');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(233, '2012-05-17 11:07:11', 'Herria taberna', 'Ikatz kalean dago eta parranda baino lehen afaltzeko taberna bikaina da. Merkea eta etxeko janaria.(Kroketak oso onak daude)', '', '', 43.32395172119140600000, -1.98534810543060300000, 19, 30, 7, 'Leiregartzi');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(234, '2012-05-18 18:05:03', 'San Juan Xar', 'Erreserba natural izendatu zuten 1987an, bertan bakarrik hazten den zuhaitz mota bati esker. Koba baten barruan San Juanen aldeko aldarea dago, berezia dena. Bertako iturriak ur miragarriak omen ditu, sendagarriak. Ingurune zoragarria, lasaia, basatia.', '', 'mini_san-juan-xar-igantzi_114579.jpg', 43.20936584472656000000, -1.70856535434722900000, 13, 93, 14, 'zozaia');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(235, '2012-05-18 20:38:34', 'Atxaspi', 'Lesakaren erdian, Euskal Herria ezagutzeko eta hartaz gozatzeko era lasai, dotore eta osasungarria.', 'www.atxaspi.com', 'mini_gauez-1mb.jpg', 43.24691009521484400000, -1.70706248283386230000, 19, 92, 16, 'Igoa');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(236, '2012-05-19 17:57:35', 'Marruzuri', 'Taberna bihurtutako okindegia dela esan genezake. Bokatak, entsaladak edo pizzak aurkitu ditzakezue bertan prezio onean. Mendira abiatu aurretik bertan gosaldu dezakezue goizean goiz, eta eramateko bokata batzuk eskatu. Tabernaria euskalduna da.', '', 'mini_1345_1.jpg', 42.86199569702148400000, -0.92237621545791630000, 19, 166, 7, 'baratxuri');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(237, '2012-05-19 18:45:25', 'Puntetako kantina', 'Pasai Donibane herrixka osorik gurutzatu ondoren pasealekua segi eta egurrezko tabernatxo batera helduko zara. Eguzkiaz berandu arte gozatzeko txokoa. Udaran entsalada eta oilasko ederrak. Prezio merkeak eta giro aparta. Ingurutik bueltaxka eman.', 'http://www.pasaia.net/eu/html/9/2383.shtml', 'mini_7244es.JPG', 43.33235168457031000000, -1.92331552505493160000, 18, 34, 7, 'txarlygraffo');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(238, '2012-05-19 19:03:59', 'Begiratokia', 'Hondartza bukaerako goi batean belardiz betetako begiratokia aurki dezakezu. Bertatik kostalde guztia ikus daiteke. Aparta belarretan etzanda egoteko eta familian umeekin batera joateko.', 'http://eu.wikipedia.org/wiki/Donibane_Lohizune', 'mini_indizea.jpg', 43.39941024780273400000, -1.66404247283935550000, 18, 57, 14, 'txarlygraffo');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(239, '2012-05-21 17:45:06', 'Zarika', 'Antiguo auzoan, hondartzatik 5 minutura, terraza handiko taberna txikia. Oso gustora egongo zara bertan, birra bat hartzen. Ogitartekoak, razioak, sandwichak, plater konbinatuak eta baita tostak ere. Gomendagarria. Futbol partiduak bertan ikus ditzakezu', '', '', 43.31184768676758000000, -2.00066804885864260000, 20, 30, 1, 'abel');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(240, '2012-05-21 19:10:20', 'Bekoetxe', 'Etxean bezala jaten den jatetxe merkea. Klasikoak, entsalada eta oilaskoa; etxeko postreak oso onak. Umeekin joateko ezinhobea. Bertan jateko edo eramateko ere bai. Oso jende jatorra, eta ondo antolatuta. Terraza bi aldetara, udarako primerakoa.', 'http://www.merenderobekoetxe.com/', 'mini_polleria.jpg', 43.25820541381836000000, -2.26455688476562500000, 11, 50, 1, 'zozaia');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(241, '2012-05-24 11:40:02', 'Kabigorri Ateneoa', 'Poesia-errezitaldiak eta kontzertuak antolatzen dituzte, eta edariez gain, janaria ere ematen dugu. Peña kalearen lehenbiziko zenbakian gaude, Irunen.', 'www.kabigorri.org', '', 43.33781814575195000000, -1.78664493560791020000, 15, 31, 11, 'xorino');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(242, '2012-05-27 17:30:58', 'Zamalbide Jatetxea', 'Jaiegun eguzkitsuetarako leku ezin hobea. Taberna eta jantokiaz aparte hiru terraza ditu. Giro lasaia dago, herri barneko kotxeen eta jendetzaren zaratatik urrun. Plater konbinatuak, 17 euroko menu elaboratua edo karta eska dezakuzu. Nahiago baduzu, trago', 'http://hosteleriadeuskadi.com/es/hosteleria/establecimientos/2648/general/', 'mini_180025_122894474449942_117768334962556_169527_5220397_n.jpg', 43.29126358032226600000, -1.90599918365478520000, 14, 49, 1, 'aitzi');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(243, '2012-05-29 11:17:51', 'Mogambo Aretoa', 'Donostia eta Pasaia artean kokatutako lokal autogestionatu honek astebururo zuzeneko musikaz gozatzeko aukera eskaintzen du. Donostia eta inguruetako musika alternatiboan erreferentea da. Giro eta xarma berezia dituen aretoa da eta garagardoa €1ean dute!', 'https://www.facebook.com/d.i.y.mogambo', '', 43.32426834106445000000, -1.93790674209594730000, 14, 30, 11, 'zapatazar');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(244, '2012-05-31 11:38:16', 'Irlara egunpasa', 'Goizean goiz Portutik 20 bat minuturo ateratzen den barkuan igo eta eguna Santa Klara irlan pasatzea plan izugarria da, batez ere itsas behera dagoenean eta eguraldi ona egitean. Paisaia ezin ederragoa, edozeinekin disfrutatzekoa.', '', 'san_sebastian_from_igeldo.jpg', 43.32155609130859400000, -1.99882507324218750000, 14, 30, 19, 'mmandarine');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(245, '2012-05-31 11:41:52', 'Ezkurra', 'Gurasoak eta agian aiton-amonak ere biltzen diren taberna horietakoa, bai, baina tratua, entsaladilla pintxoa eta kroketa izugarriak dituena. Lagunekin afaltzeko oso ongi dago, batez ere prezio nahiko baxuak daudelako.', '', '', 43.32233810424805000000, -1.97304368019104000000, 18, 30, 7, 'mmandarine');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(246, '2012-05-31 11:45:17', '', '', '', '', 0.00000000000000000000, 0.00000000000000000000, 0, 30, 0, 'mmandarine');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(247, '2012-05-31 14:07:34', 'Zinema Corner', 'Groseko Trueba zinema ondoan dagoen taberna da, non mementoan karteleran dauden filmen posterrak eta pelikula mitikoen argazkiak jartzen dituzten. Lehioari pegaturik dagoen sofan pintxoak edota sandwichak jateko toki aparta.', '', 'zinema-2.jpg', 43.32233428955078000000, -1.97473883628845210000, 17, 30, 7, 'mmandarine');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(248, '2012-05-31 14:11:26', 'Mariscos el Puerto', 'Portuan kokatzen den itsaski denda, prezio nahiko eskuragarriekin. Denetarik daukate: ganbak, kiskillak, karrakelak, nekorak...\r\nGomendioa: kiskilla edo karrakela batzuk hartu eta Urgullen jatea itsasoari begira.', '', '', 43.32358932495117000000, -1.98803722858428960000, 18, 30, 12, 'mmandarine');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(249, '2012-05-31 14:18:44', 'Victoria Eugenia antzokia', 'Aspalditik martxan daraman antzokia da, ederra kanpotik zein barrutik. Duela urte gutxi batzuk berritu zuten eta gaur egun Urumea ibaiko bista aberasten jarraitzen du. Ia beti dago ikusteko zer edo zer: antzerkiak, kontzertuak... Gazteentzako deskontuekin', '', '', 43.32286071777344000000, -1.98075771331787100000, 18, 30, 11, 'mmandarine');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(250, '2012-05-31 14:34:39', '', '', '', '', 0.00000000000000000000, 0.00000000000000000000, 0, 30, 0, 'garateker');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(251, '2012-05-31 14:48:13', 'Larra', 'Ikatz kaleko taberna txiki, baina handia. Pintxo eta bokata eleganteak, handiak eta merkeak, bereziki \"goxua\" izenekoa.\r\nPoteatzeko taberna ona eta parranda botatzeko ezin hobea.\r\nKañak goraino betetzen dituzte', '', '', 43.32413482666015600000, -1.98468446731567380000, 19, 30, 7, 'garateker');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(253, '2012-05-31 19:28:42', 'Kantina taberna (Itzurun)', 'Gipuzkoako ikuspegirik politenetakoaz disfrutatuz, bazkaldu edo afaltzeko leku atsegina. Plater konbinatuak, otartekoak edo razioak. Hondartzan bertan, terraza ederra egunez zein gauez disfrutatzeko, Flisch-en artean. Eguzkia itsasoan sartzen ikusten da!', '', 'mini_kantina.jpg', 43.29991531372070000000, -2.26056575775146500000, 17, 40, 7, 'zozaia');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(254, '2012-06-01 12:43:16', 'Larramendi torrea', 'Gaua pasatzeko toki aproposa da Azkoitia bisitatzera etortzen den edonorentzat. \r\nPizzak, bokatak eta pintxoak jateko ere aukera zabala dago.', 'http://www.larramenditorrea.com/', 'mini_hotel-larramendi-torrea.jpg', 43.17124176025390600000, -2.30430173873901370000, 15, 43, 16, 'axkottiarra');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(255, '2012-06-03 19:19:57', 'Itzalargiko borda', 'Otordutarako leku xarmangarria. Herritik bi kilometrotara kokatuta dagoen jatetxe honek naturaz eta lasaitasunaz gozatzeko aukera ematen dizu. Neguan sutondoan mahai jokotan aritu edo patxadaz solas egiteko leku paregabea, beti musika onaz lagundurik.', 'www.itzalargikoborda.com', 'mini_51_itzalargiko.jpg', 43.13119125366211000000, -1.49777770042419430000, 15, 94, 1, 'begizulo');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(256, '2012-06-03 19:34:32', 'Intza', 'Euskal besta giroa, bokatak eta plater konbinatuak afaltzeko aukera, baita begetarianoentzako anburgesak (merkea). Zerbitzari jatorrak eta neska ederrak.', '', '', 43.14546585083008000000, -1.51838243007659910000, 18, 94, 7, 'begizulo');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(257, '2012-06-05 11:50:51', 'Zulua', 'Gabetarako ezkutalekurik onena trago batzuk lasai hartzeko. Eguzkija noz urteten dan ez enteratzeko moduko zulua da Zulua. Gazte anbiente ederra eta juerga gogoa daukon guztijentzako be txoko aproposa!Ostiral guztijetan happy hour!', '', '', 0.00000000000000000000, 0.00000000000000000000, 0, 127, 7, 'Txo');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(258, '2012-06-08 08:22:36', 'Ramon', 'Sagardotegi eder bat dago Legorretan(Aulia Sagardotegia).Irekia Abenduan 13 tik Maiatzan azkeneko astea.Parranda egiteko toki egokia.', 'www.auliasagardotegia.com', '', 0.00000000000000000000, 0.00000000000000000000, 0, 181, 1, 'Ramon');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(259, '2012-06-08 15:45:31', 'Ikatz kalea', 'Alde zaharreko Juan de Bilbao kalea, Ikatz bezala ezaguna dena, auzoko bihotzaren zatia diren tabernez josia. Denetariko musika eta edaria giro euskaldunean.', '', '', 43.32419967651367000000, -1.98436641693115230000, 17, 30, 10, 'mmandarine');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(260, '2012-06-11 11:00:25', 'Karrerane Kultur etxea', 'Zaldibiako Kultur etxea', 'www.zaldibia.org/kulturetxea', 'mini_logo-kulturetxea.jpg', 43.03882598876953000000, -2.15109801292419430000, 18, 185, 11, 'oidui');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(261, '2012-06-11 11:10:17', 'Saturdiko ermita', 'Saturdiko ermitan bista kategorikoak Txindokira. Aberri egunaren ondorengo astelehenean ospatzen dira bertako festak.', 'www.zaldibia.org', 'mini_saturdiko-ermita.jpg', 43.03168869018555000000, -2.12879276275634770000, 17, 185, 14, 'oidui');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(262, '2012-06-11 11:12:34', 'Lazkao Etxe', 'Nekazalturismoa eta jatetxea', 'http://www.lazkaoetxe.com/', 'mini_lazkao-etxe.jpg', 43.01465225219726600000, -2.14694595336914060000, 16, 185, 1, 'oidui');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(263, '2012-06-11 11:13:56', 'Joxepane Herri ostatua', 'Zaldibiako Herriko taberna', '', '', 43.03805160522461000000, -2.15094780921936040000, 18, 185, 7, 'oidui');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(264, '2012-06-11 15:24:42', 'Aiako Harria', 'Bisitarako toki zoragarria. Bidegorria amaiera arte jarraituta helduko zara. Bertan ibilbide ezberdinak daude bikain markatuta eta nahi izanez gero mendiak azpikaldetik zeharka ditzakezu, baso ederrean zehar; edo bestela, hiru tontorrak ere uki ditzakezu.', 'http://euskal-herria.org/node/9', 'mini_aiako.jpg', 43.28317260742187500000, -1.79944038391113280000, 13, 32, 19, 'abel');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(265, '2012-06-12 01:17:13', 'Trinkete Antitxokoa', 'Zer da Trinkete? Antitxokoa, trinketea, taberna, lagunen txokoa? Zertarako definitu ezagutu BEHAR duzun toki interesgarri hau? Asteburuetan performanz kimerikoak, kontzertu sikodelikoak, antzerki-obra ahaztuak eta lagun kitzikagarri. Zatoz Trinketera!', 'www.facebook.com/trinkete.antitxokoa', 'mini_trink.jpg', 43.31331253051758000000, -2.67544460296630860000, 17, 123, 11, 'ager');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(267, '2012-06-18 12:49:04', 'Etxeberria', 'Hondarribiko kale nagusian dago eta atzeko atetik Apezpiku plazara ematen du taberna honek. Udako eguzkiarekin primeran egoten da plaza horretako terrazan. Giro lasaia, oso aproposa lagunartean hizketan aritzeko.', '', 'mini_etxeberria.jpg', 43.36210632324219000000, -1.79136419296264650000, 20, 35, 7, 'boligorria');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(268, '2012-06-18 13:02:07', 'The Morgan Kompany', 'Narrika kaleko Morgan jatetxearen anaia bikia, \"slow food\" filosofiari lotutako jatetxea da The Morgan Kompany. Produktu ekologikoekin prestatutako janaria, exotismo puntu batekin eta artelanez inguratuta gozatzeko leku aproposa.', 'http://www.morganjatetxea.eu/Restaurante/kompany.html', 'mini_morgan-kompany.jpg', 43.32417297363281000000, -1.98212563991546630000, 19, 30, 1, 'boligorria');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(269, '2012-06-25 11:39:21', 'Pintxo Potea Easo plazan', 'Ostegunero Easo plazan jendetza handia biltzen da gero eta arrakasta handiagoa duen \"pintxo-pote\"-ak deituta. Hainbat tabernak bat egin dute ekimenarekin. 19:00tatik aurrera.', '', 'mini_imag0124.jpg', 43.31419372558594000000, -1.98243141174316400000, 16, 30, 19, 'abel');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(270, '2012-06-27 13:02:44', 'Tamer', 'Hernanin modu onean afaltzeko leku jada klasikoa da hau. Plater konbinatuak, sandwichak eta udan terrazan afaltzeko aukera. Arraultza frijituarekin egindako sandwicha, espeziala!', '', '', 43.26424407958984400000, -1.97679340839385990000, 19, 33, 7, 'boligorria');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(271, '2012-06-28 11:57:50', 'Arbelaitz Taberna', 'Saturrarango hondartzatik 5 minututara, giro lasai, atsegina, naturarekin kontaktuan. Bokata oso onak ematen dituzte!Lasaitasunez errekatxo alboan afaltzeak ez du preziorik!!', '', 'mini_tabernako-argazkixa.jpg', 43.31604385375976600000, -2.41098999977111800000, 15, 41, 7, 'Alkarb');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(272, '2012-07-05 21:36:20', 'San Matin erlojua', 'Azkoitiko leku aparta vista ederrak ikusteko kokatuta dagoen tontorretik.\r\nAzkoitiko lehenengo biztaleen astarnak aurkitu ziren bertan.\r\nMuseoa ere badu.', '', 'mini_san-martin-dorrea.jpg', 43.17530441284180000000, -2.31233096122741700000, 18, 43, 14, 'marpotto');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(273, '2012-07-06 16:07:51', 'Spa Gym Malecon Zarautz', 'Oso leku txukuna, gorputzari opari bat egiteko bikaina. Zentroak sauna finlandiarra, bainu turkiarra, erlaxatzeko gunea, solarium bertikala eta edertasun zentroa ditu bestek beste. Ordubetek 20€ balio ditu, baina deskontuak ere egiten dituzte.', 'http://www.spamalecon.com/home.html', 'mini_spa-malecon-zarautz-h.jpg', 43.28725814819336000000, -2.17386603355407700000, 17, 39, 20, 'abel');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(274, '2012-07-06 16:22:44', 'Aisia Deba', '1200 metro karratuko talasoterapia ederra. Bi gunetan dago banatuta. 1- Itsasoko ur beroko igerilekua urpeko txorrotadekin, jacuzzia, sauna, lurrun-bainua eta erlaxatzeko gune bat. 2- Oinentzako bainuak, sauna, tenperatura aldaketak dituen dutxak etabar.', 'http://www.aisiahoteles.com/deba.asp', 'mini_he-aisiadeba5-4.jpg', 43.29714202880859400000, -2.35336613655090330000, 18, 54, 20, 'abel');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(275, '2012-07-10 11:28:19', 'Antioko baseliza', 'Baseliza erromaniko-gotikoa, barnealdeko zureria ikusgarriagatik da batez ere ezaguna. Inguruko mendietarako (Izazpi, Beloki, Samiño) abiapuntua, oinezko eta mendiko txirrindularientzat. Herrigunetik 2,5 kilometrora dago (autoentzako aparkalekua du', 'http://www.zumarraga.net/eu/ficheros/37_5261eu.pdf', 'mini_antio-300.jpg', 43.09088134765625000000, -2.30330657958984380000, 15, 160, 14, 'jtorner');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(276, '2012-07-11 13:55:23', 'Piedemonte', 'Nafarroako Ardoaren Ibilbidea proiektuarekin (http://www.rutadelvinodenavarra.com) bat egin du upategiak, jendeari ateak irekita. Beren instalazioetan bisita gidatuak eta ardo katak egiten dituzte. Gainera, dendan beren produktuak erosi ahal dira.', 'http://www.piedemonte.com/', 'mini_piedemonte.jpg', 42.48675155639648400000, -1.65703237056732180000, 13, 179, 21, 'dioniso');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(277, '2012-07-11 14:05:27', 'Marco Real upategia', 'Ardo beltz eta zuri mota ugari egiten du. Bisita gidatuak eta katak antolatzen dituzte. Ardoaren Ibilbidea proiektuko (http://www.rutadelvinodenavarra.com) partaide da.', 'http://www.familiabelasco.com/', 'mini_marco-real.jpg', 0.00000000000000000000, 0.00000000000000000000, 0, 179, 21, 'dioniso');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(278, '2012-07-11 14:10:41', 'Ochoa upategia', 'Bere mahastietako batzuk upategiaren ondoan daude, Erriberrin, eta gainerako gehienak, El Bosque de Traibuenas lursailean. Bisita gidatuak eta dastaketak antolatzen dituzte. Ardoaren Ibilbidea proiektuko (www.rutadelvinodenavarra.com) kide da.', 'www.bodegasochoa.com', 'mini_ochoa.jpg', 0.00000000000000000000, 0.00000000000000000000, 0, 179, 21, 'dioniso');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(279, '2012-07-11 15:35:37', 'Xaramela', 'Jaten ematen duten taberna. Bokadiloak, entsaladak, sandwichak... eta pintxo dotoreak barran. Prezio normalak eta giro ona.', '', '', 43.06265640258789000000, -2.49596714973449700000, 17, 44, 1, 'saapmifree');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(280, '2012-07-12 15:42:18', 'Erri-Berri erretegi-sagardotegia', 'Brasara egindako arrain eta haragiak prestatzen dituzte. Astelehenetik ostiralera menuak ateratzen dituzte, 14 eurotan. Parte zahar erdian dago, gaztelutik oso gertu.', 'http://www.asadorerriberri.com', '', 42.48119735717773400000, -1.65048515796661380000, 17, 179, 1, 'saioaL');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(281, '2012-07-16 01:03:53', 'Kanttu', 'Hemen duzue Hendaiako herrikoa. Bertan euskal giro polita aurkituko duzue eta ez hori bakarrik. Zu bezalako jendearen beharra du momentu honetan Hendaiak. Gauzak ez doaz oso ondo herrikoan diru kontuak direla eta, horregatik azaltzea estimatuko du jendeak', '', '', 43.37206649780273400000, -1.77950882911682130000, 17, 67, 7, 'asur');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(282, '2012-07-16 15:10:32', 'Villa Vieja hostala', 'Hostal oso atsegina, Erriberriko parte zaharrean. Tratua ere atsegina. Gelek (guri eman zigutenak behintzat bai) burbuiladun bainerak dauzkate, egunpasa baten ostean erlaxatzeko primerakoak.', 'http://www.hostalvillavieja.com/', '', 42.48102951049805000000, -1.64885973930358890000, 16, 179, 17, 'saioaL');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(283, '2012-07-20 11:16:43', 'Eskina', 'Nortasun handiko taberna, Moskuko mitikoenetakoa.Urdanibia plazara San Martzial kaletik jaitsi eta iskina egiten lehenengo taberna.Pintxo eta bokadillo goxoak, parrandarako ezin hobea eta inguruko petrila + Moskuko plazan duen terraza beteak egon ohi dira', 'http://www.facebook.com/Bareskina', 'mini_eskina.jpg', 43.33772277832031000000, -1.78654432296752930000, 17, 31, 7, 'mikel');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(284, '2012-07-23 14:47:30', 'Zinka ostatua', 'Azken urteetan ez da egunero irekitzen, baina ostatua kudeatzen duen elkarteak hilero antolatzen ditu kontzertuak. Xiberoko Botza irratiaren egoitzaren ondoan dago, Victor Hugo kalean.', '', '', 0.00000000000000000000, 0.00000000000000000000, 0, 78, 7, 'saioaL');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(285, '2012-07-23 16:29:17', 'Lausset ostatua', 'Santiago Bidean kokatua, ostatu honetan gaua eman eta prezio desberdineko menuak jan daitezke. Telefonoa: 0033 (0)559665303', '', '', 0.00000000000000000000, 0.00000000000000000000, 0, 206, 17, 'saioaL');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(286, '2012-07-27 04:21:28', 'Villabonako Garagardo Azoka', 'Villabonako festen (uztailaren 24tik 31ra) aurreko asteburuan izaten da, ostegunetik igandera. \r\nGipuzkoako garagardo azoka zaharrenetako da. Garagardo aukera haundi, baita janarena ere. Eta nola ez, aparteko giroa!!', '', '', 43.18991851806640600000, -2.05170249938964840000, 17, 165, 10, 'xauxar');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(287, '2012-08-01 14:38:20', 'Zaldi irteerak Mutrikun', 'Mutriku inguruetan ordubete edo bi orduko zaldi irteerak. Plan bikaina familian, lagunekin edo bikotearekin egiteko. Ander bere zaldian aurretik doan ibilbide osoan azalpen interesgarriak emanez, eta gu behintzat oso gustura ibili ginen. Gomendagarria.', 'http://www.yeguadaolmedo.es.tl/Home.htm', 'mini_3715492941_74425449e2.jpg', 43.29918670654297000000, -2.36274719238281250000, 16, 41, 19, 'saapmifree');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(288, '2012-08-02 11:14:49', 'Usotegi landetxea', 'Leku lasaia eta ikusgarria,itsasora eta mahastietara dituen bista zoragarriekin. Etxeak tradizioa eta modernitatea nahasten ditu.Jabeak jatorrak dira eta kotxea hartuta herrian minutu batean zaude,oinez joateko aldapa asko...Bikote batentzat prezio egokia', 'http://www.nekatur.net/usotegi?lang=eu-ES', 'mini_fatxada.jpg', 43.30352401733398400000, -2.21145987510681150000, 16, 45, 17, 'txarlygraffo');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(289, '2012-08-02 11:26:16', 'May Flower', 'Terrazan bazkaltzeko leku aparta. Prezioa aldetik ez da garestiena, ezta merkeena ere, baina kalitate aldetik onenetarikoa. Pertsonako 30 euroren truke bertako arrai frexkua jateko aukera ona. Langileen tratu egokia. Bete bete eginda bukatuko duzu.', 'http://www.minube.com/rincon/restaurante-mayflower-a57175', 'mini_the-may-flower-getaria.jpg', 43.30469131469726600000, -2.20274543762207030000, 20, 45, 1, 'txarlygraffo');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(290, '2012-08-02 11:47:52', 'Pipas', 'Eguraldi oneko egun bat behar bezala aprobetxatzeko lekua. Lazkao mendira igota, Txindoki mendiaren panoramika  perfektuaz gozatzeko aukera. Umeekin joateko egokia da eta txakurrak solte uzteko ere bai. Pintxoak, plater konbinatuak etab. prezioa egokiak.', '', 'mini_txindoki.jpg', 43.03478240966797000000, -2.16889858245849600000, 16, 196, 1, 'txarlygraffo');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(291, '2012-08-05 13:27:37', 'Antzorako kala', 'Izaroko irla parean dago, Ibarrangeluko Antzora auzoan (Lagara bidean). Kala salbajea, nudismoa egin nahi duenarentzat leku aproposa.', '', '', 0.00000000000000000000, 0.00000000000000000000, 0, 192, 19, 'saioaL');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(292, '2012-08-05 13:36:05', 'Boliñaspeko kala', 'Kala salbajea. Marea bajua dagoenean, Laidako hondartzatik oinez joan daiteke hara. Lasaiago egoteko leku ona.', '', '', 0.00000000000000000000, 0.00000000000000000000, 0, 192, 19, 'saioaL');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(293, '2012-08-05 14:17:05', 'Erpidea erretegia', 'Erpidea auzoan, Ullibarri-Ganboako laku ertzean. Menuak ematen dituzte (kartara), baita razioak ere. Lakura begira dagoen terraza leku ederra da mokadutxoa egiteko.', '', '', 42.93798828125000000000, -2.60745763778686520000, 18, 214, 1, 'jtorner');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(294, '2012-08-06 14:37:26', 'Jaizkibeleko Paintball', 'Lezo pasa ondoren Jaizkibelera igotzeko errepidea hartu eta pare bat minututan Paintballera iritsiko zara. Prezioen bataz bestekoa 30-40€koa da. Leku egokia lagun artean zeozer ezberdina egiteko. Esperientzia polita.', 'http://www.jaizkiball.com/', 'tp_jaizkiball.jpg', 43.33349609375000000000, -1.89758777618408200000, 16, 34, 19, 'txarlygraffo');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(295, '2012-08-07 14:55:33', 'Guadalupeko Ama', 'Hondarribitik Jaizkibeleko bidea hartuta kotxean 5 minututan zaude.Lezotik ere jaitsi zaitezke baina horretarako Jaizkibel igo eta jaitsi egin behar.Leku polita,bista zoragarriekin Hondarribi eta Hendaiara. Hondarribitik baldin bazaude bisitatzeko apropos', 'http://eu.wikipedia.org/wiki/Guadalupeko_baseliza', 'mini_320px-guadalupehon.jpg', 43.37175369262695000000, -1.81046962738037100000, 16, 35, 14, 'txarlygraffo');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(296, '2012-08-11 19:08:49', 'Iñakiren Etxea', 'Trebiñu udalerriko Albaita herrian dago, kale nagusian. Lau gelako nekazal etxea da. Telefonoa: 945379054', '', '', 42.68963623046875000000, -2.63478708267211900000, 12, 148, 17, 'dioniso');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(297, '2012-08-11 19:31:32', 'Izkiko natur parkea', 'Trebiñuren mendebaldean dagoen natur parke honetan ongi markaturik dauden hamaika ibilbide egin daitezke. Turismo bulegoa Korres herrian dago (mapak-eta han eska ditzakezue). Parean taberna bat dago (gu abuztuan izan gara eta bazkaririk ez dute ema', 'http://www.alava.net/cs/Satellite?c=Page\&cid=1193047067338\&language=eu_ES\&pagename=DiputacionAlava%2FPage%2FDPA_listado', '', 0.00000000000000000000, 0.00000000000000000000, 0, 148, 19, 'jtorner');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(298, '2012-08-21 09:28:22', 'Zeria', 'San Pedro kale enblematikoan dago 1575ean eraikitako etxe hau. 1965ean zaharberritu zuten eta orduan bihurtu zen jatetxe. Arraina eta itsaskiak dira etxeko berezitasuna. Luxuzko janaria prezio onean.', 'http://www.zeria.net/', 'mini_fachada1.jpg', 43.36752319335937500000, -1.79408669471740720000, 17, 35, 1, 'boligorria');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(299, '2012-08-26 22:39:54', 'Artaza', 'Lizarratik Urbasako mendi zerrarantz goazela aurkituko dugu Artaza herria eta kanpinga. Bailara lasai eta polita. Kanping oso txukuna, xarma handia duena eta lasaia. Urederrako ur ederrak ezagutzera joateko irteera puntu aparta.', 'campingartaza.com', 'mini_cropped-cabezera2.jpg', 42.77112579345703000000, -2.11883783340454100000, 16, 107, 18, 'elur');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(300, '2012-08-28 11:03:52', 'Santa Katalina Ermita', 'Deba gainean, leku zoragarrian kokatuta dagoen ermita da hau. Inguruotan mendi plan lasaia egin nahi duen edonorentzat egokia. Ordubetean egiten da gora eta behera, eta nahi izanez gero. Debatik gora joan, bidea markatuta dago mendian. Bista zoragarriak.', '', 'mini_deba.jpg', 43.29835128784180000000, -2.34273791313171400000, 16, 54, 19, 'saapmifree');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(301, '2012-08-28 11:17:37', 'Kalbeton', 'Taberna eta aldi berean jatetxea. Debako errepide nagusia doan kalean dago. Zerbitzu jatorra eta janari ona. Barran pintxoak dituzte eta jatetxean, janari landuagoa. Eguneko menua, plater konbinatuak, ogitartekoak eta abar. Telf: 943191970', '', 'mini_img_0626.JPG', 43.29524993896484400000, -2.35461473464965800000, 18, 54, 1, 'saapmifree');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(302, '2012-08-28 11:22:46', 'Flixgain', 'Burumendin, \"hirugarren plaiaren\" gainean dago Flixgain taberna edo txiringitoa. Leku zoragarria bertan zerbait hartzeko eta musika entzuten egoteko. Oso berandurarte eguzkitan dago, piszinan eta arranplan gerizpea agertzen denean bertara hurrea', '', 'mini_flix-gain.jpg', 43.31221771240234400000, -2.38189291954040530000, 18, 41, 7, 'abel');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(303, '2012-08-28 11:32:46', 'Saturraran kanpinga', 'Saturraran hondartzatik 5 minutura dago kanping hau, naturaz inguratuta. Leku oso lasaia, deskonexiorako bikaina, giro euskalduna. Tratuan jatorrak eta zerbitzu ona. Udan etortzeko aukera bikaina, eta Mutriku eta Ondarruko jaietan giro handia izaten da.', '', 'mini_img_0650.JPG', 43.31682586669922000000, -2.41131663322448730000, 17, 41, 18, 'abel');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(304, '2012-08-28 13:56:20', 'Mollarri (Malla Harria)', 'Aspaldian mina kargatzeko tokia izandakoa da. Andazarratetik (20 Km inguru) ekartzen zuten mina kabledun bagoietan barrena XX mendean (1913-1924), gero hau barkuetan sartu eta beste lekuetara garraiatzeko. Leku paregabea da itsas bazterrak ikusteko', '', '', 43.29443359375000000000, -2.14885711669921880000, 14, 39, 14, 'urtzai');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(305, '2012-08-28 14:26:31', 'Itsas pasealekua', 'Herriaren punta batetik bestera doan itsas pasealekua. Bertan bista paregabeak izateaz gain terrazadun hainbat taberna, surf eskolak, izoztegiak eta eskultura erakusketa bat ikusteko aukera izango duzu. Pasealekua Getariaraino luzatzen da.', '', 'mini_itsas_pasealekua.jpg', 43.28762054443359400000, -2.16897368431091300000, 14, 39, 14, 'urtzai');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(306, '2012-08-28 14:48:36', 'Euskalduna', 'Kale nagusian kokatzen den jatetxe txiki bat da. Etxeko janari goxoa edota pintxoak prezio onean edukiko duzu eskuragarri eta ostegunetan Kafe Torero kultur elkartearen eskutik musika emanaldiak. Urdai-azpiko kroketa erraldoiak guztiz gomendagarriak dira.', 'http://www.euskaldunataberna.es/', '', 43.28566741943359400000, -2.17425227165222170000, 19, 39, 7, 'urtzai');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(307, '2012-08-29 10:55:02', 'Algorriko hondartza', 'Algorriko labarren artean dagoen hondartza txikia. Udan Zumaiako hondartzetan sortzen den pilaketa masibotik kanpo. Labarrez inguratuta bainu bat hartzeko leku aproposa.', '', 'mini_algorrikohondartza.jpg', 43.29933166503906000000, -2.26749396324157700000, 20, 40, 14, 'Baraka');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(308, '2012-08-29 11:07:29', 'Algorriko labarrak', 'San Telmo baselizatik bidexka bat dago Algorriko labarren muturreraino. Eskumaldean Itzurun hondartzaren ikuspegirik ederrena eta ezkerrean Debara arteko kostaldea ikusgai. Bide erdian Txato-Anuk Ertzaintzak hil zuen militantearen omenezko oroigarria dago', '', 'mini_ermita_de_san_telmo_zumaia.jpg', 43.29964447021484400000, -2.26541781425476070000, 16, 40, 14, 'Baraka');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(309, '2012-08-29 11:23:49', 'Inpernupe Taberna', 'Zumaiako Herriko taberna. Alde zaharreko kale nagusian terraza ederra dauka.Inpernupe Kultur elkarteak tarteka antolatzen ditu kontzertu, hitzaldi eta abarrak.Asteburuan parrandarako txokoa.Ez joan Zumaiatik hango patata tortilla pintxoa probatu barik.', '', '', 43.29711914062500000000, -2.25633597373962400000, 21, 40, 7, 'Baraka');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(310, '2012-08-29 11:39:23', 'Nikol Taberna', 'Metro tabernarekin batera Zumaiako parranda gune nagusia. Berandura arte irekitzen duen taberna handi honetan herriko gazteria biltzen da astebururo. Zumaiako realzaleen topalekua da, aldi berean.', '', '', 43.29816818237305000000, -2.25596714019775400000, 21, 40, 10, 'Baraka');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(311, '2012-08-29 11:48:24', 'Leize Taberna', 'Zumaiako gazteen topalekua. Tabernari jatorrak, solasaldi baterako beti prest eta musika ona jartzen dute. Parrandarako ere gune polita izaten da.', '', '', 43.29929351806640600000, -2.25520396232605000000, 20, 40, 7, 'Baraka');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(312, '2012-08-29 13:08:29', 'Torreberri Gaztetxea', 'Zumaiako gaztetxea. ZUGAk, Zumaiako gazte asanbladak autogestionaturiko eraikin publikoa. Dorretxearen hasierako beheko eta 2. pisuan gazte asanbladak antolaturiko ekimen nagusien gunea da Torreberri. Parean plaza zabal bat eta frontoi berria dauzka.', 'http://galernasarean.info/', 'mini_torreberri.JPG', 43.29522323608398400000, -2.25964164733886700000, 20, 40, 11, 'Baraka');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(313, '2012-08-29 13:15:50', 'Alondegia', 'Zumaiako kultur etxe berritua. Goiko pisuetan liburutegi(doako internet sarrera duena), filmateka, umeen txokoa...eta aparteko bista dauzka. Beheko solairuan mugimendu handia duen(Erakusketa, kontzertu, hitzaldi eta antzezlan...) Oxford areto txukuna dago', 'http://zumaia.net/zumaian-bizi/kultur-etxea', 'mini_alondegia-zumaia-140611-javier-carvallo.jpg', 43.29726409912109400000, -2.25599789619445800000, 19, 40, 11, 'Baraka');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(314, '2012-08-30 17:22:29', 'Mellid Taberna', 'Barakaldoko historia hurbilean bere lekutxoa egin duen taberna. Punk garaiaren bommatik hona gauean etxera joan nahi ez dutenei aterpea eskaini dien taberna. Musika ona eta giro herrikoia.', '', 'mini_mellid.jpg', 43.29550170898437500000, -2.98887395858764650000, 20, 113, 7, 'Baraka');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(315, '2012-08-30 17:31:26', 'Karpe Diem', 'Taberna txikia bezain bizia. Herriko Barakaldo FC eta Athleticzale punkien topaleku.', '', '', 43.29905319213867000000, -2.99172782897949200000, 19, 113, 7, 'Baraka');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(316, '2012-08-30 17:37:15', 'Basterra taberna', 'Denboran 20 urte atzera egitea posible dela frogatzen duen taberna. Punkaren \"urrezko garaietan\" bizi zen giroa izozturik lotu da metro karratu gutxiko txoko honetan. Musika ona, garagardoak eta barreak ziurtatuta.', '', '', 43.29869461059570000000, -2.99181365966796880000, 19, 113, 7, 'Baraka');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(317, '2012-08-30 17:42:50', 'Mezpelerrak / Errekatxo', 'Naturan txertatuta jarraitzen duen Barakaldoko auzo bakarrenetakoa. Azken urtetan txalet zein SuperSur autopistaren inbasioa pairatu badu ere, inguruko erreka eta mendi artean ibilaldiak abiatzeko herrixka egokia.', '', 'mini_errekatxo.jpg', 43.26321029663086000000, -3.01489949226379400000, 18, 113, 19, 'Baraka');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(318, '2012-08-31 13:46:49', 'Dantzari taberna', 'Ardo eta pintxo bereziak. Ardo eskaintza zabala eta pintxoetarako, Gupi, kamareroaren eskua ezin hobea.', '', '', 43.29642868041992000000, -2.98985028266906740000, 20, 113, 7, 'Baraka');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(319, '2012-08-31 13:53:59', 'Casablanca taberna', 'Koktail, batido, konbinatu ederrak... eta musika aukeraketa ona.  121 Krew hip-hop taldeko jendea taberna handi honetan ikustea ez litzaizuke arraroa egin beharko.', '', '', 43.29437637329101600000, -2.99614548683166500000, 19, 113, 7, 'Baraka');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(320, '2012-08-31 14:03:09', 'Sasiburu / Eretza', 'Mezpelerreka edo Gurutzeta auzotik abiatuta zonaldean oso ohikoa den ibilaldia. Tellitutik pasa, Sasiburu mendiraino. Hortik aurrera Eretza mendiraino jarrai liteke. Bidean Castaños eta Saratxoko herriak bisitatu ahal dira. Argibide gehiago estekan', 'http://luberri.net/mendi/Eretza', 'mini_eretza.jpg', 43.25123596191406000000, -2.99909591674804700000, 14, 113, 19, 'Baraka');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(321, '2012-08-31 14:10:31', 'Santurtzitik Portugaletera', 'Santurtziko portutik Portugaleteko alde zaharrera ibarrari lotua doan bidea. Paseo ederra eta lasaia. Bizkaia zubiaren parean amaitu ahal duzu edo ibarra jarraituz Sestaoraino luzatzeko aukera daukazu.', '', 'mini_santurtzi-potu.jpg', 43.32730102539062500000, -3.02284955978393550000, 16, 111, 19, 'Baraka');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(322, '2012-08-31 14:16:23', 'Kultur', 'Herritarrek autogestionaturiko Mamarigako Kultur Etxea. Mamariga auzoaren bizitza kulturalaren bihotza eta herritar askoren topaleku. Ikastaroak, kontzertuak, hitzaldiak... auzotik eta auzoarentzat. Auzoa eta auzokideen historia bizia.', 'http://kulturetxea.jimdo.com/', 'mini_kultur.png', 43.33238220214844000000, -3.04071855545043950000, 19, 111, 11, 'Baraka');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(323, '2012-08-31 20:48:49', 'Subeltz', 'Taberna gomendagarria Mutrikun, bai parrandarako, baita zeozer jateko ere. Pintxo eta otarteko bariatuak izaten dituzte, eta batez ere, plater konbinatu asko eta onak. Giro euskalduna. Arratsaldeetan lasai egoteko lekua, eta parranda egunetan, festagiroa.', '', '', 43.30755996704101600000, -2.38538503646850600000, 20, 41, 7, 'abel');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(324, '2012-09-25 21:34:31', 'Aulia Sagardotegia', 'Tlfonoa 607365743', '', '', 43.08493041992187500000, -2.14822721481323240000, 16, 181, 8, 'Ramon');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(325, '2012-09-28 15:53:21', 'Cassolo', 'Italiar pizza goxo bat jan nahi izan ezkero hau da lekua(Jabea italiarra,baita osagaiak ere).MU Unibertsitate ondoan dago,Arimazuri plazan.Inguruan kotxea aparkatzeko arazorik ez,ta 5-10 min-tan alde zaharrean zaude.Nutela izozkia probatu,earra dau-ta!', '', 'mini_cassolo.jpg', 43.06171417236328000000, -2.49591350555419900000, 16, 44, 1, 'aimare');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(326, '2012-12-06 22:29:10', 'Gorka', 'Berrerikitako eraikuntza batzuk daude:\r\n\r\nAketxe auzoan zegoen Kortesene baserria, Mendibile auzoan. (Mapan)\r\n\r\nOndizeko dorretxea, Ondiz auzoan. Bertan, Ondizeko baseleiza ikustea merezi du.\r\n\r\nElexaldeko ur errota, Elexalde auzoan, San Juan eleizatik hu', '', '', 43.32238388061523400000, -2.97783136367797850000, 14, 150, 14, 'Gorka');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(327, '2013-01-07 18:07:34', 'Memoria Historikoaren Euskal Interpretazio Zentroa', '1936-37ko guda du ardatz Interpretazio Zentroak. Erakusketa iraunkor batez eta ibilbide tematiko batez dago osatuta. Lubakiak, babeslekuak, armak, egunkariak, jantziak... ikus daitezke. Bisita gidatuetarako 943718911, 943796463 edo turismo@debagoiena.net', 'http://www.gipuzkoakomuseoak.net/museos/museo.php?Nmuseo=1320313869\&id=eu', 'mini_dsci0331.JPG', 43.13716506958008000000, -2.48659014701843260000, 17, 209, 14, 'iraitzlazkano');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(328, '2013-02-22 13:35:31', 'Pintxo Potea Grosen', 'Donostian une honetan fenomeno itzela. Jende asko ibiltzen da Groseko tabernetan, ostegunetan batez ere, pintxopotean. Taberna askok egiten dituzte eskaintza bereziak egun honetan.', '', 'mini_p1030814-640x640.jpg', 43.32424163818359400000, -1.97333335876464840000, 16, 30, 19, 'saapmifree');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(329, '2013-02-22 13:48:06', 'Parral', 'Taberna bat eta askoz gehiago, Gasteizko Alde Zaharreko txoko honetan hainbat kultur ekitaldi antolatzen dituzte: kontzertuak, errezitaldiak... Arabako hiriburuko lokal ezinbesteko bat.', 'http://www.myspace.com/parraltaberna', 'mini_parral.jpg', 42.84869384765625000000, -2.67148542404174800000, 19, 136, 7, 'boligorria');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(332, '2013-03-01 14:17:29', 'LA TRAVIESA INTERPRETAZIO ZENTROA', 'La Traviesa jatetxetik hurbil, GR-38 Ardoaren eta Arrainaren Ibilbidea interpretatzeko zentroa dago Zenbait proiekzio, mapa eta panel informatibo ditu.  Gainera zenbait stand, panel eta liburuxka ditu eremua nolakoa den azaltzeko.', 'www.lagran.org', 'mini_latraviesaphp.jpg', 42.62539291381836000000, -2.58358645439147950000, 15, 200, 14, 'ibereziartua');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(333, '2013-03-01 15:27:53', 'Psilocybenea', '2008an jarri zuen martxan Psilocybenea aretoa herriko musika taldeen elkarteak (Psilocybe Musika Elkartea). Entsegu lokalak edukitzeaz gain, grabazio estudioa eta kontzertu programazio zabal, berezi eta interesgarria eskaintzen du.', 'http://www.psilocybenea.com/', 'mini_psilocybe.jpg', 43.34931564331055000000, -1.80019140243530270000, 19, 35, 11, 'boligorria');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(334, '2013-03-01 15:35:52', 'Kabia', 'Kartara jateko leku elegantea. Degustazio menuak ere badituzte, pertsonako 40-45 eurotan.', 'http://www.restaurantekabia.com/', 'mini_kabia.jpg', 43.08970260620117000000, -2.31537103652954100000, 16, 160, 1, 'saioaL');");
