	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(337, '2013-03-11 12:25:59', 'Cu&eacute;lebre Taberna', 'Terrazan eguzkia hartzen lasai egoteko taberna. Aieteko Munto auzoan kokatua bista politekin. Umeak eramateko leku aproposa bertan txikientzako parkea baitago. Prezio egokiak eta jateko ona, etxeko platerak bereziki.', 'http://www.ddonosti.com/comercio/la-taberna-del-cuelebre', 'mini_4fc28d09-cfd8-4205-9714-67b4bca5f70f.jpg', 43.30762481689453000000, -1.99363231658935550000, 18, 30, 7, 'txarlygraffo');");
