	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(339, '2013-03-11 14:45:40', 'Nerbioi ibaiaren jauziaren behatokia', 'Santiago Mendiaren txoko isolatuenetariko eta sakonetariko batean, Burgoseko lurraldean dago Nerbioi Ibaiaren Jauzia. 300 metrotik gorako altuerarekin eta zaldiaren buztan forma bitxiarekin, Mendebaldeko Europan ikus daitezkeen ur-jauzi handi eta ederrena', 'http://www.ordunaturismo.com', 'mini_nerbioi.jpg', 42.97405242919922000000, -3.05261206626892100000, 10, 222, 14, 'ibereziartua');");
