	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(364, '2013-04-20 04:46:57', 'Mikel Deuna. Arribeko \"surtidorea\"', 'Mikel Deuna izena badu ere Surtidorea bezela ezagunagoa da. Arribeko gasolindegiko taberna jatetxea da-eta\r\nGlamour haundirik ez izan arren jana mundiala! Kantitate eta kalitate aldetik (postreak binaka. Txerri hankak espezialitatea)', '', '', 43.03365325927734400000, -1.98321461677551270000, 17, 213, 1, 'xauxar');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(365, '2013-04-22 17:48:16', 'Higeralde landetxea', '5 izarretako hotel baten modukoa. Bertako nagusia oso jatorra, Bixen. Komeriak iristeko, baserri bideak dira, baina merezi du dudarik gabe. Gertuko tratua eta bista bikainak, Hendaia aldea osorik ikus daiteke.', 'http://www.nekatur.net/higeralde?lang=eu-ES', 'mini_terraza_monte_higeralde1_gallery2.jpg', 43.36695480346680000000, -1.81086087226867680000, 12, 35, 17, 'lesakakoramon');");
