	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(368, '2013-04-24 15:34:30', 'Ibarre nekazal etxea', 'Antzuolako ibarrean kokaturik dago eta familia batena da. Nekazal aktibitatea mantentzen dute eta inguruan sagasti, baratze, eta ganadu zelaiak dituzte, besteak beste. Euskaldunak dira eta hurbileko harreta eskaintzen dute. Janaria goxoa eta ona da bertan', 'ww.ibarre.com', 'mini_ibarre.png', 43.09665679931640600000, -2.37688255310058600000, 18, 193, 17, 'aitzoliturbe');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(369, '2013-04-24 15:50:34', 'Antzuolako Gaztetxia', 'Hainbat urtez fundamentuzko lokalik gabe egon ostean, 1012ko Urrian ireki genuen gaztetxea. Egunero egoten da zabalik arratsaldeko 17:00etatik aurrera. Herriko egun berezien sortzaile nagusia da Gazte Asanblada. Parrandarik handienak gaztetxean izaten dir', 'www.antzuolakogaztetxia.info', 'mini_antzuolakogaztetxia.jpg', 0.00000000000000000000, 0.00000000000000000000, 0, 193, 10, 'aitzoliturbe');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(370, '2013-04-24 15:55:39', 'Antzuolako Gaztetxia', 'Antzuolako Gaztetxea Antzuolako Gazte Asanbladak gestionatzen du. Bertan, batez ere gazteentzako aisialdi alternatiboa eskaintzen den arren (hitzaldi, tailer, kontzertu eta abar) heldu eta haurrentzako ekintzak ere antolatzen dira. Leku goxoa da egoteko.', 'www.antzuolakogaztetxia.info', 'mini_antzuolakogaztetxia2jpg', 43.09836959838867000000, -2.38224148750305180000, 17, 193, 11, 'aitzoliturbe');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(371, '2013-04-24 16:01:09', 'Torresoroa', 'Herriko kultura eraikinik esanguratsuena da. Udalak antolaturiko aterpeko kultura ekitaldi gehienak (bertso saioak, ipuin kontalariak, kontzertuak, antzerkiak...) bertan egiten dira. Bestalde, Antzuolako Oinarin dantza taldeak entseguetarako darabilki.', 'ww.antzuola.com', 'mini_torresoroa.jpeg', 43.09766387939453000000, -2.37995624542236330000, 15, 193, 11, 'aitzoliturbe');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(372, '2013-04-24 16:05:58', 'Herria', 'Kanping formalik ez dagoen arren, gauez lo egiteko edozein lekutan (pixkat diskretoa izan dadila) monta zenezake kanping-a, ez dut uste inork arazorik jarriko dizunik! Beraz gazte, badakizu, parranda ostean autoa aparkatuta utzi ta egin lo lasai kanpinean', '', '', 0.00000000000000000000, 0.00000000000000000000, 0, 193, 18, 'aitzoliturbe');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(373, '2013-04-24 16:10:47', 'Antzuolako Gaztetxia', 'Antzuolako Gazte Asanbladak gestionatzen du eta egunero egoten da irekita 17:00etatik aurrera. Giro goxo eta euskalduna aurki zenezake. Adin guztietakoentzat irekia. Hainbat garagardoren artean, bailaran ekoizitako garagardo bat saltzen da bertan.Bikaina!', 'www.antzuolakogaztetxia.info', 'mini_antzuolakogaztetxia2jpg-2', 43.09834671020508000000, -2.38228440284729000000, 17, 193, 7, 'aitzoliturbe');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(374, '2013-05-02 14:46:02', 'agorregi zalditokia', 'Zaldiaren nortasuna nolakoa den erakutsiz, doma naturala bultzatuz lan egiten duen ekipoa aurkituko duzu Zarautzko Agorregi zalditokian.Paraje ezin hobea, egun pasa antolatzeko primerako ingurunea.Udan ingelesa praktikatzeko aukera ere eskaintzen dute.', 'http://zaldiagorregi.es/', 'mini_image_91.jpg', 43.27420425415039000000, -2.18765258789062500000, 9, 39, 19, 'olatz');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(375, '2013-05-06 16:09:12', 'Larraitz Jatetxea', 'Txindokitik bueltan zatozela, Larraitz auzoan dago. Eguraldi ona egiten badu, kanpoan dagoen terrazan primeran egoten da. Eguneko menua, plater konbinatu edo ogitarteko goxoak.Karta ere badute. Baita telebista pantaila erraldoi bat ere.', 'http://www.larraitzjatetxea.net/', 'mini_31954-larraitz_jatetxea_comida_vasca.jpg', 43.04679489135742000000, -2.09147524833679200000, 15, 227, 1, 'mandarino');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(376, '2013-05-06 16:13:48', 'Olaizola Jatetxea', 'Leku zoragarria. Giro jatorra. Jateko bikaina. Prezio merkeak. Familiakin juteko aproposa.', 'http://www.olaizola.com/eu/', 'mini_olaizola-jatetxea.jpg', 43.28730392456055000000, -1.83826744556427000000, 13, 32, 1, 'mandarino');");
	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(377, '2013-05-08 17:51:22', 'Beti Alai', 'Eguneko menua jateko jatetxe paregabea. Prezioz ondo eta aukera ugaria. Kartara jateko ere aukera ona.', '', '', 43.13786697387695000000, -2.07345223426818850000, 19, 46, 1, 'artsuaga');");
