	tx.executeSql("INSERT INTO `herriak_elementuak` (`id`, `alta_data`, `izena`, `deskribapena`, `url`, `irudia`, `gmaps_lat`, `gmaps_lng`, `gmaps_zoom`, `fk_herria`, `fk_azpiatala`, `erabiltzailea`) VALUES(445, '2013-05-12 16:55:37', 'Otzeta Jatetxea', 'Menu goxoak prezio onean', '', '', 43.01631546020508000000, -2.52805852890014650000, 18, 182, 1, 'auritzio');");
